							btn_next_OnPressed()
{
	_swdoc.intCIPageNo++;

	//-- Add Paging
	var strFilter = "&strFilter="+_swdoc.strCIFilter;
	strFilter += "&paged=true&start="+_swdoc.intCIPageNo+"&end=&cmdb_type='ME->Site'&fk_cmdb_id="+_swdoc.oSite().fk_cmdb_id+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_results,strFilter,null,0)
	_swdoc.MF().sl_results.Refresh();

	if(sl_results.rowCount() < _swdoc.intPerPage)
	{
		_een(_swdoc.MF().btn_next,false);	
	}
	_een(_swdoc.MF().btn_prev,true);


}

							btn_prev_OnPressed()
{
	_swdoc.intCIPageNo--;

	//-- Add Paging
	var strFilter = "&strFilter="+_swdoc.strCIFilter;
	strFilter += "&paged=true&start="+_swdoc.intCIPageNo+"&end=&cmdb_type='ME->SITE'&fk_cmdb_id="+_swdoc.oSite().fk_cmdb_id+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_results,strFilter,null,0)
	_swdoc.MF().sl_results.Refresh();

	if (_swdoc.intCIPageNo == 1) 
	{
		_een(_swdoc.MF().btn_prev,false);
	}
	_een(_swdoc.MF().btn_next,true);
}

