//--
//-- based on the current workflow stage show / hide tabs / buttons etc
function setup_process_fields(boolDocChange)
{
	//-- F0105032
	if(_swdoc.OC().bpm_workflow_id!="")
	{
		if(_swdoc.strCurrentWorkflow != _swdoc.OC().bpm_workflow_id)
		{
			_swdoc.arrAudit = new Array();
			var strParams = "fk_workflow_id="+app.pfu(bpm_workflow.pk_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				_swdoc.arrAudit[strBinding] = _swdoc[arrBinding[0]][arrBinding[1]];
			}
			_swdoc.strCurrentWorkflow = _swdoc.OC().bpm_workflow_id;
		}
	}//-- F0105032

	//-- NWJ
	//-- assign _swdoc to app.cfg array (so we can refresh tasks list from task forms
	app.cfg.allocaterfcdoc(_swdoc.OC().callref,_swdoc)

	_swdoc.strOriginalSLA = _swdoc.opencall.priority;

	// no process so do not show process tabs
	if(_swdoc.OC().bpm_workflow_id=="")
	{
		_swdoc.MF().TabDet.ShowTabItem(4, false);
		app._etab(_swdoc.MF().TabDet,0);
		return true;
	}

	//--
	//-- check if we need to initialise stage (in case something has gone wrong somwhere in vpme)
	if((_swdoc.OC().bpm_workflow_id!="")&&(_swdoc.OC().bpm_stage_id < 1))
	{
		var oRec = app.g.get_record("bpm_workflow", _swdoc.OC().bpm_workflow_id);
		if(oRec)
		{
			var strStageID = oRec.fk_firststage_id;
			if(strStageID!="0")
				_swdoc.ChangeRecord("bpm_stage", strStageID)			
		}	
	}

	//-- if user can add tasks and this stage allows it
	var boolActive = false;
	if(_swdoc.OC().status<16)
		boolActive = true;

	//-- if user can add tasks and this stage allows it
	var strParams = "cr="+_swdoc.OC().callref+"&sid="+_swdoc.OC().bpm_stage_id ;
	var intTaskCount = app.g.sqs_rowcount("call/common/cnt_bp_tasks",strParams);
    var boolHasTasks = intTaskCount>0;
    var boolAddTasks = ( (_swdoc.oStage().flg_addcalltasks==1) &&(boolActive)&&(app.session.HaveAppRight(app.PG_BPM, app.BPM_ADDTASK,app.session.dataDictionary)) )
	var boolShowTasks = ((_swdoc.oStage().flg_hastasks==1)||(boolAddTasks)||(boolHasTasks));
	//-- F0090013 only show tasks tab if there are tasks or can add on fly - as stage may have tasks, but they may not be created
	var boolShowTasks = ((boolAddTasks)||(boolHasTasks));

	app._een(_swdoc.MF().mbtn_mod_task1,boolAddTasks);
	_swdoc.MF().TabDet.ShowTabItem(6,boolShowTasks);
	if((boolShowTasks)&&(boolDocChange))
	{
		//-- NWJ - let SW handle refreshing ...
		//MF().sl_tasks.Refresh();
	}

	//-- AUTH STUFF
	var boolRequiresAuth = (_swdoc.OC().bpm_waitingauth==1);	
	var boolAddAuths = ( (_swdoc.oStage().flg_addcallauths==1) && (app.session.HaveAppRight(app.PG_BPM, app.BPM_ADDAUTH,app.session.dataDictionary)) )
	if(_swdoc.oStage().flg_unanimous_auth==1)
	{
		app._evi(_swdoc.MF().authWeighting1,false);
	}	
	//-- if we have auth show tab and disable status lb
	_swdoc.MF().TabDet.ShowTabItem(5, boolRequiresAuth||boolAddAuths);

	//if we require authorisation, or disable manual, do not enable status
	//--only disable manual progression if stage is marked to disable AND stage has tasks (or requires auth)
	var boolTaskStop = _swdoc.oStage().flg_disable_manual==1 && boolHasTasks;
	app._een(_swdoc.MF().lb_wf_state1,(!(boolTaskStop|| boolRequiresAuth)));

	app._evi(_swdoc.MF().mbtn_mod_auth1,boolAddAuths &&(boolActive));	
	//-- EOF AUTH STUFF

	//-- if we have data form show button
	app._een(_swdoc.MF().btn_bpm_form1,((_swdoc.oWF().ext_sw_form!="")||(_swdoc.oStage().swdataform!="")));

	//-- select tab 0 or 1 depending on auth
	app._etab(_swdoc.MF().TabDet,(boolRequiresAuth)?5:0);
	if(boolDocChange)
			app.g.reset_bpm_form_attribute_check(_swdoc);		
	app.g.process_bpm_form_attribute_check(_swdoc, _swdoc.OC().bpm_workflow_id, _swdoc.oStage().pk_stage_id);

	return true;
}
