    if (_swdoc.can_update == 0)
    {
        //-- show close button
        //-- hide close and accpet button
        app._evi(btn_assign,false);
        app._evi(btn_cancel,false);
        app._evi(btn_accept,false);
        app._evi(btn_save,false);
       	app._evi(btn_convert,false);
        app._evi(lbl_actions,false);

        return;
    }

    //-- if unaccepted & not resolved or closed or cancelled show the accept and assign button
    var currStatus = _swdoc.opencall.status;
    app._evi(btn_convert,false);

    //-- accepted
    if (currStatus == 1)
    {
        //-- show resolve, re-assign, cancel button button
        //-- hide close and accept button
        app._evi(btn_assign,true);
        app._evi(btn_cancel,true);
        app._evi(btn_accept,false);
		app._een(btn_save,false);
        app._evi(btn_convert,true);
    }

    //-- NWJ - 07.05.2004 - THis will never happen in problem now
    //-- resolved
    if (currStatus == 6)
    {
        //-- show close button
        //-- hide close and accpet button
        app._evi(btn_assign,false);
        app._evi(btn_cancel,false);
        app._evi(btn_accept,false);
		app._een(btn_save,false);
    }

    //-- unaccepted/unassigned
    if ((currStatus == 3) || (currStatus == 2))
    {
        //-- show cancel, accept button
        app._evi(btn_accept,true);
        app._evi(btn_assign,true);
        app._evi(btn_cancel,true);
        app._een(btn_save,false);
    }

    //-- closed or cancelled
    if (currStatus > 15)
    {
        //-- show close button
        //-- hide close and accpet button
        app._evi(btn_assign,false);
       	app._evi(btn_cancel,false);
        app._evi(btn_accept,false);
        app._evi(btn_save,false);
        app._evi(lbl_actions,false);
    }

    return true;
}

