mbtn_inc_actions1_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;
	var iCauseCI = 2;
	var iImpactCI = 3;

	//-- can they change?
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

	//-- NWJ 02.11.2006 - unlink incidents
	if(nItemID == iDisassociate)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_incs1,"callref",",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_slave_calls(_swdoc.OC().callref,strCallRefs,'PROBLEM-INCIDENT'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This known error has been dis-associated from the following incidents.\n\n";

				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Incident (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";

					//-- update incident
					strPrbText = "The known error (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been dis-associated from this Incident.";
					app.g.new_diaryevent(arrCallrefs[x],strPrbText,"Known Error Management","Error Dis-association");
				}
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Known Error Management","Incident Dis-association");
			}
			sl_incs1.Refresh();
			sl_incs1_OnItemSelected(0);

			_swdoc.remove_selected_incident_cis(strCallRefs);			
		}
		_swdoc.filter_impact_customers(_swdoc.EF());
		return;
	}
	else if(nItemID == iImpactCI)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_incs1,"callref",",");	
		var oResultCIs = app.cmdb.get_call_ci_info(strCallRefs , "INCIDENT", "");
		if(oResultCIs.keys!="")	_swdoc.link_affect_cis_to_problem(oResultCIs.keys,oResultCIs.text,oResultCIs.type);
	}
	else if(nItemID == iCauseCI)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_incs1,"callref",",");	
		var oResultCIs = app.cmdb.get_call_ci_info(strCallRefs , "INCIDENT", "");
		if(oResultCIs.keys!="")_swdoc.link_cause_cis_to_problem(oResultCIs.keys,oResultCIs.text,oResultCIs.type);
	}	
	_swdoc.filter_impact_customers(_swdoc.EF());

}

btn_search_inc1_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

    //-- NWJ 02.11.2006
    //-- open call search form in select mode   
    var strURL = "canreset=1";  
    app.itsm.search_calls("INC",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var array_inc = oResults.selectedkeys.split(",");  
 		var strText = "This known error has been associated with the following incidents\n\n";
		var boolIncs = false;
		//-- loop through selected incidents and link them
    	for (var x=0;x<array_inc.length;x++)
    	{
        	var inc_callref = array_inc[x];
        	var inc_callref_text = app.g.pad_callref(inc_callref);


			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_incs1,inc_callref,0);
			if (rowPos == -1)
			{ 
				boolIncs = true;
				//-- call app.g method to link problem to incident
				if(app.g.relate_calls(_swdoc.OC().callref,inc_callref,'PROBLEM-INCIDENT'))
				{
					strText += "Incident (" + inc_callref_text + ") associated\n";

					//-- update ke or problem diary
					strIncText = "The known error (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this incident."
					app.g.new_diaryevent(inc_callref,strIncText,"Known Error Management","Error Association");
					var xmlmc = new XmlMethodCall;

					xmlmc.SetParam("keCallref", _swdoc.OC().callref);
					xmlmc.SetParam("incCallref", inc_callref);

					// Invoke the VPME method LogCall. 				
					if(xmlmc.Invoke("VPME", "keAssociation"))
					{
 					}
					else
					{
    					// Error
    					MessageBox(xmlmc.GetLastError());
					}


				}
			}

    	} //-- eof for
		var boolImpRefresh = true;
		//-- create incident call diary update then reload list
		if(boolIncs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Known Error Management","Incident Association");
			sl_incs1.Refresh();

			//-- Now check if we should link incident ci items as being affected
			if(dd.GetGlobalParamAsNumber("Application Settings/Problem/CopyIncidentCIs")==1)
			{
				var oResultCIs = app.cmdb.get_call_ci_info(oResults.selectedkeys , "INCIDENT", "");
				if(oResultCIs.keys!="")
				{
					boolImpRefresh = false;
					_swdoc.link_affect_cis_to_problem(oResultCIs.keys,oResultCIs.text);
				}
			}

		}
		if(boolImpRefresh)
			_swdoc.filter_impact_customers(_swdoc.EF(),true);
    });
}

