	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

	var strURL = "canreset=1";  

    app.itsm.search_calls("RFC",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var array_rels = oResults.selectedkeys.split(",");  
		var strText = "This known error has been associated with the following change requests\n\n";
		var boolRELs = false;
		//-- loop through selected problems and link them
    	for (var x=0;x<array_rels.length;x++)
    	{
        	var rfc_callref = array_rels[x];
        	var rfc_callref_text = app.g.pad_callref(rfc_callref);

			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_rels1,rfc_callref,"callref");
			if (rowPos == -1)
			{ 		
				boolRELs=true;
				//-- call app.g method to link problem to incident
				if(app.g.relate_calls(rfc_callref,_swdoc.OC().callref,'RFC-ERROR'))
				{
					strText += "Change Request (" + rfc_callref_text + ") associated\n";

					//-- update rfc diary
					strRfcText = "The known error (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this change request.";
					app.g.new_diaryevent(rfc_callref,strRfcText,"Known Error Management","Error Association");
				}
			}
    	} //-- eof for

		//-- create call diary update then reload list
		if (boolRELs)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Known Error Management","Change Association");
			sl_rels1.Refresh();
		}
    });
}

OnFormLoaded(bDataReloaded)
{
	TabRel.ShowTabItem(8,((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	app._etab(TabRel,0);
	//-- Load Second Tab
	_swdoc.EF().sl_configitems1.Refresh();
	//-- Show appropriate KB Tab
	var tTab = _swdoc.EF().TabRel.tab;
	if(!app.g.use_sw_knowledgebase())
	{
		if(app.strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			_swdoc.EF().TabRel.ShowTabItem(10,true);
			_swdoc.EF().TabRel.ShowTabItem(7,false);
			app._etab(_swdoc.EF().TabRel, tTab);
		}
	}
	else
	{
		_swdoc.EF().TabRel.ShowTabItem(10,false);
		_swdoc.EF().TabRel.ShowTabItem(7,true);
		app._etab(_swdoc.EF().TabRel, tTab);
	}


}

mbtn_cci_views2_OnMenuItem(strName, nItemID)
{

	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren=5;
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_services,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_services,-1,"ck_config_item");

		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

