btn_search_inc3_OnPressed()
{
    app.g.search_for("swkb", true,"",function(oRes)
    {
    	var strKeys = oRes.selectedkeys;
		if(strKeys=="")return;
		var arrKeys = strKeys.split(",");
 		var strText = "This Known Error has been associated with the following Knowledgebase Documents\n\n";
		for (var x=0; x< arrKeys.length;x++)
		{
			var fk_key = arrKeys[x];
			if(app.g.sl_findrow_byvalue(sl_kbs1,fk_key,"docref")==-1)
			{
				app.oKB.relate_call_to_kb(fk_key,_swdoc.OC().callref,'Used');
				strText += "Knowledgebase Document (" + fk_key + ") associated\n";
			}
		}			
		app.g.new_diaryevent(_swdoc.OC().callref ,strText,"Known Error Management","Knowledgebase Document Association");

		sl_kbs1.Refresh();
		sl_kbs1.SetRowSelected(0);
		sl_kbs1_OnItemSelected(0);
		return;
    });
}

