mbtn_rel_actions_OnMenuItem(strName, nItemID)
{
	var iDisassociate = 1;

	//-- can they change?
	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

	//-- NWJ 02.11.2006 - unlink releases
	if(nItemID == iDisassociate)
	{
		var strCallRefs = app.g.sl_getselected_valuesdel(sl_rels,"callref",",");
		if(strCallRefs!= "")
		{
			if(app.g.unrelate_master_calls(strCallRefs, _swdoc.OC().callref, 'RELEASE-ERROR'))
			{
				var arrCallrefs = strCallRefs.split(',');
				var strText = "This Known Error has been dis-associated from the following Releases.\n\n";

				for(var x=0;x<arrCallrefs.length;x++)
				{
					strText += "Release (" + app.g.callref_pad(arrCallrefs[x]) + ") dis-associated\n";

					//-- update incident
					strPrbText = "The known error (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been dis-associated from this Release.";
					app.g.new_diaryevent(arrCallrefs[x],strPrbText,"Known Error Management","Error Dis-association");
				}
				app.g.new_diaryevent(_swdoc.OC().callref,strText,"Known Error Management","Release Dis-association");
			}
			sl_rels.Refresh();
			sl_rels_OnItemSelected(0);
		}
		_swdoc.filter_impact_customers(_swdoc.EF());
		return;
	}
	_swdoc.filter_impact_customers(_swdoc.EF());

}

btn_search_rel_OnPressed()
{

	if (!app.itsm.can_edit(_swdoc.OC().callclass,true))return;

    //-- NWJ 02.11.2006
    //-- open call search form in select mode   
    var strURL = "canreset=1";  
    app.itsm.search_calls("REL",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var array_rel = oResults.selectedkeys.split(",");  
 		var strText = "This known error has been associated with the following releases\n\n";
		var boolRels = false;
		//-- loop through selected incidents and link them
    	for (var x=0;x<array_rel.length;x++)
    	{
        	var rel_callref = array_rel[x];
        	var rel_callref_text = app.g.pad_callref(rel_callref);


			//-- check that incident is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(sl_rels,rel_callref,0);
			if (rowPos == -1)
			{ 
				boolRels = true;
				//-- call app.g method to link problem to incident
				if(app.g.relate_calls(rel_callref,_swdoc.OC().callref,'RELEASE-ERROR'))
				{
					strText += "Release (" + rel_callref_text + ") associated\n";

					//-- update ke or problem diary
					strRelText = "The known error (" + app.g.pad_callref(_swdoc.OC().callref) + ") has been associated to this Release."
					app.g.new_diaryevent(rel_callref,strRelText,"Known Error Management","Error Association");
				}
			}

    	} //-- eof for
		var boolImpRefresh = true;
		//-- create incident call diary update then reload list
		if(boolRels)
		{
			app.g.new_diaryevent(_swdoc.OC().callref,strText,"Known Error Management","Release Association");
			sl_rels.Refresh();

		}
		if(boolImpRefresh)
			_swdoc.filter_impact_customers(_swdoc.EF(),true);
    });
}

