method.code:
						//-- pointers
function COND()
{
	return _swdoc.bpm_cond;
}

function MF()
{
	return _swdoc.mainform;
}

function calc_onholdduration()
{
	var intMinutes = _swdoc.COND().onhold_durationhrs * 60;
	intMinutes = intMinutes + _swdoc.COND().onhold_durationmins;
	_swdoc.COND().onhold_duration = intMinutes + "";
}

function displayProfileCode(strStatusValue)
{
	var boolVisible = false; 
	if (strStatusValue=="6" || strStatusValue=="16" || strStatusValue=="18")
	{
		boolVisible = true;
	}
	app._evi(_swdoc.MF().fld_res_profile_value,boolVisible);
	app._evi(_swdoc.MF().lbl_res_profile_value,boolVisible);
	app._evi(_swdoc.MF().fld_profile_value,!boolVisible);
	app._evi(_swdoc.MF().lbl_profile_value,!boolVisible);
	if (boolVisible && _swdoc.COND().set_profile == 1)
	{
		app._een(_swdoc.MF().fld_res_profile_value, true);
		app._een(_swdoc.MF().lbl_res_profile_value, true);
	}else if (!boolVisible && _swdoc.COND().set_profile == 1)
	{
		app._een(_swdoc.MF().fld_profile_value, true);
	}
}

						OnSaveData()
{
	//-- cacl onhold duration in minutes
	_swdoc.calc_onholdduration();


	return true;
}

						OnDataSaved()
{


	//-- nwj - 16.05.2013 set globallar so bmpui knows if ok to reload
	app.g.SavedWorkflowRecord = true;


}

							lb_mailbox_OnLeftClick()
{
	//-- get mailbox list
	
	if(lb_mailbox.pickList=="")
	{
		//-- call php to get mailbox list
		var strURL = "php/itsm/get_mailboxlist.php"; 
		var strResult = app.g.submit_php(strURL,true);
		lb_mailbox.pickList = strResult;
	}


	return true;
}

							lb_template_OnLeftClick()
{
	//-- get template list
	
	if(lb_template.pickList=="")
	{
		//-- call php to get mailbox list
		//MessageBox(lb_mailbox.text)
		var strURL = "php/itsm/get_templatelist.php?mailboxname=" + lb_mailbox.text; 
		var strResult = app.g.submit_php(strURL,true);
		lb_template.pickList = strResult;
	}

	return true;
}

							OnFormLoaded()
{
	var boolUpdate = (_swdoc.COND().pk_condition_id!=0);
	if(boolUpdate)
	{
		//-- updating - for now disable bpme vpme actions - as not supported in vpme call node?
		TabCond.ShowTabItem(2,true);
		if(_swdoc.COND().set_profile==1)
		{
			app._een(fld_profile_value,true);
			app._een(lbl_profile_value,true);
		}
	}
	else
	{
		_swdoc.COND().fk_workflow_id = _swdoc.GetArg("fk_workflow_id");
		_swdoc.COND().fk_stage_id = _swdoc.GetArg("fk_stage_id");
		_swdoc.COND().fk_timer_id = _swdoc.GetArg("fk_timer_id");		
		_swdoc.COND().flg_runatstart= _swdoc.GetArg("flg_onstart");
		TabCond.ShowTabItem(2,false);		
	}
	
	//-- nwj set title to reflect type of condition
	if(_swdoc.COND().flg_runatstart==1)
	{
		_swdoc.SetTitle("Process Stage On Start Condition");
	}
	else
	{
		_swdoc.SetTitle("Process Stage Status Change Condition");	
	}
	
	
	var boolNoCond = (_swdoc.COND().test_condition=="");
	app._een(btn_apply_test, boolNoCond);
	app._een(btn_and,(!boolNoCond));
	app._een(btn_or,(!boolNoCond));

	if(_swdoc.COND().set_call_sla_new!="")
	{
		app._ema(_swdoc.MF().fld_call_priority, true);
	}

	//-- show diary update
	TabCond.ShowTabItem(3,_swdoc.COND().flg_diaryupdate=="1");
	app._etab(TabCond,0);
		
	//-- set form modified to false as users complain about closing forms and getting prompted to save / cancel when they
	//-- havent done anything.
	_swdoc.ResetModiedFlag("bpm_cond");
	
	var strStatusValue = _swdoc.MF().lb_status.value;
	_swdoc.displayProfileCode(strStatusValue);
}

							tb_mins_OnValueChanged(strValue)
{
	//--
	if(strValue>59)
	{
		_swdoc.COND().onhold_durationmins="0";
		_swdoc.UpdateFormFromData();
	}

}

							TabCond_OnTabSelected(nTab)
{
	var tAction = 1;
	var tVPME = 2;
	if(nTab==tAction)
	{
		app._een(lbl_hours,(_swdoc.COND().set_call_status=="4"));
		app._een(lbl_mins,(_swdoc.COND().set_call_status=="4"));	
		app._een(tb_hours,(_swdoc.COND().set_call_status=="4"));
		app._een(tb_mins,(_swdoc.COND().set_call_status=="4"));
	}
	else if (nTab==tVPME)
	{
		app._een(btn_del_vpme, (sl_vpme.rowCount()>0));
		app._een(btn_up_pos,(sl_vpme.curSel>0));
		app._een(btn_down_pos, ( ((sl_vpme.curSel+1) < sl_vpme.rowCount()) && (sl_vpme.curSel>0)));
		
		
		//-- get list of vpme scripts
		var strURL = "php/itsm/get_vpmescripts.php?in_ddname=" + dd.Name; 
		var strResult = app.g.submit_php(strURL,true);
		lb_scripts.pickList = strResult;
	}
	
}

							lb_diary_OnValueChanged(strValue)
{
	//-- show diary update
	TabCond.ShowTabItem(3,strValue=="1");
	
	if(strValue=="0")app._etab(TabCond,1);


}

							btn_apply_test_OnPressed()
{
	
	if((lb_op.text=="")||(lb_test.text ==""))
	{
		MessageBox("Please specify some test criteria");
		return;
	}
	
	var boolString=false;
	var strOp = lb_op.text;
	var strTest = "";
	switch(lb_test.text.toLowerCase())
	{
		case "bpm status":
			strTest = "&[new_opencall.bpm_status_id]";		
			boolString = true;
			break;
		case "opencall status":
			strTest = "&[new_opencall.status]";				
			break;
		case "customer id":
			strTest = "&[new_opencall.cust_id]";				
			boolString = true;		
			break;						
		case "customer name":
			strTest = "&[new_opencall.cust_name]";				
			boolString = true;		
			break;			
		case "support group":
			strTest = "&[new_opencall.suppgroup]";				
			boolString = true;		
			break;			
		case "support analyst":
			strTest = "&[new_opencall.owner]";				
			boolString = true;		
			break;			
			//-- old values to test
		case "pre - bpm status":
			strTest = "&[old_opencall.bpm_status_id]";		
			boolString = true;
			break;
		case "pre - opencall status":
			strTest = "&[old_opencall.status]";		
			break;
		case "pre - customer id":
			strTest = "&[old_opencall.cust_id]";				
			boolString = true;		
			break;						
		case "pre - customer name":
			strTest = "&[old_opencall.cust_name]";				
			boolString = true;		
			break;			
		case "pre - support group":
			strTest = "&[old_opencall.suppgroup]";				
			boolString = true;		
			break;			
		case "pre - support analyst":
			strTest = "&[old_opencall.owner]";				
			boolString = true;		
			break;			

	}
	
	//--
	//-- get appropriate value
	var strValue = tb_value.text;	
	if(lb_teststatus.visible)strValue=lb_teststatus.value;
	if(lb_testwfstatus.visible)strValue=lb_testwfstatus.value;	
	
	if(boolString)
	{
		strTest = "'" + strTest + "'";
		strValue = "'" + strValue + "'";
	}

	var strFullTest = strTest + strOp + strValue;
	_swdoc.COND().test_condition = _swdoc.COND().test_condition + " " + strFullTest;
	_swdoc.UpdateFormFromData();
	
	app._een(btn_apply_test,false);
	app._een(btn_and,true);
	app._een(btn_or,true);

}

							btn_and_OnPressed()
{
	_swdoc.COND().test_condition = _swdoc.COND().test_condition + " " + " AND " + " ";
	_swdoc.UpdateFormFromData();

	app._een(btn_apply_test,true);
	app._een(btn_and,false);
	app._een(btn_or,false);	
	
}

							btn_or_OnPressed()
