}

							lb_test_OnValueChanged(strValue)
{
	//- -show inputs for user based on the test they are doing
	if(strValue.toLowerCase().indexOf("opencall status")!=-1)
	{
		app._evi(lb_teststatus,true);
		app._evi(tb_value,false);
		app._evi(lb_testwfstatus,false);
	}
	else if(strValue.toLowerCase().indexOf("bpm status")!=-1)
	{
		app._evi(lb_teststatus,false);
		app._evi(lb_testwfstatus,true);
		app._evi(tb_value,false);
	}
	else
	{
		app._evi(lb_teststatus,false);
		app._evi(lb_testwfstatus,false);
		app._evi(tb_value,true);
		
	}

}

							tb_cond_OnValueChanged(strValue)
