	var boolNoCond = (strValue=="");
	app._een(btn_apply_test, boolNoCond);
	app._een(btn_and, (!boolNoCond));
	app._een(btn_or, (!boolNoCond));


}

							btn_add_vpme_OnPressed()
{
	//-- insert relation
	var strScript = lb_scripts.text;
	if(strScript!="")
	{
		var nextPos = sl_vpme.rowCount();
		nextPos++;
		
		var strParams = "cid="+app.pfu(_swdoc.COND().pk_condition_id)+"&script="+app.pfu(strScript)+"&nid="+app.pfu(nextPos)+"&sid="+app.pfu(_swdoc.COND().fk_stage_id)+"&wf="+app.pfu(_swdoc.COND().fk_workflow_id)
		app.g.submitsqs("form/bpm_condition/add_vpme_cond", strParams);

		sl_vpme.Refresh();
		app._een(btn_del_vpme,(sl_vpme.rowCount()>0));
		sl_vpme.SetRowSelected(sl_vpme.rowCount()-1, true,false);
	}

}

							btn_del_vpme_OnPressed()
{
	var strConfirm = "Are you sure you want to remove this vpme script execution?";
	var intSequence = sl_vpme.GetItemTextRaw(sl_vpme.curSel, "execorder");
	if(app.g.sl_deldbrow(sl_vpme,sl_vpme.curSel,'pk_auto_id',null,strConfirm))
	{

		var strUpdate="";
		//-- loop through list and set seq for each - 1 to #
		var intID=0;
		var nextSeq=0;
		for(var x=0; x < sl_vpme.rowCount();x++)
		{
			var thisSeq = sl_vpme.GetItemTextRaw(x, "execorder");
			if(thisSeq>intSequence)
			{
				intID = sl_vpme.GetItemTextRaw(x, "pk_auto_id");
				var strParams = "vid="+app.pfu(intID);
				app.g.submitsqs("form/bpm_condition/dec_vpme_seq", strParams);
			}
		}
	
		sl_vpme.Refresh();
		sl_vpme.SetRowSelected(0);	
		sl_vpme_OnItemSelected(0);

		app._een(btn_del_vpme,(sl_vpme.rowCount()>0));
		
	}



}

							btn_up_pos_OnPressed()
{
	//-- NWJ : move vpme seq up 
	//-- get selected vpme id to move up
	var currRow = sl_vpme.curSel;
	var intUpID = sl_vpme.GetItemTextRaw(sl_vpme.curSel, 'pk_auto_id');
	var intUpSeq = sl_vpme.GetItemTextRaw(sl_vpme.curSel, 'execorder');	
	
	//-- get stage id to move down
	var intDownID = sl_vpme.GetItemTextRaw(sl_vpme.curSel-1, 'pk_auto_id');
	var intDownSeq = sl_vpme.GetItemTextRaw(sl_vpme.curSel-1, 'execorder');
	
	var strParams = "vid="+app.pfu(intUpID);
	if(app.g.submitsqs("form/bpm_condition/dec_vpme_seq", strParams))
	{
		var strParams = "vid="+app.pfu(intDownID);
		if(app.g.submitsqs("form/bpm_condition/inc_vpme_seq", strParams))
		{
			//-- refresh list
			sl_vpme.Refresh();
			sl_vpme.SetRowSelected(currRow-1, true, false);
		}
	}


}

							btn_down_pos_OnPressed()
{
	//-- NWJ : move vpme seq down 
	//-- get selected vpme id to move down
	var currRow = sl_vpme.curSel;
	var intUpID = sl_vpme.GetItemTextRaw(sl_vpme.curSel+1, 'pk_auto_id');
	var intUpSeq = sl_vpme.GetItemTextRaw(sl_vpme.curSel+1, 'execorder');	
	
	//-- get id to move down
	var intDownID = sl_vpme.GetItemTextRaw(sl_vpme.curSel, 'pk_auto_id');
	var intDownSeq = sl_vpme.GetItemTextRaw(sl_vpme.curSel, 'execorder');
	
	//-- update seq id for both 
	var strParams = "vid="+app.pfu(intUpID);
	if(app.g.submitsqs("form/bpm_condition/dec_vpme_seq", strParams))
	{
		var strParams = "vid="+app.pfu(intDownID);
		if(app.g.submitsqs("form/bpm_condition/inc_vpme_seq", strParams))
		{
			//-- refresh list
			sl_vpme.Refresh();
			sl_vpme.SetRowSelected(currRow+1, true, false);
		}
	}



}

							sl_vpme_OnItemSelected(nRow)
{
	
	app._een(btn_up_pos,(nRow>0));
	app._een(btn_down_pos,(nRow < (sl_vpme.rowCount()-1)));
}

							elipsis_cust_OnLeftClick()
{

  	//-- NWJ 
    //-- open customer search form in select mode
	app.g.search_for("customers",true,"",function(sResults)
	{
		if (sResults.selectedkeys=="") return;
	
		//-- 27.08.2008 - make sure we are not adding duplicates
		var arrResults = sResults.selectedkeys.split(",");
		sResults.selectedkeys = "";
		for(var x=0;x<arrResults.length;x++)
		{
			var strTest = "'" + arrResults[x] + "'";
			if (_swdoc.COND().email_customers.indexOf(strTest)!=-1)
			{
				//-- exists in list already
			}
			else
			{
				if(sResults.selectedkeys!="")sResults.selectedkeys += ",";
				sResults.selectedkeys += app.g.pfs(arrResults[x]);
			}
		}
		//-- recheck after rebuilt
		if (sResults.selectedkeys=="") return;
		sResults.selectedkeys = app.g.string_replace(sResults.selectedkeys,",","','",true);
		
		if(_swdoc.COND().email_customers!="")_swdoc.COND().email_customers += ",";
		_swdoc.COND().email_customers += "'" + sResults.selectedkeys + "'";
		_swdoc.UpdateFormFromData()
	
		return false;
	});
}

							elipsis_cust_OnRightClick()
{
	//-- clear
	_swdoc.COND().email_customers = "";
	_swdoc.UpdateFormFromData();
	
	return false;
	
}

