							elipsis_analysts_OnLeftClick()
{
	//-- NWJ - let user select analysts to email
	//-- popup standard analyst popup 
	
	var strAnalysts = "";
	var strNewList = "";
	var strCurrAnalysts = _swdoc.COND().email_analysts;
	
	var dlg = new PickAnalystDialog;
	dlg.Open("Transfer to Analyst/Group", function(dlg)
	{
		if(!dlg)
			return "";

		if(dlg.analystid != "")
    	{
    		//-- set single analystid
    		if(strAnalysts!="")strAnalysts+=",";
    		strAnalysts += "'" + app.g.pfs(dlg.analystid) + "'";
    	}
    	else
    	{
    		//-- get all analysts in group
    		strAnalysts = app.g.get_group_analystids(dlg.groupid);
    		var arrGrpAnalysts = strAnalysts.split(",");
    		strAnalysts="";
    		for(var x=0;x<arrGrpAnalysts.length;x++)
    		{
    			//-- set all analyst ids in group	
				if(strAnalysts!="")strAnalysts+=",";    			
				strAnalysts += "'" + app.g.pfs(arrGrpAnalysts[x]) + "'";		
    		}
    	}
    
    	//-- 27.08.2008 - make sure we are not adding duplicates
		var arrResults = strAnalysts.split(",");
		for(var x=0;x<arrResults.length;x++)
		{
			var strTest = arrResults[x];
			if (strCurrAnalysts.indexOf(strTest)!=-1)
			{
				//-- exists in list already
			}
			else
			{
				if(strNewList!="")strNewList += ",";
				strNewList += arrResults[x];
			}
		}
		
		//-- recheck after rebuilt
		if (strNewList=="") return;

		if(strCurrAnalysts!="")strCurrAnalysts+=",";
		strCurrAnalysts += strNewList;

    	_swdoc.COND().email_analysts = strCurrAnalysts;
		_swdoc.UpdateFormFromData();
	});
	
	return false;
}

