							fld_set_profile_OnValueChanged(strValue)
{
	var boolEnable = strValue==1;
	app._een(lbl_profile_value,boolEnable);
	app._een(fld_profile_value,boolEnable);
	app._een(lbl_res_profile_value,boolEnable);
	app._een(fld_res_profile_value,boolEnable);
	if(!boolEnable)
	{
		bpm_cond.set_profile_value = "";
		app._ete(fld_profile_value,"");
		bpm_cond.set_res_profile_value = "";
		app._ete(fld_res_profile_value,"");
		_swdoc.UpdateFormFromData();
	}
}

							fld_profile_value_OnLeftClick()
{
	//--
	app.g.pick_problemprofile(_swdoc.COND().set_profile_value,"", false, function(strCode)
	{
		if(strCode)
		{
			_swdoc.COND().set_profile_value = strCode;
			_swdoc.UpdateFormFromData();
		}
		else if(strCode=="")
		{
			_swdoc.COND().set_profile_value = "";
			app._ete(fld_profile_value,"");
			_swdoc.UpdateFormFromData();
		}
	});
	return false;
}

