							fld_res_profile_value_OnLeftClick()
{
	app.g.pick_resolutionprofile(_swdoc.COND().set_res_profile_value,"", function(strCode){
		if(strCode){
			_swdoc.COND().set_res_profile_value = strCode;
			_swdoc.UpdateFormFromData();
		}
		
		else if(strCode==""){
			_swdoc.COND().set_res_profile_value = "";
			app._ete(fld_res_profile_value,"");
			_swdoc.UpdateFormFromData();
		}
	});
	
	return false;	
}
