method.code:
						//-- pointers
function ACTION()
{
	return _swdoc.ci_avail_action;
}

function MF()
{
	return _swdoc.mainform;
}

function setup_periodhhmm()
{
	//-- convert seconds into hours and minutes
	var strHHMM = app.g.hoursminutes_fromminutes(Math.round(_swdoc.ACTION().periodseconds/60));
	var arrInfo = strHHMM.split(".");
	
	app._ete(_swdoc.MF().tb_evhrs, arrInfo[0]);
	app._ete(_swdoc.MF().tb_evmins, arrInfo[1]);	
}

function periodhhmm_tosec()
{
	//-- convert seconds into hours and minutes
	var intMinutes = (_swdoc.MF().tb_evhrs.text * 60) + new Number(_swdoc.MF().tb_evmins.text);
	_swdoc.ACTION().periodseconds = (intMinutes * 60) + "";
	_swdoc.setup_periodhhmm();
}

							lb_mailbox_OnLeftClick()
