							OnFormLoaded()
{
	var boolUpdate = (_swdoc.ACTION().pk_id!=0);
	if(boolUpdate)
	{
		//-- updating
		TabCond.ShowTabItem(2,true);				
		if(ci_avail_action.raise_itsm_sla>0)
		{
			app._een(_swdoc.MF().lbl_r_sla, true);
			app._een(_swdoc.MF().fld_r_sla, true);
			app._ema(_swdoc.MF().fld_r_sla, true);
		}
	}
	else
	{
	
		_swdoc.ACTION().fk_ci_id = _swdoc.GetArg("fk_ci_id");
		TabCond.ShowTabItem(2,false);		
		_swdoc.ACTION().periodseconds = "0";
		
		_swdoc.LoadRecordData("swdata", "config_itemi", _swdoc.ACTION().fk_ci_id);
		_swdoc.UpdateFormFromData();
	}
	
	if(_swdoc.GetArg("service")=="1")
	{
		app._evi(_swdoc.MF().ci_item, false);
		app._evi(_swdoc.MF().service, true);
		app._slf(_swdoc.MF().Field5 , "_swc_flg_sc=1&_swc_fk_config_type="+app.pfu(_swdoc.GetArg("ck_config_type")));
		app._ete(_swdoc.MF().fld_fk_ci_item, 'Service');
		app._ete(_swdoc.MF().fld_text_title, 'Set Service Item');
		app._ewi(_swdoc.MF().fld_text_title , 100);
	}
	else
	{
		app._slf(_swdoc.MF().Field5 , "_swc_fk_config_type="+app.pfu(_swdoc.GetArg("ck_config_type")));
	}
	//-- show diary update
	TabCond.ShowTabItem(3,_swdoc.ACTION().flg_diaryupdate=="1");
	app._etab(TabCond, 0);
	
	//-- hide lb app.cmdb status
	app._evi(lb_cmdbstatus, false);
	app._evi(lb_ccis, false);
	app._evi(tb_value, true);
	
	//-- set form modified to false as users complain about closing forms and getting prompted to save / cancel when they
	//-- havent done anything.
	_swdoc.ResetModiedFlag("ci_avail_baction");
	
	app._een(btn_and1, (_swdoc.ACTION().execoncondition!=""));
	app._een(btn_or1, (_swdoc.ACTION().execoncondition!=""));	
	app._een(btn_apply_test1, (_swdoc.ACTION().execoncondition==""));
	
	_swdoc.setup_periodhhmm();
	
}

							TabCond_OnTabSelected(nTab)
{
	var tVPME = 2;
	if (nTab==tVPME)
	{
		app._een(btn_del_vpme, (sl_vpme.rowCount()>0));
		app._een(btn_up_pos, (sl_vpme.curSel>0));
		app._een(btn_down_pos, ( ((sl_vpme.curSel+1) < sl_vpme.rowCount()) && (sl_vpme.curSel>0)));
		
		//-- get list of vpme scripts
		var strURL = "php/itsm/get_vpmescripts.php?in_ddname=" + dd.Name; 
		var strResult = app.g.submit_php(strURL,true);
		lb_scripts.pickList = strResult;
	}
	
}

							lb_diary_OnValueChanged(strValue)
{
	//-- show diary update
	TabCond.ShowTabItem(3,strValue=="1");
	

}

							btn_add_vpme_OnPressed()
{
	//-- insert relation
	var strScript = lb_scripts.text;
	if(strScript!="")
	{
		var nextPos = sl_vpme.rowCount();
		nextPos++;
		
		var strParams = "aid="+app.pfu( _swdoc.ACTION().pk_id)+"&script="+app.pfu(strScript)+"&nid="+app.pfu(nextPos);
		app.g.submitsqs("form/cmdb_avail_baction/add_vpme_cond", strParams);

		sl_vpme.Refresh();
		app._een(btn_del_vpme, (sl_vpme.rowCount()>0));		
		sl_vpme.SetRowSelected(sl_vpme.rowCount()-1, true,false);
	}

}

							btn_del_vpme_OnPressed()
{
	var strConfirm = "Are you sure you want to remove this vpme script execution?";
	var intSequence = sl_vpme.GetItemTextRaw(sl_vpme.curSel, "execorder");
	if(app.g.sl_deldbrow(sl_vpme,sl_vpme.curSel,'pk_auto_id',null,strConfirm))
	{

		var strUpdate="";
		//-- loop through list and set seq for each - 1 to #
		var intID=0;
		var nextSeq=0;
		for(var x=0; x < sl_vpme.rowCount();x++)
		{
			var thisSeq = sl_vpme.GetItemTextRaw(x, "execorder");
			if(thisSeq>intSequence)
			{
				intID = sl_vpme.GetItemTextRaw(x, "pk_auto_id");
				var strParams = "vid="+app.pfu(intID);
				app.g.submitsqs("form/bpm_condition/dec_vpme_seq", strParams);
			}
		}
	
		sl_vpme.Refresh();
		sl_vpme.SetRowSelected(0);	
		sl_vpme_OnItemSelected(0);

		app._een(btn_del_vpme,(sl_vpme.rowCount()>0));
		
	}
}

							btn_up_pos_OnPressed()
{
	//-- NWJ : move vpme seq up 
	//-- get selected vpme id to move up
	var currRow = sl_vpme.curSel;
	var intUpID = sl_vpme.GetItemTextRaw(sl_vpme.curSel, 'pk_auto_id');
	var intUpSeq = sl_vpme.GetItemTextRaw(sl_vpme.curSel, 'execorder');	
	
	//-- get stage id to move down
	var intDownID = sl_vpme.GetItemTextRaw(sl_vpme.curSel-1, 'pk_auto_id');
	var intDownSeq = sl_vpme.GetItemTextRaw(sl_vpme.curSel-1, 'execorder');
	
	var strParams = "vid="+app.pfu(intUpID);
	if(app.g.submitsqs("form/cmdb_avail_baction/dec_vpme_seq", strParams))
	{
		var strParams = "vid="+app.pfu(intDownID);
		if(app.g.submitsqs("form/cmdb_avail_baction/inc_vpme_seq", strParams))
		{
			//-- refresh list
			sl_vpme.Refresh();
			sl_vpme.SetRowSelected(currRow-1, true, false);
		}
	}



}

							btn_down_pos_OnPressed()
{
	//-- NWJ : move vpme seq down 
	//-- get selected vpme id to move down
	var currRow = sl_vpme.curSel;
	var intUpID = sl_vpme.GetItemTextRaw(sl_vpme.curSel+1, 'pk_auto_id');
	var intUpSeq = sl_vpme.GetItemTextRaw(sl_vpme.curSel+1, 'execorder');	
	
	//-- get id to move down
	var intDownID = sl_vpme.GetItemTextRaw(sl_vpme.curSel, 'pk_auto_id');
	var intDownSeq = sl_vpme.GetItemTextRaw(sl_vpme.curSel, 'execorder');
	
	//-- update seq id for both 
	var p = {};	p.execorder = intDownSeq;
	if(app.g.submittableupdate("ci_avail_vpme",intUpID,p))
	{
		var p = {};	p.execorder = intUpSeq;
		if(app.g.submittableupdate("ci_avail_vpme",intDownID,p))
		{
			//-- refresh list
			sl_vpme.Refresh();
			sl_vpme.SetRowSelected(currRow+1, true, false);
		}
	}

}

							sl_vpme_OnItemSelected(nRow)
{
	
	app._een(btn_up_pos, (nRow>0));
	app._een(btn_down_pos, (nRow < (sl_vpme.rowCount()-1)));
}

							elipsis_cust_OnLeftClick()
{

  	//-- NWJ 
    //-- open customer search form in select mode
	app.g.search_for("customers",true,"",function(sResults)
	{
		if (sResults.selectedkeys=="") return;
	
		sResults.selectedkeys = app.g.string_replace(sResults.selectedkeys,",","','",true);
		
		if(_swdoc.ACTION().email_customers!="")_swdoc.ACTION().email_customers += ",";
		_swdoc.ACTION().email_customers += "'" + sResults.selectedkeys + "'";
		_swdoc.UpdateFormFromData()
	
		return false;
	});
}

							elipsis_cust_OnRightClick()
{
	//-- clear
	_swdoc.ACTION().email_customers = "";
	_swdoc.UpdateFormFromData();
	
	return false;
	
}

