	//-- clear
	_swdoc.ACTION().email_analysts = "";
	_swdoc.UpdateFormFromData();
	return false;
	
}

							elipsis_analysts_OnLeftClick()
{
	//-- NWJ - let user select analysts to email
	//-- popup standard analyst popup 
	
	var strAnalysts = _swdoc.ACTION().email_analysts;
	var dlg = new PickAnalystDialog;
	dlg.Open("Transfer to Analyst/Group",function(dlg)
	{
		if(!dlg)
			return;
			
		if(dlg.analystid != "")
    	{
    		//-- set single analystid
    		if(strAnalysts!="")strAnalysts+=",";
    		strAnalysts += "'" + dlg.analystid + "'";
    	}
    	else
    	{
    		//-- get all analysts in group
    		strNewAnalysts = app.g.get_group_analystids(dlg.groupid);
    		
    		//-- set all analyst ids in group	
			if(strAnalysts!="")strAnalysts+=",";    			
			strAnalysts += "'" + strNewAnalysts + "'";
    	}
    

    	_swdoc.ACTION().email_analysts = strAnalysts;
		_swdoc.UpdateFormFromData();
	});
	return false;
}

							lb_analyst_OnLeftClick()
{
	//-- NWJ - Set the assigned to properties
	var dlg = new PickAnalystDialog;
	dlg.Open("Transfer to Analyst/Group",function(dlg)
	{
		if(!dlg)
			return;
		
		_swdoc.ACTION().assignto_group = dlg.groupid;
		_swdoc.ACTION().assignto_analyst = dlg.analystid;
		_swdoc.UpdateFormFromData();
	});
	return false;
}

							lb_analyst_OnRightClick()
{
	_swdoc.ACTION().assignto_group = "";
	_swdoc.ACTION().assignto_analyst = "";
	app._ete(lb_analyst, "");
	app._ete(lb_group, "");
	_swdoc.UpdateFormFromData();
	return false;
}

							btn_apply_test1_OnPressed()
{
	
	if((lb_op1.text=="")||(lb_test.text ==""))
	{
		MessageBox("Please specify some test criteria");
		return;
	}
	
	var boolString=false;
	var strOp = lb_op1.text;
	var strTest = "";
	switch(lb_test.text.toLowerCase())
	{
		case "breach type":
			strTest = "&[in_breachtype]";		
			boolString = true;
			break;
		case "breach value":
			strTest = "&[in_breachvalue]";		
			boolString = true;
			break;
		case "cmdb status":
			strTest = "&[in_current_cmdb_status]";		
			boolString = true;
			break;
		case "related configuration id":
			strTest = "&[in_relateditem_id]";		
			boolString = true;		
			break;			
		case "related cmdb status":
			strTest = "&[in_relateditem_cmdb_status]";		
			boolString = true;		
			break;			

	}
	
	//--
	//-- get appropriate value
	var strValue = tb_value.text;	
	if(lb_cmdbstatus.visible)strValue=lb_cmdbstatus.value;	
	if(lb_ccis.visible)strValue=lb_ccis.value;		
	
	if(boolString)
	{
		strTest = "'" + strTest + "'";
		strValue = "'" + strValue + "'";
	}

	var strFullTest = strTest + strOp + strValue;
	_swdoc.ACTION().execoncondition = _swdoc.ACTION().execoncondition + " " + strFullTest;
	_swdoc.UpdateFormFromData();
	
	app._een(btn_apply_test1, false);
	app._een(btn_and1, true);
	app._een(btn_or1, true);

}

							btn_and1_OnPressed()
{
	_swdoc.ACTION().execoncondition = _swdoc.ACTION().execoncondition + " " + " AND " + " ";
	_swdoc.UpdateFormFromData();

	app._een(btn_apply_test1, true);
	app._een(btn_and1, false);
	app._een(btn_or1, false);	
	
}

							btn_or1_OnPressed()
{

	_swdoc.ACTION().execoncondition = _swdoc.ACTION().execoncondition + " " + " OR " + " ";
	_swdoc.UpdateFormFromData();

	app._een(btn_apply_test1, true);
	app._een(btn_and1, false);
	app._een(btn_or1, false);	

}

							lb_test_OnValueChanged(strValue)
{
	//-- show inputs for user based on the test they are doing
	if(strValue.toLowerCase().indexOf("cmdb status")!=-1)
	{
		app._evi(lb_cmdbstatus, true);
		app._een(lb_cmdbstatus, true);
		app._evi(lb_ccis, false);		
		app._evi(tb_value, false);
	}
	else if(strValue.toLowerCase().indexOf("configuration id")!=-1)
	{
		app._evi(lb_ccis, true);
		app._evi(tb_value, false);
		app._evi(lb_cmdbstatus, false);
	}
	else
	{
		app._evi(tb_value, true);
		app._evi(lb_cmdbstatus, false);
		app._evi(lb_ccis, false);
	}

}

