							Field20_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here

	if(strValue!="")
	{
        var p = {};
        p.sla = strValue;
        var slarec = app.g.get_sf_record("itsmsp_slad","itsmsp_slad.get_sla",p);
		ci_avail_action.raise_itsm_sla = slarec.pk_slad_id;
		app._een(_swdoc.MF().lbl_r_sla, true);
		app._een(_swdoc.MF().fld_r_sla, true);
		app._ema(_swdoc.MF().fld_r_sla, true);
	}
	else
	{
		ci_avail_action.raise_itsm_sla = 0;
		ci_avail_action.raise_sla = "";
		app._een(_swdoc.MF().lbl_r_sla, false);
		app._een(_swdoc.MF().fld_r_sla, false);
		app._ema(_swdoc.MF().fld_r_sla, false);
		_swdoc.UpdateFormFromData();
	}

}
