							tabRequest_OnTabSelected(nTab)
{
	var tIncs = 0;
	var tPrbs = 1;
	var tKes = 2;	
	var tRFCs = 3;
	var tRels = 4;
	var tSrs = 5;

	switch(nTab)
	{
		case tIncs:
			app.cmdb.sl_load_ci_calls(sl_incs, _swdoc.oCI().pk_auto_id , "INCIDENT", "","");
			break;
		case tPrbs:
			var strType = (flg_prb_cause.text==1)?"PROBLEM-CAUSE":"PROBLEM-AFFECT";		
			app.cmdb.sl_load_ci_calls(sl_prb, _swdoc.oCI().pk_auto_id , strType, "Problem","");
			break;
		case tKes:
			var strType = (flg_kes_cause.text==1)?"PROBLEM-CAUSE":"PROBLEM-AFFECT";		
			app.cmdb.sl_load_ci_calls(sl_kes, _swdoc.oCI().pk_auto_id , strType, "Known Error","");
			break;

		case tRFCs:
			var strType = (flg_rfc_cause.text==1)?"RFC-CAUSE":"RFC-AFFECT";
			app.cmdb.sl_load_ci_calls(sl_rfcs, _swdoc.oCI().pk_auto_id , strType, "","");
			break;
		case tRels:
			//var strType = (flg_rfc_cause.text==1)?"REL-CAUSE":"REL-AFFECT";
			var strType = "RELEASE";
			app.cmdb.sl_load_ci_calls(sl_rels, _swdoc.oCI().pk_auto_id , strType, "","");
			break;									
		case tSrs:
			var strType = "REQUEST";
			app.cmdb.sl_load_ci_calls(sl_srs, _swdoc.oCI().pk_auto_id , strType, "","");
			break;									
	}
	

}

