							lb_status_OnValueChanged(strValue)
{
	//-- reset status's
	_swdoc.oCI().isimpacted = "No";		
	_swdoc.oCI().isfaulty = "No";
	_swdoc.oCI().isdeactivated = "No";
	_swdoc.oCI().isactive = "No";		
	_swdoc.oCI().isunavailable = "No";	
				
	//-- check if selected status is an unavailable status or inactive
	var strStatusInfo = app.cmdb.get_statusinfo(_swdoc.oCI().ck_config_type, strValue);
	var strStatusColour = strStatusInfo.split("|")[0];
	var strStatus = strStatusInfo.split("|")[1];	
	
	app._ecol(lb_status, strStatusColour);
	//--87622
	mainform.OnFieldValueChanged("lb_cmdbstatus",strStatus);
	_swdoc.oCI().cmdb_status = strStatus;
	
	if ((strStatus.toLowerCase()=="unavailable") && (_swdoc.strOriginalUnavailability=="No"))
	{
		_swdoc.oCI().isunavailable = "Yes";
		if(!_swdoc.boolMakeUnavailable) 
		{
			_swdoc.boolMakeUnavailable = true;
		}
	}
	else if (strStatus.toLowerCase()=="deactivated")
	{
		_swdoc.oCI().isdeactivated = "Yes"
	}
	else if (strStatus.toLowerCase()=="faulty")
	{
		_swdoc.oCI().isfaulty = "Yes";		
	}
	else if (strStatus.toLowerCase()=="impacted")
	{
		_swdoc.oCI().isimpacted = "Yes";		
	}	
	else 
	{
		//-- assume active
		_swdoc.oCI().isactive = "Yes";				
	}
	
	//-- if was originally unavail and now making avail
	/*if((_swdoc.strOriginalUnavailability=="Yes") &&(!_swdoc.boolMakeUnavailable))
	{
		//-- if we can monitor this type of ci
		if(_swdoc.oCIType().flg_canmonitor==1 || _swdoc.oCI().flg_availmntr==1)
		{
			if(confirm("Setting this status will mark the configuration item as available. Would you like to modify the availability notification settings?"))
			{
				app._etab(tabCI, 2);
			}		
		}
	}*/
	
	//-- F0089008 always prompt for notification update
	//-- if we can monitor this type of ci
	if(_swdoc.oCIType().flg_canmonitor==1 || _swdoc.oCI().flg_availmntr==1)
	{
		if(confirm("Would you like to modify the availability notification settings?"))
		{
			app._etab(tabCI, 2);
		}
	}


	_swdoc.UpdateFormFromData();	
}

							btn_baseline_OnPressed()
{
	//-- Baseline active baseline
	//-- create exact copy of it and its extended details and availability settings
	if(!app.cmdb.can_baseline(true))return;
	
	var intNewCI = app.cmdb.create_baseline(_swdoc.oCI().pk_auto_id, _swdoc.oCI().baselineid, _swdoc.oCI().ck_config_type);
	if(intNewCI > 0)
	{
		_swdoc.boolBaselining = true;
		_swdoc.oCI().isactivebaseline = "No";
		_swdoc.Save(0);
		_swdoc.boolBaselining = false;
				
		if (confirm("The item has been baselined successfully. Do you want to view the active baseline?"))
		{
			//-- 
			_swdoc.CloseForm();
			app.cmdb.popup_configitem(intNewCI,true,"",true);			
		}
	}
	
}

