							sl_baselines_OnItemSelected(nRow)
{
	var strABLN = sl_baselines.GetItemText(nRow, 2);
	app._een(btn_reactivate, (strABLN!="Yes"));

	var intPK = sl_baselines.GetItemText(nRow, 0);
	app._een(btn_delete_bln, ((strABLN!="Yes")&&(intPK!=_swdoc.oCI().pk_auto_id)&& (dd.GetGlobalParamAsString("Application Settings/CMDB/AllowDatabaseDelete")==1) ));
	
	var strBLNIdx = sl_baselines.GetItemText(nRow, 1);
	app._een(btn_compare_bl, (strBLNIdx!=_swdoc.oCI().ck_baselineindex));

}

							btn_reactivate_OnPressed()
{
	if(!app.cmdb.can_baseline(true))return;
	var intCI = sl_baselines.GetItemText(sl_baselines.curSel, 'pk_auto_id');
	
	//-- get active bln
	var activeCIid = 0;
	var intRow = app.g.sl_findrow_byvalue(sl_baselines,"Yes","isactivebaseline");
	if (intRow>-1)activeCIid = sl_baselines.GetItemTextRaw(intRow, "pk_auto_id");
	
	if(app.cmdb.reactivate_baseline(intCI, activeCIid))
	{
		_swdoc.CloseForm();
		app.cmdb.popup_configitem(intCI,true,"",true);			
	}


}

							btn_delete_bln_OnPressed()
{
	if(!app.cmdb.can_baseline(true))return;
	var strDeleteItems = app.g.sl_getselected_valuesdel(sl_baselines,'pk_auto_id',",");
	var strConfirm = "Are you sure that you want to delete the selected baseline from the database?"
	if(confirm(strConfirm))
	{
		//-- F0101341
		var strParams = "cids=" + strItems;
		if( app.g.submitsqs("cmdb/process/delete_baseline",strParams)){
			//-- remove deleted items
			app.g.sl_clearrows(sl_baselines,strDeleteItems,0,null,0);
		}
	}
}

							flg_authorised_OnValueChanged(strValue)
{
	var strNewValue =(strValue==0)?"No":"Yes";
	
	var passbackvalue = app.cmdb.check_ci_element_attribute(flg_authorised,_swdoc,"config_itemi.isauthorised",strNewValue);
	if(passbackvalue=="__notcmdbfield__") return true;
	if(passbackvalue!=strNewValue)
	{
			_swdoc.strChangeBackBinding = "config_itemi.isauthorised";
			_swdoc.strChangeBackToValue = passbackvalue;
			//-- need to reset value back
			mainform.SetTimer(1, 10);
	}	
	else
	{
		_swdoc.oCI().isauthorised = strNewValue;
	}
	
}

