							btn_create_history_OnPressed()
{
	//-- create an availability history record
	var strArgs = "fk_ci_item="+_swdoc.oCI().ck_config_item+"&fk_ci_id=" + _swdoc.oCI().pk_auto_id + "&startedonx=" + tb_startedonx.text + "&endedonx=" + tb_resolvedonx.text;
	app.OpenFormForAdd("cmdb_avail_history", "", strArgs, true,function(oForm){
		if(oForm.document.boolSaved){
			_swdoc.oAvail().startedonx = "";
			_swdoc.oAvail().expectresolvebyx = "";		
			_swdoc.oAvail().resolvedonx = "";		
			_swdoc.UpdateFormFromData();	
			sl_availhist.Refresh();
		}
	});	
}

