							lb_user1_OnLeftClick()
{
	var strURL = "canreset=1&fk_company_id=" + app.pfu(_swdoc.oCI().fk_company_id);
	var oRes = app.g.search_for("customers",true,strURL);

	if(oRes.selectedkeys=="")return;
	
	var arrKeys = oRes.selectedkeys.split(",");
	var arrName = oRes.selectedother.split(",");	
	var arrCompIDS = oRes.selectedcompanyids.split(",");		
	mainform.OnFieldValueChanged("lb_user1",arrKeys[0]);
	_swdoc.oCI().fk_userdb = arrKeys[0];
	_swdoc.oCI().owner_name = arrName[0];	
	_swdoc.oCI().fk_company_id = arrCompIDS[0];		
	_swdoc.oCI().companyname = lb_company1.display;
	
	_swdoc.UpdateFormFromData();
}

