							lb_site1_OnValueChanged(strValue)
{
	if((_swdoc.strCurrentSite!=strValue)&&(_swdoc.strCurrentSite!="")&&(!_swdoc.boolSiteChanged))
	{
		//-- do we want to remove the old relationship
		if(confirm("Do you want to delete the CMDB relationship between this configuration item and the old site?"))
		{
			recSite = app.g.get_record("site", _swdoc.strCurrentSite)
			if(recSite)
			{
				app.cmdb.delete_meconfigrelation(recSite.fk_cmdb_id,_swdoc.oCI().pk_auto_id);  
			}
		}
	}
	_swdoc.boolSiteChanged=true;	

}

							lb_company1_OnRightClick()
{
	mainform.OnFieldValueChanged("lb_company1","");
	_swdoc.oCI().fk_userdb = "";
	_swdoc.oCI().owner_name = "";
	_swdoc.oCI().fk_company_id = "";
	_swdoc.oCI().companyname = "";
	_swdoc.oCI().fk_site = "";

	app._ete(lb_company1, "");
	_swdoc.UpdateFormFromData();

	if((_swdoc.strCurrentSite!="")&&(!_swdoc.boolSiteChanged))
	{
		//-- do we want to remove the old relationship
		if(confirm("Do you want to delete the CMDB relationship between this configuration item and the old site?"))
		{
			recSite = app.g.get_record("site", _swdoc.strCurrentSite)
			if(recSite)
			{
				app.cmdb.delete_meconfigrelation(recSite.fk_cmdb_id,_swdoc.oCI().pk_auto_id);  
			}
		}
	}
	_swdoc.boolSiteChanged=true;	

	return true;
}

							lb_company1_OnLeftClick()
{
	//-- search for ci company
	var oRes = app.g.search_for("company");
	if(oRes.selectedkeys=="")return;
	
	var arrKeys = oRes.selectedkeys.split(",");
	var arrName = oRes.selectedother.split(",");	
	strHash = arrKeys[0];
	
	if ((strHash == '') || (strHash == 'none')) return;
	
	if ((strHash == _swdoc.oCI().fk_company_id))return;
	
	mainform.OnFieldValueChanged("lb_company1",strHash);
	_swdoc.oCI().fk_company_id = strHash;	
	_swdoc.oCI().fk_site = "";
	
	if(_swdoc.oCI().fk_userdb!="")
	{
			var oRec = app.g.get_record("userdb", _swdoc.oCI().fk_userdb);
			if((oRec)&&(oRec.fk_company_id!=strHash))
			{
				_swdoc.oCI().fk_userdb = "";
				_swdoc.oCI().owner_name ="";
			}
	}
	
	_swdoc.UpdateFormFromData();
	_swdoc.oCI().companyname = lb_company1.display;


	return false;
}

