							mbtn_ci_contract_view1_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;	
	var iOrg = 2;	
	
  	if(nItemID == iProperties)
	{
 		var strKeyValue = app.g.sl_getprikeyvalue(sl_contracts1, sl_contracts1.curSel);
		if (strKeyValue=="")return;
		var strType = app.g.sl_getcolvalue(sl_contracts1, sl_contracts1.curSel, "type");
		if(strType=="supplier")
		{
			app.OpenFormForEdit("Tf3PartyContract", strKeyValue, "", true, function(){
				sl_contracts1.SetRowSelected(sl_contracts1.curSel,true);
				
				//-- refresh in case removed ci from contract while viewing properties
				var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
				var strFilter = "swc=pk_contract_id&kvs=0&sf=type_supplier";
				if(strContracts!=0){
					strFilter = "swc=pk_contract_id&sf=type_supplier&kvs="+ strContracts;
				}
				app._slf(sl_contracts1,strFilter);
				sl_contracts1.SetRowSelected(0);
			});	
		}else
		{
			app.OpenFormForEdit("itsm_contract", strKeyValue, "", true, function(){
				sl_contracts1.SetRowSelected(sl_contracts1.curSel,true);
				//-- refresh in case removed ci from contract while viewing properties
				var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
				var strFilter = "swc=pk_contract_id&kvs=0&sf=type_supplier";
				if(strContracts!=0){
					strFilter = "swc=pk_contract_id&sf=type_supplier&kvs="+ strContracts;
				}
				app._slf(sl_contracts1,strFilter);
				sl_contracts1.SetRowSelected(0);
			});
		}
		sl_contracts1.SetRowSelected(sl_contracts1.curSel,true);
   }
  	else if(nItemID == iOrg)
	{
		var strOrg = sl_contracts1.GetItemTextRaw(sl_contracts1.curSel, "fk_company_id");
		if (strOrg=="")return;
		var strType = app.g.sl_getcolvalue(sl_contracts1, sl_contracts1.curSel, "type");
		if(strType=="supplier")
		{
			app.OpenFormForEdit("EfSupplier", strOrg, "",true, function(){
				//-- refresh in case removed ci from contract while viewing properties
				var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
				var strFilter = "swc=pk_contract_id&kvs=0&sf=type_supplier";
				if(strContracts!=0){
					strFilter = "swc=pk_contract_id&sf=type_supplier&kvs="+ strContracts;
				}
				app._slf(sl_contracts1,strFilter);
				sl_contracts1.SetRowSelected(0);
			})	;
		}
		else
		{
			app.OpenFormForEdit("TfCompany", strOrg, "",true, function(){
				//-- refresh in case removed ci from contract while viewing properties
				var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
				var strFilter = "swc=pk_contract_id&kvs=0&sf=type_supplier";
				if(strContracts!=0){
					strFilter = "swc=pk_contract_id&sf=type_supplier&kvs="+ strContracts;
				}
				app._slf(sl_contracts1,strFilter);
				sl_contracts1.SetRowSelected(0);
			})	;
		}
    }
	//-- refresh in case removed ci from contract while viewing properties
	var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
	var strFilter = "swc=pk_contract_id&kvs=0&sf=type_supplier";
	if(strContracts!=0)
	{
		strFilter = "swc=pk_contract_id&sf=type_supplier&kvs="+ strContracts;
	}
	app._slf(sl_contracts1,strFilter);
	sl_contracts1.SetRowSelected(0);
	
}

