							btn_clone_OnPressed()
{
	// TODO: Add your event handler code here
	//-- 2.4 - prompt analyst for number of clones
	//-- 87523 - save CI before cloning
	_swdoc.Save(0);
	var strText = "Number of Clones of "+_swdoc.oCI().ck_config_item+"?";
	var intNumberOfCopies = prompt(strText,"1");
	if(intNumberOfCopies==null)
		return;
	if(isNaN(intNumberOfCopies))
	{
		MessageBox('Invalid number of copies. Please input a number.');
	}
	else
	{
		if(intNumberOfCopies>0)
		{
			if(intNumberOfCopies>100)
				if(!confirm('You have entered a large number of clones to be created, proceed?'))
					return false;
			var resultValue = app.cmdb.create_clone(_swdoc.oCI().pk_auto_id, _swdoc.oCI().baselineid, _swdoc.oCI().ck_config_type,intNumberOfCopies);
			if(resultValue<0)
				MessageBox('Failed to clone CI.');
			else
			{
				var boolConfirm = confirm('Cloning completed. View new configuration items?');
				if(boolConfirm)
				{
					app.cmdb.search_ci(true, "canreset=1&ck_config_item=CLONE__"+_swdoc.oCI().ck_config_item+"__%");
				}
			}
		}else
		{
			MessageBox('Please input a value greater than 0.');
		}
	}




}

							tb_identifier_OnValueChanged(strValue)
{
	if(app.cmdb.ci_exists(strValue,_swdoc.oCI().ck_config_type))
	{
		//-- this ci id and type already exists warn user and cancel save
		MessageBox("The " + _swdoc.oCI().ck_config_type + " item " + strValue + " already exists in the CMDB, a duplicate cannot be created");
		app._eva(mainform.tb_identifier, _swdoc.strConfigItem);
		app._ete(mainform.tb_identifier, _swdoc.strConfigItem);
		_swdoc.oCI().ck_config_item = _swdoc.strConfigItem;
		return false;
	}else
	{
		_swdoc.oCI().flg_canrename = 0;
		_swdoc.strConfigItem = strValue;
	}


}

