							fld_fk_sld1_OnValueChanged(strValue)
{
	if(_swdoc.oCI().pk_auto_id>0)
	{
		var arrParams ={};
		arrParams.fk_child_id = config_itemi.pk_auto_id;
		arrParams.fk_parent_type = 'ME->SLA';
		arrParams.fk_parent_itemtext = strValue;
		var intCount = app.g.sqs_rowcountbycol("CONFIG_RELI",arrParams);
		if(intCount==0 && strValue!="")
		{
        	var strRel = "Uses";
        	var recSLA = app.g.get_record("itsmsp_slad", strValue);    
			app.cmdb.generate_meconfigrelation(recSLA.fk_cmdb_id,config_itemi.pk_auto_id,strRel); 
		}
	}
	
	// TODO: Add your event handler code here
	var boolEnable = strValue!="";
	_swdoc.oCI().fk_sla = "";


	app._een(_swdoc.MF().lb_fk_sla, boolEnable);
	app._een(_swdoc.MF().Label4, boolEnable);
	_swdoc.UpdateFormFromData();

}

