function setup_form_fields()
{
	var strValue = majinc_notif.type;
	var boolHide = false;
	if(strValue=="Support Group")
	{
		boolHide = true;
		_swdoc.MF().TabType.ShowTabItem(1,true);
	}else if(strValue=="All")
	{
		boolHide = true;
		_swdoc.MF().TabType.ShowTabItem(2,true);
	}else if(strValue=="External Email")
	{
		boolHide = true;
		_swdoc.MF().TabType.ShowTabItem(3,true);
	}
	if(boolHide)	
		_swdoc.MF().TabType.ShowTabItem(0,false);
	app._een(_swdoc.MF().msg1, _swdoc.note().flg_messenger==1);
	app._ema(_swdoc.MF().analyst, _swdoc.MF().TabType.tab==0);
	app._ema(_swdoc.MF().group1, _swdoc.MF().TabType.tab==1);
	app._ema(_swdoc.MF().bespoke_email, _swdoc.MF().TabType.tab==3);

	var boolEmail = _swdoc.note().flg_email==1;
	_swdoc.email_update(boolEmail);
	_swdoc.ResetModiedFlag("majinc_notif");



	_swdoc.UpdateFormFromData();
}

function email_update(boolEmail)
{
	app._een(_swdoc.MF().lb_mailbox1,boolEmail);
	app._een(_swdoc.MF().lb_template1,boolEmail);
	app._een(_swdoc.MF().lb_mailbox2,boolEmail);
	app._een(_swdoc.MF().lb_template3,boolEmail);
	app._een(_swdoc.MF().lb_mailbox3,boolEmail);
	app._een(_swdoc.MF().lb_template3,boolEmail);

	app._ema(_swdoc.MF().lb_mailbox1,boolEmail);
	app._ema(_swdoc.MF().lb_template1,boolEmail);
	app._ema(_swdoc.MF().lb_mailbox2,boolEmail);
	app._ema(_swdoc.MF().lb_template3,boolEmail);
	app._ema(_swdoc.MF().lb_mailbox3,boolEmail);
	app._ema(_swdoc.MF().lb_template3,boolEmail);

	app._een(_swdoc.MF().Label1,boolEmail);
	app._een(_swdoc.MF().Label2,boolEmail);
	app._een(_swdoc.MF().Label3,boolEmail);
	app._een(_swdoc.MF().Label4,boolEmail);
	app._een(_swdoc.MF().Label5,boolEmail);
	app._een(_swdoc.MF().Label6,boolEmail);

	if(!boolEmail)
	{
		_swdoc.note().mailbox = "";
		_swdoc.note().email_template = "";
	}
	_swdoc.UpdateFormFromData();

}

type_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	var lTab = TabType.tab;
	if(strValue=="Analyst")
	{
		majinc_notif.fld_display = majinc_notif.groupid+":"+majinc_notif.analystid;
		TabType.ShowTabItem(0,true);
	}else if(strValue=="Support Group")
	{
		majinc_notif.fld_display = majinc_notif.groupid ;
		TabType.ShowTabItem(1,true);
	}else if(strValue=="All")
	{
		TabType.ShowTabItem(2,true);
		majinc_notif.fld_display = "All";
	}else if(strValue=="External Email")
	{
		majinc_notif.fld_display = majinc_notif.email;
		TabType.ShowTabItem(3,true);
		majinc_notif.flg_email = 1;
	}

	TabType.ShowTabItem(lTab,false);
	app._ema(analyst, TabType.tab==0);
	app._ema(group1, TabType.tab==1);
	app._ema(bespoke_email, TabType.tab==3);

}

analyst_OnLeftClick()
{
	// TODO: Add your event handler code here
	app.g.pick_analyst("Select Analyst", function(oAnalyst)
	{
		if(!oAnalyst)
			return false;
		app._ete(analyst, oAnalyst.name);
		majinc_notif.analystid = oAnalyst.id;
		majinc_notif.groupid = oAnalyst.supportgroup;
		majinc_notif.fld_display =  oAnalyst.supportgroup+":"+oAnalyst.id;
		_swdoc.UpdateFormFromData();	
	});

	return false;
}

