function init_next_lbs()
{
//	app._een(_swdoc.MF().lb_nq1,(_swdoc.oQ().fk_nextstage!="0"));

	if(_swdoc.oQ().fk_nextwiz=="")
	{
		if(_swdoc.oQ().fk_nextstage != "0")_swdoc.oQ().fk_nextstage = "0";
		if(_swdoc.oQ().fk_nextstage != "0")app._ete(_swdoc.MF().lb_ns1,"0");
	}

	if(_swdoc.oQ().fk_nextstage=="0")
	{
//		app._een(_swdoc.MF().lb_nq1,false);	
		if(_swdoc.oQ().fk_nextq != "0")_swdoc.oQ().fk_nextq = "0";
		if(_swdoc.oQ().fk_nextq != "0")app._ete(_swdoc.MF().lb_nq1,"0");
	}	

	_swdoc.UpdateFormFromData();
}

						OnSaveData()
{
	if(!app.service.can_update(true))
		return false;
	// TODO: Add your event handler code here
	if(_swdoc.oQ().pk_qid<1)
	{
		if(_swdoc.GetArg("fk_form_id"))
			_swdoc.oQ().fk_form_id = _swdoc.GetArg("fk_form_id");
			_swdoc.oQ().qindex = _swdoc.GetArg("qindex");		
	}

	return true;
}

						OnDataSaved()
{

  // TODO: Add your event handler code here
  _swdoc.UpdateFormFromData();
  if(_swdoc.boolInserting)
  {
    var strChoiceKeys = app.g.sqllist_get_valuesdel(mainform.sl_choices1, 'PK_QCID', ",", "");
    if(strChoiceKeys!="")
    {
      var strParams = "qid="+wssm_wiz_q.pk_qid+"&cids="+app.pfu(strChoiceKeys);
      app.g.submitsqs("form/wiz_question/update_choice_key", strParams);
    }
  }
  var qID = _swdoc.wssm_wiz_q.pk_qid;
  var get_fk_qid = "cmd=get_fk_qid&table=wssm_wiz_q_valid&qID="+qID;
  var queRS = new SqlQuery;
  queRS.InvokeStoredQuery("Execute/question_validation_types", get_fk_qid); //send post request
}

