							lb_type_OnValueChanged(strValue)
{
  app._een(lb_picker,(strValue=="Custom Picker"));
  app._ema(lb_picker, lb_picker.enable);

  wssm_wiz_q.pickername = (!lb_picker.enable)?"":wssm_wiz_q.pickername;
  _swdoc.UpdateFormFromData();


   if (app.LC(strValue)!="radiobox" && app.LC(strValue)!="selectbox" && app.LC(strValue)!="option selector - single select"){
    app._eva(flg_jump,0);
    app._een(flg_jump,false);
    wssm_wiz_q.flg_jumponanswer = 0;

      //Close tab jump to another question
      TabQ.ShowTabItem(2,false);
   }
   else {
          app._eva(flg_jump , wssm_wiz_q.flg_jumponanswer);
          app._een(flg_jump , true);

          if (flg_jump.value)
            TabQ.ShowTabItem(2,true);
      }

  //validation types only enabled if lb_type is multiline or texbox.

  if (( app.LC(strValue) == "textbox" ) ||
     ( app.LC(strValue) == "multiline" )) {
    app._een(mainform.lb_validation , true);
  } else {
    app._een(mainform.lb_validation , false);
    app._een(mainform.btn_add_validation , false);
    //mainform.btn_add_validation.visible = false;
  };

  TabQ.ShowTabItem(3,false);
  //-- hide show choices
  switch(app.LC(strValue))
  {
    case 'textbox':
    case 'multiline':
    case 'radiobox':
    case 'checkbox':
    case 'selectbox':
      TabQ.ShowTabItem(1,true);
      TabQ.ShowTabItem(3,false);
      break;
    case 'option selector':
    case 'option selector - single select':
      TabQ.ShowTabItem(1,false);
      TabQ.ShowTabItem(3,true);
      break;
    default:
      TabQ.ShowTabItem(1,false);
      TabQ.ShowTabItem(3,false);
      break;
  }

  app._etab(TabQ,0);

}

