							tb_target_OnValueChanged(strValue)
{
	if(strValue!="")
	{
		if(strValue.toLowerCase().indexOf("extoc_")!=0)
		{
			if ((app.LC(strValue).indexOf("itsm_opencall_rfc.")!=0) && (app.LC(strValue).indexOf("itsm_opencall_problem.")!=0) &&  (app.LC(strValue).indexOf("opencall.")!=0) && (app.LC(strValue).indexOf("updatedb.")!=0) )
			{
				MessageBox("The data binding should be made against opencall, updatedb or an extoc_ table.");
				wssm_wiz_q.targetcolumn="";
				_swdoc.UpdateFormFromData();
			}
		}

		if(strValue=="opencall.probcode")
		{
			if(wssm_wiz_q.flg_prefixq==1)
				MessageBox("Cannot prefix question for probcode field.");
			wssm_wiz_q.flg_prefixq = 0;
			_swdoc.UpdateFormFromData();
			app._een(fld_prefix,false);	
			return true;	
		}
	}
	app._een(fld_prefix,true);		

}

