							lb_validation_OnValueChanged(strValue)
{
// Regex, et all  default text is set in globals of _swdoc
var setElement = function(type){
 mainform.validation_details.text = type;
 mainform.validation_details.color = '#767676';
 mainform.validation_details.enable = true;
};
var disableElement = function(){
  mainform.validation_details.text = '';
  mainform.validation_details.color = '#767676';
  mainform.validation_details.enable = false;
};
var enable_err_msg = function(){
 mainform.err_msg.text = '';
 mainform.err_msg.color = '#767676';
 mainform.err_msg.enable = true;
};

switch(strValue) {
  case 'URL':
  	setElement(_swdoc.sUrl)
  	break;
  case 'Regex':
    setElement(_swdoc.sRegex)
    break;
  case 'Min Length':
    setElement(_swdoc.sMin)
    break;
  case 'Max Length':
    setElement(_swdoc.sMax)
    break;
  default:
    disableElement()
    enable_err_msg()
    break;
};

//loop through table.
var i = 0;
var tableArray = [];
while(i < SqlList1.rowCount()) {
  // make add/update button display correct name
  if (mainform.SqlList1.GetItemText(i, 0) == lb_validation.text){
    app._ete(mainform.btn_add_validation , 'Update');
    break;
  } else {
    app._eva(mainform.btn_add_validation , 'Add')
  };
  // deselect all rows when ValidationType is selected from Dropdown Menu. To avoid accidental deletions.
  if (SqlList1.IsRowSelected(i)) {
  	SqlList1.SetRowSelected(i, false, false);
  };
  i ++;
};


//disable add/update button if validation type field is empty.
mainform.lb_validation.value == ''
  ? app._een(btn_add_validation , false)
  : app._een(btn_add_validation , true);

}

