							btn_add_validation_OnPressed()
{
//Get text from text fields
var valid_details = mainform.validation_details;
var valid_type = mainform.lb_validation;
var err_msg = mainform.err_msg;
var qID = _swdoc.wssm_wiz_q.pk_qid;
//MessageBox(mainform.validation_details.text)
//Dont save data and send alert if details are needed
if (valid_type.value == ''){
  app._een(btn_add_validation , false);
  MessageBox("Cannot add blank Validation Type");
  return false;
} else if (valid_details.enable){
  if ( (valid_details.value == '' )
  || (_swdoc.detailsArr.indexOf(valid_details.text)!= -1 && valid_details.text != '' ) ) {
    MessageBox("Please add validation details to submit.");
    return false;
  };
};

//Make send info in data fields to php file to make post request
var queRS = new SqlQuery;
var strParams = "qID="+qID;
    strParams += "&type="+valid_type.text;
    strParams += "&details="+encodeURIComponent(valid_details.text);
    strParams += "&err_msg="+encodeURIComponent(err_msg.text);
//  For checking if sending proper string to PHP file:  
//MessageBox(strParams);

queRS.InvokeStoredQuery("Execute/question_validation_types", strParams);

//refresh table
SqlList1.Refresh();

var hasRows = "table=wssm_wiz_q&qID="+qID+"&valid_type=";
if(SqlList1.rowCount() > 0){
  hasRows += 1;
  queRS.InvokeStoredQuery("Execute/question_validation_types", hasRows);
}

return true;

}

							btn_remove_OnPressed()
{
//TODO: make button disabled until table row is selected.

var qID = _swdoc.wssm_wiz_q.pk_qid;
var i = 0;
var strParams = "cmd=remove&qID="+qID;


var updateString = function(nRow) {
  strParams += "&type=" + SqlList1.GetItemTextRaw(nRow, 0);
  valid_details = SqlList1.GetItemTextRaw(nRow, 1);
  if(valid_details != '' && valid_details != 'Add additional required info IE regex or min/max length' ) {
    strParams += ("&details=" + valid_details);
  }

};

//update stdring to match row that's selected
while(i < SqlList1.rowCount()) {
  if (SqlList1.IsRowSelected(i)) {
  	updateString(i);
  };
  i ++;
};
var queRS = new SqlQuery;
queRS.InvokeStoredQuery("Execute/question_validation_types", strParams); //send post request
SqlList1.Refresh(); //refresh table after post request is sent

var hasNoRows = "table=wssm_wiz_q&qID="+qID+"&valid_type=";
if(SqlList1.rowCount() == 0){
  hasNoRows += 0;
    queRS.InvokeStoredQuery("Execute/question_validation_types", hasNoRows);
}

return true;

}

