return true;
}

							SqlList1_OnItemSelected(nRow)
{

  //get selcted row
  var nRow = 0;
  var i = 0;
  while(i < SqlList1.rowCount()) {
    if (SqlList1.IsRowSelected(i)) {
      nRow = i;
      break;
    } else {
      i ++;
    };
  };

  //get values from nrow:
  var rowValue = [];
  rowValue[0] = SqlList1.GetItemTextRaw(nRow, 0);
  rowValue[1] = SqlList1.GetItemTextRaw(nRow, 1);
  rowValue[2] = SqlList1.GetItemTextRaw(nRow, 2);

  //update textbox = rowValue
  var needsDetails = 'Regex, Min Length, Max Length, URL';
  var updateElement  = function() {
    mainform.btn_add_validation.value = 'Update';
    mainform.lb_validation.text = rowValue[0];
    mainform.lb_validation.value = rowValue[0];
    mainform.validation_details.value = rowValue[1];
    mainform.err_msg.value = rowValue[2];
    mainform.err_msg.text = rowValue[2];

    if ( needsDetails.indexOf(rowValue[0]) != -1 ) {
      mainform.validation_details.text = rowValue[1];
      mainform.validation_details.enable = true;
      mainform.validation_details.color = '#000';
      mainform.btn_add_validation.value = 'Update';
    } else {
      var disableElement = function(){
        mainform.validation_details.color = '#767676';
        mainform.validation_details.enable = false;
        //MessageBox('false');
      };
      disableElement();

    };
  };

  updateElement(rowValue);


  return true;

}

							err_msg_OnValueChanged(strValue)
{

return true;
}

