action.attrib1:
if(_swdoc.OC().logcall_source=='SELFSERVICE')
{
    var strParams = 'in_callref='+_swdoc.OC().callref;
    var intCount = app.g.get_sqrecordset("call/common/count_wiz_qs",strParams);
    if(intCount>0)
    {
 		var strServer = '&[app.webroot]';
 		var strCurrentdd = '&[app.currentdd]';

		app.global.OpenHtmlWindow("wssm_wiz_input", "frame", strServer + "/clisupp/details/"+ strCurrentdd +"/wssm_wiz_input.php?sessionid="+app.session.sessionId+"&fk_callref="+_swdoc.OC().callref, "Selfservice Wizard Input", true, 950, 700);
    }
    else
    {
     //MessageBox("resolved call count = " + intCount);
    }
}
else
{
	MessageBox('Call was not logged via selfservice.')
}

strURL= "company_code="+_swdoc.oCompany().pk_company_id;
        
if (_swdoc.OC().custom_i3){
        app.OpenFormForEdit("itsmEfQLogResCloseData", _swdoc.OC().custom_i3,strURL,true, function(aForm){});
}       
else
{
	app.OpenFormForAdd("itsmEfQLogResCloseData", "", strURL, true, function(aForm){
		if (aForm)
		{
			var newRecId = aForm.document.record_id;
			if (newRecId != ""){
				_swdoc.OC().custom_i3 = newRecId;
			}
			else {
				MessageBox("No Resolution / Closure details were created. If this form is saved as a Quick Log Call, there will be no Resolution / Closure details.");
			}
		}
	});
}

method.code:
						OnCallLoggedOK(nCallRef,
                        dateRespondBy,
                        dateFixBy,
                        strCustName,
                        strSla)
{


    //-- nwj 
    //-- 15.03.2004   
    var httpserver = app.session.server + ":" + app.session.httpPort;
    var intCallRef = app.g.callref_unpad(nCallRef);
	
	_swdoc.boolLogged = true;
	_swdoc.loggedCallref = intCallRef;

	var strDiaryText = "";
    var olist = _swdoc.EF().sl_configitems;
    //-- loop through selected CI and assign them to this call
    for (var x=0; x < olist.rowCount(); x++)
    {
        var pk_ci_id = olist.GetItemTextRaw(x,"pk_auto_id");
        var ci_item_key = olist.GetItemTextRaw(x,"ck_config_item");

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'INCIDENT'))
		{
			strDiaryText    += "Configuration Item (" + ci_item_key + ") was added to this Incident.\n"
		}
    }
    //-- if we have Ci to add add it
    if (strDiaryText!="")
    {
		var strActionSource = "Incident Management";
		var strCode			= "Added Configuration Item";
		app.g.new_dairyevent(intCallRef,strDiaryText,strActionSource,strCode);    
    }


	var strDiaryText = "";
    var olist = _swdoc.EF().sl_services;
    //-- loop through selected CI and assign them to this call
    for (var x=0; x < olist.rowCount(); x++)
    {
        var pk_ci_id = olist.GetItemTextRaw(x,"pk_auto_id");
        var ci_item_key = olist.GetItemTextRaw(x,"ck_config_item");

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'INCIDENT'))
		{
			strDiaryText    += "Service (" + ci_item_key + ") was added to this Incident.\n"
		}
    }
    //-- if we have Ci to add add it
    if (strDiaryText!="")
    {
		var strActionSource = "Incident Management";
		var strCode			= "Added Service";
		app.g.new_dairyevent(intCallRef,strDiaryText,strActionSource,strCode);    
    }

    //-- insert a relationship between incident and problem / ke
    var strProblems = app.g.sl_get_valuesdel(_swdoc.EF().sl_problems,0,",");
    if (strProblems !="")
    {
    	var strText = "This incident has been associated with the following Problems and Known Errors\n\n";
    	var strClasses = app.g.sl_get_valuesdel(_swdoc.EF().sl_problems,1,",");
    	var arrClasses = strClasses.split(",");
    	var arrProblems = strProblems.split(",");
    	for(var x=0; x < arrProblems.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(arrProblems[x],intCallRef,'PROBLEM-INCIDENT'))
			{
				strText += arrClasses[x] + " (" + app.g.pad_callref(arrProblems[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strPrbText = "The incident (" + nCallRef + ") has been associated to this " + arrClasses[x];
				app.g.new_diaryevent(arrProblems[x],strPrbText,"Incident Management","Incident Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Incident Management","Problem Association");
    }

    //-- insert a relationship between incident and rfcs
    var strRFCS = app.g.sl_get_valuesdel(_swdoc.EF().sl_rfcs,0,",");
    if (strRFCS !="")
    {
    	var strText = "This incident has been associated with the following Change Requests\n\n";
    	var strClasses = app.g.sl_get_valuesdel(_swdoc.EF().sl_rfcs,1,",");
    	var arrClasses = strClasses.split(",");
    	var arrRFCS = strRFCS.split(",");
    	for(var x=0; x < arrRFCS.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(arrRFCS[x],intCallRef,'RFC-INCIDENT'))
			{
				strText += "Change Request (" + app.g.pad_callref(arrRFCS[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strRfcText = "The incident (" + nCallRef + ") has been associated to this " + arrClasses[x];
				app.g.new_diaryevent(arrRFCS[x],strRfcText,"Incident Management","Incident Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Incident Management","Change Association");
    }


     //-- insert a relationship between incident and rfcs
    var strSRs = app.g.sl_get_valuesdel(_swdoc.EF().sl_srs,0,",");
    if (strSRs !="")
    {
    	var strText = "This incident has been associated with the following Service Requests\n\n";
    	var arrSRs = strSRs.split(",");
    	for(var x=0; x < arrSRs.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(arrSRs[x],intCallRef,'REQUEST-INCIDENT'))
			{
				strText += "Service Request (" + app.g.pad_callref(arrSRs[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strRfcText = "The incident (" + nCallRef + ") has been associated to this Service Request";
				app.g.new_diaryevent(arrSRs[x],strRfcText,"Incident Management","Incident Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Incident Management","Service Request Association");
    }

     //-- insert a relationship between incident and related incidents
    var strRIncs = app.g.sl_get_valuesdel(_swdoc.EF().sl_relincs,0,",");
    if (strRIncs !="")
    {
    	var strText = "This incident has been associated with the following incidents\n\n";
    	var arrRIncs = strRIncs.split(",");
    	for(var x=0; x < arrRIncs.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(arrRIncs[x],intCallRef,'INCIDENT-INCIDENT'))
			{
				app.g.relate_calls(intCallRef,arrRIncs[x],'INCIDENT-INCIDENT');
				strText += "Incident (" + app.g.pad_callref(arrRIncs[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strRfcText = "The incident (" + nCallRef + ") has been associated to this incident";
				app.g.new_diaryevent(arrRIncs[x],strRfcText,"Incident Management","Incident Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Incident Management","Incident Association");
    }

     //-- insert a relationship between incident and major incidents
    var strMIncs = app.g.sl_get_valuesdel(_swdoc.EF().sl_majinc,0,",");
    if (strMIncs !="")
    {
    	var strText = "This incident has been associated with the following major incidents\n\n";
    	var arrMIncs = strMIncs.split(",");
    	for(var x=0; x < arrMIncs.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(arrMIncs[x],intCallRef,'MINC-INCIDENT'))
			{
				strText += "Major Incident (" + app.g.pad_callref(arrMIncs[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strRfcText = "The incident (" + nCallRef + ") has been associated to this major incident";
				app.g.new_diaryevent(arrMIncs[x],strRfcText,"Incident Management","Incident Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Incident Management","Major Incident Association");
    }

	if(_swdoc.OC().itsm_majorinc=="1")
	{
		app.g.new_diaryevent(intCallRef ,"This incident has been logged as a Major Incident","Incident Management","Major Incident Creation");
     	//-- insert a relationship between incident and major incidents
    	var strIncs = app.g.sl_get_valuesdel(_swdoc.EF().sl_assinc,0,",");
    	if (strIncs !="")
    	{
    		var strText = "This major incident has been associated with the following incidents\n\n";
    		var arrIncs = strIncs.split(",");
    		for(var x=0; x < arrIncs.length; x++)
    		{
				//-- call app.g method to link problem to incident
				if(app.g.relate_calls(intCallRef,arrIncs[x],'MINC-INCIDENT'))
				{
					strText += "Incident (" + app.g.pad_callref(arrIncs[x]) + ") associated\n";
					
					//-- update ke or problem diary
					strRfcText = "The major incident (" + nCallRef + ") has been associated to this incident";
					app.g.new_diaryevent(arrIncs[x],strRfcText,"Incident Management","Incident Association");
					
				}
			}			
			app.g.new_diaryevent(intCallRef ,strText,"Incident Management","Major Incident Association");
    	}
    }

    //-- insert a relationship between incs and doc
    if(!app.g.use_sw_knowledgebase())
	{
		if(app.strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			var strDocs = app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,",");
    		if (strDocs !="")
    		{
    			var strText = "This Incident has been associated with the following Knowledgebase Documents\n\n";
    			var arrDocs = strDocs.split(",");
    			for(var x=0; x < arrDocs.length; x++)
    			{
					//-- call app.g method to link problem to incident
					app.g.relate_call_to_rakb(arrDocs[x],intCallRef,'Used','Incident',false);
					strText += "Knowledgebase Document (" + arrDocs[x] + ") associated\n";
				}			
				app.g.new_diaryevent(intCallRef ,strText,"Incident Management","Knowledgebase Document Association");
    		}
		}
	}
	else
	{
    	var strDocs = app.g.sl_get_valuesdel(_swdoc.EF().sl_kbs1,0,",");
    	if (strDocs !="")
    	{
    		var strText = "This Incident has been associated with the following Knowledgebase Documents\n\n";
    		var arrDocs = strDocs.split(",");
    		for(var x=0; x < arrDocs.length; x++)
    		{
				//-- call app.g method to link problem to incident
				app.oKB.relate_call_to_kb(arrDocs[x],intCallRef,'Used');
				strText += "Knowledgebase Document (" + arrDocs[x] + ") associated\n";
			}			
			app.g.new_diaryevent(intCallRef ,strText,"Incident Management","Knowledgebase Document Association");
    	}
   	}

	//-- F0105032 - audit trail if incoming call	
	if(_swdoc._form.PreloadType==2)
	{
		if(_swdoc.OC().bpm_workflow_id!="")
		{
			var intEpochTime = app.global.GetCurrentEpocTime();
			var oAnalyst = app.g.get_sys_record("swanalysts",app.session.analystId);
			var oGroup = app.g.get_sys_record("swgroups",app.session.groupId);
			var strParams = "fk_workflow_id="+app.pfu(_swdoc.OC().bpm_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				var strTable = arrBinding[0];
				var strColName = arrBinding[1];
				
				var oldValue = _swdoc.oOpencall[strColName];
				
				var newValue = _swdoc[strTable][strColName];
				if(app.g.dd_isnumeric(strTable, strColName))
				{
					if(newValue<0)
						newValue = app.g.fix_epoch(newValue);
					if(oldValue<0)
						oldValue = app.g.fix_epoch(oldValue);
				}
				if(oldValue!=newValue)
				{
					var strFieldDisplay = dd.tables[app.LC(strTable)].columns[strColName].DisplayName;
					var strFmtOldValue = app.g.dd_format(strTable, strColName, oldValue);
					var strFmtNewValue = app.g.dd_format(strTable, strColName, newValue);
					var oInsertTmp = new app.oXmlmcInsert('oc_audit');
					oInsertTmp.setfield('fk_callref',_swdoc.OC().callref);
					oInsertTmp.setfield('updatetimex',intEpochTime);
					oInsertTmp.setfield('updatedby',app.session.analystId);
					oInsertTmp.setfield('updatedbyname',oAnalyst.name);
					oInsertTmp.setfield('updatedbygroup',app.session.groupId);
					oInsertTmp.setfield('updatedbygroupname',oGroup.name);
					oInsertTmp.setfield('databinding',strBinding);
					oInsertTmp.setfield('bindingfield',arrBinding[1]);
					oInsertTmp.setfield('bindingtable',arrBinding[0]);
					oInsertTmp.setfield('fielddisplay',strFieldDisplay);
					oInsertTmp.setfield('oldvalue',oldValue);
					oInsertTmp.setfield('newvalue',newValue);
					oInsertTmp.setfield('fmtoldvalue',strFmtOldValue);
					oInsertTmp.setfield('fmtnewvalue',strFmtNewValue);
					oInsertTmp.execute();
				}
			}
		}
	}	//-- EOF F0105032 
}

						OnRecordResolved(strTable)
{
	//MessageBox("Resolved : " + strTable);
	//-- set sla based on worflow default
	if(IsObjectDefined("mainform") && (strTable=="bpm_workflow"))
	{
		var boolSet = true;
		var boolEnable = true;

		if(bpm_workflow.fk_default_sla_new>0)
		{
			var boolSLA = bpm_workflow.flg_sla_apply>0;
			if(boolSLA)
			{
				var boolSet = true;
				if(_swdoc.boolIncSLASetManually)
				{
					var boolOverride = confirm("It is recommended to use SLA set against the process.\n\n\Would you like to set the SLA to the process value?");
					if(!boolOverride)
						boolSet = false;
				}
				
				if(boolSet)
				{
					if(bpm_workflow.flg_sla_enforce==1)
						boolEnable = false;
				}
			}
			else
			{
				boolSet = false;
			}
		}
		else
		{
			boolSet = false;
		}
		
		if(boolSet)
		{
			//-- F0107934
			_swdoc.boolSLAEnforced = false;
			_swdoc.handle_set_sladef(bpm_workflow.fk_default_sla_name,false,true);
			var boolTempSLASetManually = _swdoc.boolIncSLASetManually;
			_swdoc.boolIncSLASetManually = true;
			_swdoc.handle_set_priority(bpm_workflow.fk_default_sla);
			_swdoc.boolIncSLASetManually = boolTempSLASetManually;
			_swdoc.UpdateFormFromData();
			//-- END
			if(!boolEnable)
			{
				app._een(mainform.lb_oc_slad1,false);
				app._een(mainform.lbl_oc_sladef,false);
				_swdoc.boolSLAEnforced = true;
			}
		}
		
		if(boolEnable)
		{
			app._een(mainform.lb_oc_slad1,true);
			app._een(mainform.lbl_oc_sladef,true);
			_swdoc.boolSLAEnforced = false;
		}
		
		app.g.process_bpm_form_attribute_check(_swdoc,bpm_workflow.pk_workflow_id);
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}

    //-- When auto resolved user then we will need to refresh some of the sqllists as they dont do so
    //-- automatically
    if (IsObjectDefined("mainform") && strTable == "userdb")
    {
    	//-- F0099057, reset CI Ids to force re-query
 		_swdoc.companyCIIDS = "";
		_swdoc.contractCIIDS = "";
		_swdoc.siteCIIDS = "";
     	_swdoc.setup_customer_details()

    	//-- rjc - 2.4.0 - customer changed and site selected, confirm site part of new customers organisation
		if(_swdoc.OC().site!="[Use Customers Site]"){
			var recSite = app.g.get_record("site", _swdoc.OC().site);
			if(recSite)
			{
				if(app.LC(recSite.fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
				{
					_swdoc.OC().site = "[Use Customers Site]";
					app._ete(_swdoc.MF().lb_oc_site, "[Use Customers Site]");
				}
			}
		}
		
		if(_swdoc.OC().costcenter!="[Use Customer Charge Centre]"){
			var recCC = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(recCC)
			{
				if(app.LC(recCC.fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
				{
					_swdoc.OC().costcenter = "[Use Customer Charge Centre]";
					app._ete(_swdoc.MF().lb_oc_cc, "[Use Customer Charge Centre]");
				}
			}
		}
		if(_swdoc.OC().fk_dept_code!="[Use Customer Department]"){
			var recCC = app.g.get_record("department", _swdoc.OC().fk_dept_code);
			if(recCC)
			{
				if(app.LC(recCC.fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
				{
					_swdoc.OC().fk_dept_code = "[Use Customer Department]";
					app._ete(_swdoc.MF().fld_oc_dept,"[Use Customer Department]");
				}
			}
		}
		
		// DTH: display the SLA from the Customer based on the default. Tnis will also set the Priority if there is one for the Customer
		if(_swdoc.OC().itsm_slaname=="[Use Customer SLA]" && _swdoc.oCust().sld_name != "")
		{
			_swdoc.MF().lb_oc_slad1_OnValueChanged("[Use Customer SLA]");
		}		
				
		//-- check if customer is in active    	
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
				
		_swdoc.set_org_filter();
		
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
    }
    if(strTable == "company")
 	{
 		//-- Grab Company Record
 		var oCompanyTemp = app.g.get_record("company",_swdoc.oCompany().pk_company_id,'companyname');
 		//--F0110887 Start
		var intExpiryEpoch = app.g.fix_epoch(oCompanyTemp.supportexpiryx);
		app.g.checkOrgSupport(_swdoc.oCust().companyname, intExpiryEpoch, true);
		//--F0110887 End
 	}

    
    if (IsObjectDefined("mainform") && strTable == "company")
    {
    	
    	//###20120716
    	_swdoc.multi_c_set_company(_swdoc.oCompany().pk_company_id);
    	
     	//-- F0099057, reset CI Ids to force re-query
 		_swdoc.companyCIIDS = "";
		_swdoc.contractCIIDS = "";
		_swdoc.siteCIIDS = "";
	   	app.g.strLogAndResolveCompany = _swdoc.OC().fk_company_id;
/*###
    	if(_swdoc.oCust().fk_company_id!=_swdoc.OC().fk_company_id)
    	{
    		_swdoc.ResetRecord('userdb');
    		
			if(dd.GetGlobalParamAsNumber("Application Settings/Incident/OnOrganisationResolveCustomer")==1)
			{
				_swdoc.boolResolvingCust = true;
				_swdoc.strSearchFor = "";
				mainform.SetTimer(1, 10);
			//_swdoc.ResolveRecord("userdb", "keysearch", _swdoc.resolve_cust_record(""));
			}
		}
*/				
		if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
		
		if(_swdoc.MF().fld_cis.value =="by Organisation")
    		_swdoc.load_company_cis();
		else if(_swdoc.MF().fld_cis.value =="by Site")
			_swdoc.load_site_cis();
 		else
 		{
 			if(_swdoc.oCust().keysearch=="")
 			{
 				app._slf(_swdoc.MF().sl_cust_cis,"pk_auto_id=-1");
 			}
 		}
 		_swdoc.check_cis();
 		
 		//-- rjc - 2.4.0 - customer changed and site selected, confirm site part of new customers organisation
		if(_swdoc.OC().site!="[Use Customers Site]"){
			var recSite = app.g.get_record("site", _swdoc.OC().site);
			if(recSite)
			{
				if(app.LC(recSite.fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
				{
					_swdoc.OC().site = "[Use Customers Site]";
					app._ete(_swdoc.MF().lb_oc_site, "[Use Customers Site]");
				}
			}
		}
 		if(_swdoc.OC().costcenter!="[Use Customer Charge Centre]"){
			var recCC = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(recCC)
			{
				if(app.LC(recCC.fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
				{
					_swdoc.OC().costcenter = "[Use Customer Charge Centre]";
					app._ete(_swdoc.MF().lb_oc_cc,  "[Use Customer Charge Centre]");
				}
			}
		}
		if(_swdoc.OC().fk_dept_code!="[Use Customer Department]"){
			var recCC = app.g.get_record("department", _swdoc.OC().fk_dept_code);
			if(recCC)
			{
				if(app.LC(recCC.fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
				{
					_swdoc.OC().fk_dept_code = "[Use Customer Department]";
					app._ete(_swdoc.MF().fld_oc_dept, "[Use Customer Department]");
				}
			}
		}
		
		//-- Filter Contracts for Company
		app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract);

		if(_swdoc.OC().itsm_slaname!="")
		{
			//-- Set Contract Based on Selected SLA
			var slarec = app.g.get_sqrecordset("general/get_itsm_sla","kv="+_swdoc.OC().itsm_slaname);
			if(slarec.Fetch())
			{
				var strSLA = app.g.get_field(slarec,"pk_slad_id");
				if(strSLA!="")
					_swdoc.handle_set_contract(strSLA);
			}
		}
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);		
		_swdoc.UpdateFormFromData();
	}
	
}

						OnRecordValueChanged(strTable,
                              strColumn,
                              strValue,
                              strOldValue)
{
    //-- NWJ - 16.04.2004
    //MessageBox(strTable+" "+strValue+" "+strColumn);
    //-- when user changes probcode (profile)
    //-- launch APR
    if (strColumn == "probcode")
    {
		//-- F0093877
		_swdoc.boolChangeProfile = true;
    	_swdoc.intProfileCodeCI = -1; //-- user has manually set the profile code    	
        if (strValue != strOldValue)
		{
			//-- 29.04.2008 - nwj - tidy up by using doc func
			_swdoc.process_profilecode_checks();
			

            //-- 27.05.2004 get attribute 1 of profile
            //-- which is the preferred support group
            _swdoc.determine_prefered_support(false,strValue);
        }
        app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
        mainform.SetTimer(4, 100);
    }   
    else if( (strColumn=="itsm_impact_level")||(strColumn=="itsm_urgency_level") )
    {
    	_swdoc.boolIncSLASetManually = true; //--107865
    	 
    	if(_swdoc.OC().itsm_sladef!="")
    	{
    		//-- if use incident assessment over ci
    		/*if(app.itsm.get_ci_assessmnet_level_setting("Incident")!=1)
    		{
    			_swdoc.OC().priority = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
    			_swdoc.UpdateFormFromData();
    		}*/
    		
    		var strPriority = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
			if(strPriority!="") _swdoc.handle_set_priority(strPriority,false);
			_swdoc.UpdateFormFromData();
    	}
    } else if(strColumn=="priority" && strTable == "opencall")
    {
    	if(_swdoc.boolChangeProfile)
    	{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			var oSLA = app.g.get_record("itsmsp_slad", oRec.itsm_sla);

			_swdoc.handle_set_sladef(oSLA.slad_id);
			_swdoc.boolIncSLASetManually = true;
			_swdoc.handle_set_priority(oRec.sla);
 			_swdoc.UpdateFormFromData();
   		}
    }
    else if (strColumn=="fk_company_id"||strColumn=="site"||strColumn=="fk_dept_code"||strColumn=="costcenter"||strColumn=="pk_workflow_id")
	{
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}

    
}

						OnLogCall()
{
	_swdoc.set_preferred_assignment(false);
    _swdoc.OC().custom_i3=0;
    return true;
}

