//###20120716
function multi_c_set_company(companyId){

	var c = app.g.get_record("company", companyId);
	_swdoc.OC().fk_company_id = c.pk_company_id;	
	_swdoc.OC().companyname = c.companyname;
	
	_swdoc.call_comp_fk_cmdb_id = c.fk_cmdb_id;
	app._eva(_swdoc.MF().pl_oc_org , c.pk_company_id);
	app._ete(_swdoc.MF().pl_oc_org , c.pk_company_id);
	_swdoc.MF().pl_oc_org.display = c.companyname;
	
	var boolCompanySet = ("" !== _swdoc.OC().fk_company_id);
	app._ema(_swdoc.MF().x_fld_org, !boolCompanySet);
	app._evi(_swdoc.MF().x_fld_org, !boolCompanySet);
	app._een(_swdoc.MF().x_fld_org, false);
	
	app._ema(_swdoc.MF().pl_oc_org, boolCompanySet);
	app._evi(_swdoc.MF().pl_oc_org, boolCompanySet && _swdoc.MF().lb_company.visible);
	app._een(_swdoc.MF().pl_oc_org, boolCompanySet && (1 == dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")) );

    _swdoc.set_org_filter();
    
    _swdoc.load_cis();
}

function set_org_filter(){

  	if (""!=_swdoc.OC().cust_id){
		var strKeyValues = "pk_company_id="+app.pfu(_swdoc.OC().fk_company_id) + "&fk_user_id="+app.pfu(_swdoc.OC().cust_id);
		app._slf(_swdoc.MF().pl_oc_org , strKeyValues);
	} else {
		app._slf(_swdoc.MF().pl_oc_org , "pk_company_id=");
	}
	var strCompFilter = "_swc_fk_company_id=" + _swdoc.MF().pl_oc_org.value;
	app._slf(_swdoc.MF().fld_oc_dept , strCompFilter);
	app._slf(_swdoc.MF().lb_oc_site , strCompFilter);
	app._slf(_swdoc.MF().lb_oc_cc , strCompFilter);

	_swdoc.OC().site = (_swdoc.OC().fk_company_id.toLowerCase() != _swdoc.oCust().fk_company_id.toLowerCase())?"":_swdoc.oCust().site;
	_swdoc.OC().fk_dept_code = (_swdoc.OC().fk_company_id.toLowerCase() != _swdoc.oCust().fk_company_id.toLowerCase())?"":_swdoc.oCust().department;
	_swdoc.OC().costcenter = (_swdoc.OC().fk_company_id.toLowerCase() != _swdoc.oCust().fk_company_id.toLowerCase())?"":_swdoc.oCust().costcenter;
	_swdoc.OC().fk_contract_id = "";
	_swdoc.handle_set_contract("");	
	_swdoc.UpdateFormFromData();
}




function setup_customer_details()
{   
	_swdoc.load_cis();

	var oUserdbRating = app.g.get_record("userdb_rating",_swdoc.oCust().keysearch,'fk_keysearch');
	if(oUserdbRating!=null)
	{
		app._ete(_swdoc.MF().lbl_open_request,": "+oUserdbRating.active_count);
		app._ete(_swdoc.MF().lbl_closed_request,": "+oUserdbRating.inactive_count);
	}

	//-- show overall rating
	switch(_swdoc.oCust().flg_ocrating)
	{
		case 1:
			app._eim(_swdoc.MF().img_orating,"&[app.webroot]" + "/clisupp/formimages/itsm/cr_neg16.gif");
			app._ete(_swdoc.MF().lbl_orating,": Overall satisfaction negative");
			break;			
		case 2:
			app._eim(_swdoc.MF().img_orating,"&[app.webroot]" + "/clisupp/formimages/itsm/cr_neu16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction neutral");
			break;						
		case 3:
		
			app._eim(_swdoc.MF().img_orating,"&[app.webroot]" + "/clisupp/formimages/itsm/cr_pos16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction positive");
			break;						
		default:
			app._eim(_swdoc.MF().img_orating,"&[app.webroot]" + "/clisupp/formimages/itsm/cr_neu16.gif");
			app._ete(_swdoc.MF().lbl_orating,": Overall satisfaction undetermined");
			break;
	}
	
	_swdoc.multi_c_set_company(_swdoc.oCust().fk_company_id);
	//-- log from email fix
	if(app.LC(_swdoc.oCust().fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))	
	{
		_swdoc.ResolveRecord("company", "pk_company_id", _swdoc.oCust().fk_company_id);
	}
	
}

function load_cis()
{
	var ciFilter = _swdoc.MF().fld_cis.value;
	
	switch(ciFilter){
		case "by Organisation":
		case "by Settings: Organisation":
			if ("[Use Customer's Organisation]" == _swdoc.OC().fk_company_id) _swdoc.OC().fk_company_id = _swdoc.oCust().fk_company_id;
	    	_swdoc.load_company_cis(_swdoc.OC().fk_company_id);		 
			break;
		case "by Site":
		case "by Settings: Site":
			if ("[Use Customer's Site]" == _swdoc.OC().site) _swdoc.OC().site = _swdoc.oCust().site;
			_swdoc.load_site_cis(_swdoc.OC().site);
			break;
		case "by Customer: Organisation":
			_swdoc.load_company_cis(_swdoc.oCust().fk_company_id);		 
			break;
		case "by Customer: Site":
			_swdoc.load_site_cis(_swdoc.oCust().site);		 
			break;
		case "by Contract":
			_swdoc.load_contract_cis();
			break
		default: _swdoc.load_customer_items();
	}
    
}

//###20120607 - filtering out unwanted services
function filter_out_non_display_services(CIIDS){

	if(CIIDS=="")
		CIIDS = "-1";
	var strServiceKeys = "-1";
    var oRS = app.g.get_sqrecordset("call/incident/services_to_exclude","ci_id="+CIIDS);
    while (oRS.Fetch()){ 
		strServiceKeys +=",";
		strServiceKeys += app.g.get_field(oRS,"fk_cmdb_id");
	}
	return strServiceKeys;
}

function load_customer_items()
{
	//###20120719 - rewrite to cope with change of customer
	if(_swdoc.oCust().keysearch=="")
	{
		app._slf(_swdoc.MF().sl_cust_cis,"pk_auto_id=-1");
		return;
	}
	//-- load users config items	
	if(typeof _swdoc.multi_customerCIIDS[_swdoc.oCust().keysearch] === "undefined")
		_swdoc.get_customer_cis(_swdoc.oCust().keysearch);
		
	// DTH: webclient - add the following from load_company_services
	var userCIS = _swdoc.multi_customerCIIDS[_swdoc.oCust().keysearch];
		
	if(typeof _swdoc.multi_customerServiceIDS[_swdoc.oCust().keysearch] === "undefined"){
		_swdoc.multi_customerServiceIDS[_swdoc.oCust().keysearch] = app.service.get_customer_services(_swdoc.oCust().keysearch,true);

		//###20120607 - filtering out unwanted services
		if(_swdoc.boolServiceCIBehaviourOnIncident) _swdoc.multi_customerServiceIDS[_swdoc.oCust().keysearch] = _swdoc.filter_out_non_display_services(_swdoc.multi_customerServiceIDS[_swdoc.oCust().keysearch]);
		
	}
			
	// DTH: webclient - add the following from load_site_services
	var serviceKeys = _swdoc.multi_customerServiceIDS[_swdoc.oCust().keysearch];

	// DTH: webclient - adding the following from Load_customer_services
	//var serviceKeys = app.service.get_customer_services(_swdoc.oCust().keysearch,true);
	if(userCIS=="")
		userCIS = "-1";
	userCIS = serviceKeys + "," + userCIS;
	
	_swdoc.update_ci_list(userCIS);
}

//-- uncheck then re check those that exist
function uncheckset_customer_items()
{
	for(var y=0; y < _swdoc.MF().sl_cust_cis.rowCount();y++)
	{
		_swdoc.MF().sl_cust_cis.SetRowChecked(y, false);
	}

	//-- check those that are linked to this incident
	var strCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems,'pk_auto_id',",");
	var arrCIS = strCIs.split(",");
	for(var y=0; y < arrCIS.length;y++)
	{
		var intRowPos = app.g.sl_findrow_byvalue(_swdoc.MF().sl_cust_cis, arrCIS[y], 'pk_auto_id');	
		if(intRowPos>-1)
		{
			_swdoc.MF().sl_cust_cis.SetRowChecked(intRowPos, true);
		}
	}	


// DTH: webclient - swap references in the following from _swdoc.MF().sl_cust_services to _swdoc.MF().sl_cust_cis
	//-- check those that are linked to this incident
	var strCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_services,'pk_auto_id',",");
	var arrCIS = strCIs.split(",");
	for(var y=0; y < arrCIS.length;y++)
	{
		var intServRowPos = app.g.sl_findrow_byvalue(_swdoc.MF().sl_cust_cis, arrCIS[y], 'pk_auto_id');	
		if(intServRowPos>-1)
		{
			_swdoc.MF().sl_cust_cis.SetRowChecked(intServRowPos, true);
		}
	}	
}

function check_probprofile_against_problems_kes()
{
	//ib.RemoveAllItems();
    _swdoc.ib.RemoveItemsByGroup("problem");
           
    //-- when user selects a problem call profile
    //-- chec kto see if we have a matching Problem record
  //if (_swdoc.itsm_settings.prb_auto_apr == 1)
    if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AutomaticProfileProblemRecognition")==1)
    {    			
        //-- now search for any problem calls that have the same profile code
        //--
        if(_swdoc.OC().probcode!="")
        {
  			var intCount = 0;
   			var aRS = app.g.get_sqrecordset("call/incident/prb_cnt_profcode","probcode="+_swdoc.OC().probcode);
			if (aRS.Fetch())
			{
				intCount = app.g.get_field(aRS,"prbcount");
			}
	
        	if (intCount > 0 )        
        	{
            	//-- there are some matching problem calls for this problem profile
            	//-- so show in the info bar (as requested by pre-sales - used to be an alert)
            	_swdoc.ib.AddItem("There are Problems or Known Errors that potentially match this Incident.", "5", "red", "problem");
				return;
        	}
        }
    }
          
    var strCIIds = "";  
    if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AutomaticCIProblemRecognition")==1)
    {    			
        strCIIds = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems,"pk_auto_id",",");
    }
    
    if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AutomaticServiceProblemRecognition")==1)
    {    	
        if(strCIIds=="")
        {
         	strCIIds = app.g.sl_get_valuesdel(_swdoc.EF().sl_services,"pk_auto_id",",");
        }else
        {
         	var strServiceIDs = app.g.sl_get_valuesdel(_swdoc.EF().sl_services,"pk_auto_id",",");
         	if(strServiceIDs!="")
        		strCIIds += "," +strServiceIDs;
        }
    }

	if(strCIIds!="")
	{
		var intCount = 0;
   		var oRec = app.g.get_sqrecordset("call/incident/prb_cnt_related","ci_id="+strCIIds);
		if(oRec.Fetch())
		{
			intCount = app.g.get_field(oRec,"cnt");
		}

       	if (intCount > 0 )        
        {
           //-- there are some matching problem calls for this problem profile
            //-- so show in the info bar (as requested by pre-sales - used to be an alert)
            _swdoc.ib.AddItem("There are Problems or Known Errors that potentially match this Incident.", "5", "red", "problem");
		}
		
	}
	_swdoc.UpdateFormFromData();
}

//-- 02.03.2006
//-- check if breached incident count
function check_probprofile_against_incidentcount(strProbCode, intAlertTrigger,pcRecord)
{
	//-- if alert trigger is not specified we need to get counter from probcode table
	_swdoc.intProblemAlertPeriod = 0;
	_swdoc.intFirstProblemIncidentDatex = 0;
	_swdoc.intPeriodBreach = 0;	
	_swdoc.incident_profile_count	= 0;
	
	if (intAlertTrigger==-1)
	{
		if(pcRecord)
		{
			var pcRecord = app.g.get_record("pcinfo",strProbCode);	
		}
		else
		{
			var pcRecord =app.g.get_record("pcinfo",strProbCode);	
		}
		
		if(pcRecord)
        {
        	intAlertTrigger = new Number(pcRecord.user2);
        	_swdoc.intProblemAlertPeriod  = pcRecord.user3;
			_swdoc.intProblemAlertPeriod--;_swdoc.intProblemAlertPeriod++;
		  	_swdoc.intFirstProblemIncidentDatex = app.g.fix_epoch(pcRecord.first_incident_datex);    	
			_swdoc.incident_profile_count = pcRecord.incident_count;	
        }
        else
        {
        	intAlertTrigger = 0;
        }
        
        //-- switched off
        if(_swdoc.intProblemAlertPeriod =="")_swdoc.intProblemAlertPeriod = 0;
	}

	_swdoc.incident_profile_count++;
	_swdoc.boolProblemAlert = false;
    if (intAlertTrigger > 0)
    {		
		//-- if greater than alert analyst of possible problem
		if (_swdoc.incident_profile_count >= intAlertTrigger)
		{
			//-- we need ot raise some sort of alert when the call is actually logged
			_swdoc.boolProblemAlert = true;

			//-- if this profile uses an alert period (ie number of incidents rasied within an hour or 2 etc		
			if(_swdoc.intProblemAlertPeriod > 0)			
			{
				testDatex = app.global.GetCurrentEpocTime() - _swdoc.intFirstProblemIncidentDatex;
				testPeriod = _swdoc.intProblemAlertPeriod * 3600; //-- get period in seconds
				
				
				//-- if number of incidents has exceed limit and within period - then signal alert to pm
				if (testDatex < testPeriod)
				{
					_swdoc.intPeriodBreach = 1;
					
					//-- 07.11.2006 - NWJ
					//-- check if we should tell the analyst (some customer will want this others wont) 
					//-- so if you are configuring just switch to 0 in dd
					if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AlertPossibleIncidentProfileBreach")==1)
   					{
						MessageBox("There may be a potential problem. There are currently " + _swdoc.incident_profile_count + " Incidents that have this incident profile.");
					}	
				}
			}
			else
			{
				//-- profile doesnt use period limit just incident limit
				_swdoc.intPeriodBreach = 1;
				
				//-- 23.11.2009 - RJC
				//-- if you reach the threshold without using an alert period
				//-- check if we should tell the analyst (some customer will want this others wont) 
				//-- so if you are configuring just switch to 0 in dd
				if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AlertPossibleIncidentProfileBreach")==1)
   				{
					MessageBox("There may be a potential problem. There are currently " + _swdoc.incident_profile_count + " Incidents that have this incident profile.");
				}	
			}
		}		
    }    
}

function determine_prefered_support(boolCI, strKeyValue)
{		
    //-- 12.01.2007
    //-- NWJ
    //-- do we want to use CI prefered support over profile
	var intPPS = dd.GetGlobalParamAsNumber("Application Settings/Incident/ProfilePSTakesPriorityOverCiPS", 1);    
	var boolSetByCI=false;    
    //-- if added or removed a CI/s
    if (boolCI)
    {   
    	//-- returns row pos that was used to make assessment - we use this to
    	//-- then determine pref support 
    	var intUseCIKey = _swdoc.check_ci_assessment();
    	
    	//-- check if we want to use ci support prefered group / analyst
    	if ((intPPS==0) || (_swdoc.OC().itsm_prefered_suppgroup=="") ||(_swdoc.OC().probcode==""))
    	{
 			//-- only do if we have one ci or we have an assessment ci pos
   			if((intUseCIKey>-1)||(_swdoc.EF().sl_configitems.rowCount()==1))
    		{    	
   				if(intUseCIKey==-1)	intUseCIKey=0;
    			//-- work out which ci support group / analyst to use
    			_swdoc.intAssessmentCI = intUseCIKey;//app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIRow,"pk_auto_id");
 				var intCIRow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems,intUseCIKey,"pk_auto_id");	
				if(intCIRow==-1)
				{
 					var intCIRow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_services,intUseCIKey,"pk_auto_id");	
    				var strSupportG = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intCIRow,"ci_support_group");
					var strSupportA = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intCIRow,"ci_support_analyst");  
				}else{   			
    				var strSupportG = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intCIRow,"ci_support_group");
					var strSupportA = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intCIRow,"ci_support_analyst");  
				}  		
				if(strSupportA!="")
				{			
					//-- have an analyst
					var anAnalyst = app.g.get_analyst_detail(strSupportA);		
					if(anAnalyst)							
					{
						_swdoc.OC().itsm_prefered_suppgroup = strSupportA;
						_swdoc.OC().itsm_prefered_text = anAnalyst.name;
						boolSetByCI=true;												
					}
				}
				else if(strSupportG != "")
				{
					//-- have a supp group
					var aGroup = app.g.get_group_detail(strSupportG);    	
					if(aGroup)
					{
						_swdoc.OC().itsm_prefered_suppgroup = strSupportG;
						_swdoc.OC().itsm_prefered_text = aGroup.name;
						boolSetByCI=true;						
					}
				}
				else
				{
					//-- there is no prefer so reset
					//-- 29.04.2008 - out of 2.2.0 testing
					_swdoc.OC().itsm_prefered_suppgroup = "";
					_swdoc.OC().itsm_prefered_text = "";	
					
				}
			}
    	}
    	
    }
    else
    {
    	//-- manually added or changed profile
    	//-- 2.2.0 29.04.2008 - apply if profile ps is prefered or if there is no pref already
    	if( (intPPS!=0)||(_swdoc.OC().itsm_prefered_suppgroup=="")) 
    	{
    		
    		if(!boolCI)_swdoc.intAssessmentCI = -1; //-- so we can reset appropriately
        	if((strKeyValue=="")||(strKeyValue==undefined))strKeyValue = _swdoc.OC().probcode;
        	var oRec = app.g.get_record("pcinfo",strKeyValue);
        	if (oRec)
        	{
        		_swdoc.OC().itsm_prefered_suppgroup = "";
				_swdoc.OC().itsm_prefered_text = "";
	
        	
        		var anAnalyst = app.g.get_analyst_detail(oRec.user1);		
        		if(anAnalyst)
        		{
					_swdoc.OC().itsm_prefered_suppgroup = anAnalyst.id;
					_swdoc.OC().itsm_prefered_text = anAnalyst.name;       	
        		}
        		else
        		{
        			var aGroup = app.g.get_group_detail(oRec.user1);    	
        			if(aGroup)
        			{
						_swdoc.OC().itsm_prefered_suppgroup = aGroup.id;
						_swdoc.OC().itsm_prefered_text = aGroup.name;
					}
        		}
        	}
            	
        	//-- NWJ - 01.03.2005
        	//-- check the alert count for this problem profile
        	//-- if it has one find number of incidents in sys cache that match
        	_swdoc.check_probprofile_against_incidentcount(strKeyValue, -1,oRec);
        	
    	}
	}
 
    _swdoc.UpdateFormFromData();
}

//-- do probcode checking
function process_profilecode_checks()
{
	//-- refresh pcode incident list
	//-- have to apply sleep otherwise the sqllist does not filter
	if(_swdoc.bool_slpcodeincs_displayed)
	{
		//app.global.Sleep(50);
		_swdoc.EF().sl_incprofile.Refresh();		
	}        

    //-- go see if there is a matching problem
    _swdoc.check_probprofile_against_problems_kes();
}
function check_ci_assessment()
{
   	//###20120619 - only show SLAs connected to selected CIs
    if (app.oSettings.get_setting("boolIncidentSLABehaviourOnCI")){
		//-- get which SLA we should use based on CI/s
		var strSLAs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems,"fk_sld",",","");
		
		if (""!=strSLAs)
		{
			var strFilter = _swdoc.strOrigSLAFilter;
			arrSLAs = strSLAs.split(",");
			var strSLAFilter = "";
			for(var i = 0; i<count(arrSLAs);i++)
			{
				strSLAFilter += "&_swc_pk_slad_id[]="+arrSLAs[i];
			}
			strFilter = strFilter + strSLAFilter;
        	app._slf(_swdoc.MF().lb_oc_slad1 , strFilter);
        	if (""!=_swdoc.MF().lb_oc_slad1.value)
        	{
        		var strParams = strFilter+"&_swc_slad_id="+app.pfu(_swdoc.MF().lb_oc_slad1.value);
	        	var oRS = app.g.get_sqrecordset("call/incident/get_slas_match_cis",strParams);
               	if(!oRS.Fetch())
                {
	                _swdoc.OC().itsm_slaname = "";
                }
        	}
		} 
		else 
		{
			app._slf(_swdoc.MF().lb_oc_slad1 , _swdoc.strOrigSLAFilter);
        	_swdoc.OC().itsm_slaname = "";
		}
	}

    //-- if use ci assessment levels
   	var intUseCIrow = -1; 
   	
    if(app.itsm.get_ci_assessmnet_level_setting("Incident")==1)
    {
       	var strUseSLA = "";
		
 		var slaArray = new Array();
 
 		var oList = _swdoc.EF().sl_configitems;
		var strSLAs = "";
		var strVal="";
		var strSLAVal="";
		for (var x=0;x<oList.rowCount();x++)
		{
			strVal = oList.GetItemTextRaw(x,"fk_sla");
			strSLAVal = oList.GetItemTextRaw(x,"fk_sld");
			if(strVal =="[Use SLA Default Priority]")
			{
				var oRecSLD = app.g.get_record("itsmsp_slad", strSLAVal);
				if(oRecSLD)
				{	
					strVal = oRecSLD.fk_ssla;
				}
			}
			strVal = app.g.pfs(strVal);
			if(strVal!="")
			{
				if (strSLAs !="")strSLAs +=",";
				strSLAs  += strVal;
				slaArray[strVal]=strSLAVal;
			}
		}

 		var oList = _swdoc.EF().sl_services;
		var strVal="";
		for (var x=0;x<oList.rowCount();x++)
		{
			strVal = oList.GetItemTextRaw(x,"fk_sla");
			strSLAVal = oList.GetItemTextRaw(x,"fk_sld");
			if(strVal =="[Use SLA Default Priority]")
			{
				var oRecSLD = app.g.get_record("itsmsp_slad", strSLAVal);
				if(oRecSLD)
				{	
					strVal = oRecSLD.fk_ssla;
				}
			}
			strVal = app.g.pfs(strVal);
			if(strVal!="")
			{
				if (strSLAs !="")strSLAs +=",";
				strSLAs  += strVal;
				slaArray[strVal]=strSLAVal;
			}
		}
  		//var strSLAs = app.g.get_sld_slas(strSLAs);
    	if(dd.GetGlobalParamAsNumber("Application Settings/Incident/UseCILongestSLA")==1)    			
		{
			strUseSLA = app.g.get_longest_sla(strSLAs);    			
    	}
    	else
    	{
    		strUseSLA = app.g.get_shortest_sla(strSLAs);
    	}			

		//-- if have SLA to use
		intUseCIRow = 0;
		var boolIsService = false;
		if(strUseSLA!="")
		{
			var intSLD = slaArray[strUseSLA]; 		
			//-- we have a prefered SLA - get the CI that has this SLA and use its assess levels
			intUseCIrow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems,strUseSLA,"fk_sla");	
			if(intUseCIrow==-1)
			{
				//not a ci found with priority
				boolIsService = true;
				intUseCIrow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_services,strUseSLA,"fk_sla");
				if(intUseCIrow==-1)
				{
					//not found with priority try with SLA
					intUseCIrow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems,intSLD,"fk_sld");	
					if(intUseCIrow==-1)
					{
						boolIsService = true;
						intUseCIrow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_services,intSLD,"fk_sld");	
   						var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_impact_level");
						var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_urgency_level"); 
						var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIRow,"pk_auto_id");
					}else
					{
    					var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_impact_level");
						var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_urgency_level");    
						var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIRow,"pk_auto_id");
					}
					
				}else{
					//is a app.service found with sla
   					var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_impact_level");
					var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_urgency_level"); 
					var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIRow,"pk_auto_id");
				}
				
			}else{
    			var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_impact_level");
				var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_urgency_level");    
				var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIRow,"pk_auto_id");
			}
		}
		else
		{
			if(_swdoc.EF().sl_configitems.rowCount()==0)
			{
				boolIsService = true;
				//-- no slas defined for CI/s so try base it on assessment levels of first item
    			var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"fk_impact_level");
				var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"fk_urgency_level"); 
				var intSLD =app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"fk_sld"); 
				var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"pk_auto_id");
			}else
			{
				//-- no slas defined for CI/s so try base it on assessment levels of first item
    			var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,0,"fk_impact_level");
				var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,0,"fk_urgency_level"); 
				var intSLD =app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,0,"fk_sld"); 
				var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,0,"pk_auto_id");
			}				
			if(intSLD>0)
				strUseSLA = app.itsm.get_sla_from_slad_matrix(intSLD, strImpact, strUrgency);
		}
		
		if(strUrgency=="")strUrgency=_swdoc.strDefaultUrgency;
 		if(strImpact=="")strImpact=_swdoc.strDefaultImpact;

		_swdoc.OC().itsm_urgency_level = strUrgency;				
		_swdoc.OC().itsm_impact_level = strImpact;	
		
		if(intSLD!="")
		{
			if(intSLD>0)
			{	
				var oRec = app.g.get_record("itsmsp_slad", intSLD);	
				if(oRec)
				{
					//-- F0108327
					_swdoc.handle_set_sladef(oRec.slad_id);
					_swdoc.handle_set_contract(intSLD);	//-- Set Contract Based on Selected SLA
					var boolTemp = _swdoc.boolIncSLASetManually;
					_swdoc.boolIncSLASetManually = true;
					if(strUseSLA!="") 
					_swdoc.handle_set_priority(strUseSLA);
					_swdoc.boolIncSLASetManually = boolTemp;
					//--END
				}
			}
		}
		else
		{		
			if(intSLD>0)
			{	
				var oRec = app.g.get_record("itsmsp_slad", intSLD);	
				if(oRec)
				{
					//-- F0108327
					_swdoc.handle_set_sladef(oRec.slad_id);
					_swdoc.handle_set_contract(intSLD);	//-- Set Contract Based on Selected SLA
					var boolTemp = _swdoc.boolIncSLASetManually;
					_swdoc.boolIncSLASetManually = true;
					if(strUseSLA!="") 
					_swdoc.handle_set_priority(strUseSLA);
					_swdoc.boolIncSLASetManually = boolTemp;
					//--END
				}
			}
		}

		if(_swdoc.OC().itsm_sladef>0)
		{
			app._een(_swdoc.MF().lbl_oc_priority, true);
			app._een(_swdoc.MF().lb_oc_sla, true);
		}		
		
		//-- get and set profile code if it has'nt been set manually by the user
		if(boolIsService)
		{
			var intCIid = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"pk_auto_id");
			var strCode = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_profilecode");		
		}else
		{
			var intCIid = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"pk_auto_id");
			var strCode = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_profilecode");
		}
		
		if(_swdoc.intProfileCodeCI==-1)
		{
			//-- was manually set so ask if want to change it - but only if one to use is not blank
			
			if((strCode!="")&&(strCode!=_swdoc.OC().probcode)&&(confirm("The recommended profile code to use for this incident differs from that of the one you selected. Do you want to change it?")) )
			{
				_swdoc.OC().probcode = strCode;
				_swdoc.intProfileCodeCI = intCIid;
			}
		}
		else 
		{
			//MessageBox("strCode : "+strCode);
			if(strCode!=_swdoc.OC().probcode)
			{
				_swdoc.OC().probcode = strCode;
				if(strCode=="")
				{
					mainform.SetTimer(2, 100)
					//MF().tb_probcode.text="";
				}
			}
			//MessageBox("intProfileCodeCI : "+intCIid);
			_swdoc.UpdateFormFromData();		
 			_swdoc.intProfileCodeCI = intCIid;			
		}
   }
   else
   {
   		var ciKey = -1;
   }
	_swdoc.process_profilecode_checks();
	_swdoc.UpdateFormFromData();		
     
    return ciKey;

}


//-- 22.08.2008 - log new problem and / or rfc
function onlog_raise_probrfc()
{
	return true;
	_swdoc.OC().callref = _swdoc.loggedCallref;
	var iOption = _swdoc.MF().cb_raise.text
	var iProblem = 1;
	var iRFC = 2;
	var iBoth = 3;

	
	if((iOption==iProblem)||(iOption==iBoth))
	{
		//-- store info in global vars
		app.g.LOG_FROM_CALL = _swdoc.opencall;
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].lastupdatetxt = updatedb.updatetxt;
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = updatedb.updatetxt;		

	
		//-- open new problem record
		app.global.LogNewCall("Problem");
    }
	
	if((iOption==iRFC)||(iOption==iBoth))
	{	
		//-- store info in global vars again as may have been reset 
		app.g.LOG_FROM_CALL = _swdoc.opencall;
		app.g.CALLINFO[_swdoc.OC().callref] = new Object();
		app.g.CALLINFO[_swdoc.OC().callref].lastupdatetxt = updatedb.updatetxt;
		app.g.CALLINFO[_swdoc.OC().callref].origupdatetxt = updatedb.updatetxt;		
	
		//-- open new problem record
		app.global.LogNewCall("Change Request");
    }
}

function set_preferred_assignment(boolFromLogAndResolve)
{
    //-- 10.06.2004
    //-- NWJ
    //-- if just logging a call if we have 
    //-- a support group assign to them
    if (_swdoc.OC().itsm_prefered_suppgroup != "")
    {
    	//-- see if we have a matching analyst (if not try the suppo groups)   	
    	var anAnalyst = app.g.get_analyst_detail(_swdoc.OC().itsm_prefered_suppgroup);
    	if(anAnalyst)
    	{
	        _swdoc.OC().suppgroup = anAnalyst.supportgroup;
    	    _swdoc.OC().owner = anAnalyst.id;
    	}
    	else
    	{
    		//-- analyst not found so check if we are assigning to a group
			var aGroup = app.g.get_group_detail(_swdoc.OC().itsm_prefered_suppgroup);    	
			if (aGroup)
			{
	        	_swdoc.OC().suppgroup = _swdoc.OC().itsm_prefered_suppgroup;
    	    }
		}
		
        if ((!boolFromLogAndResolve)&&(_swdoc.OC().owner != ""))
        {
            //-- set call to unaccepted
            //-- when assigned to an analyst
            _swdoc.OC().status = 3;
        }
    }
}

function load_company_cis(strCompanyID)
{
	//###20120717 rewrite to cope with multiple organisations
	if(""==strCompanyID)
	{
		app._slf(_swdoc.MF().sl_cust_cis,"pk_auto_id=-1");
		return;
	} 
	if(typeof _swdoc.multi_companyCIIDS[strCompanyID] === "undefined")
		_swdoc.get_company_cis(strCompanyID);
		
	// DTH: webclient - add the following from load_company_services
	var userCIS = _swdoc.multi_companyCIIDS[strCompanyID];
	var serviceKeys = app.service.get_organisation_services(strCompanyID,true);
	//###20120607 - filtering out unwanted services
	if(_swdoc.boolServiceCIBehaviourOnIncident) serviceKeys = _swdoc.filter_out_non_display_services(serviceKeys);

	if(serviceKeys=="")
	{
		userCIS = userCIS;
	}
	else
	{
		userCIS = serviceKeys + "," + userCIS;
	}
	_swdoc.update_ci_list(userCIS);
}



function get_company_cis(strCompanyID)
{
	//###20120717 - rewrite to accommodate multiple organisations
	var strKeyValues = app.cfg.get_company_cikeys(strCompanyID,true,true);	
	var strParams = "fk_company_id="+app.pfu(strCompanyID);
    var oRS = app.g.get_sqrecordset("general/get_company_sites",strParams);
	while(oRS.Fetch())
	{
		if(strKeyValues!="")strKeyValues+=",";	
		var strSiteName = app.g.get_field(oRS,"site_name");
	    strKeyValues+= app.cmdb.get_site_cis(strSiteName);
	}	
	
	if(strKeyValues=="")
		strKeyValues = "-1";
	_swdoc.multi_companyCIIDS[strCompanyID] = strKeyValues;

}

function check_cis()
{
	//-- F0100436 - update _swdoc.check_cis function
	if(_swdoc.EF().sl_configitems.rowCount() =="")
		return;
	var intStartCount = _swdoc.EF().sl_configitems.rowCount();
	var strCIKeys = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems, 'pk_auto_id', ",", "");

	var strAssociatedCIKeys = "-1";
	var strParams = "fk_children="+(strCIKeys)+"&fk_parent="+app.pfu(_swdoc.oCompany().fk_cmdb_id);
    var oRS = app.g.get_sqrecordset("call/incident/cisrelatedtocompany",strParams);
	while(oRS.Fetch())
	{
		strAssociatedCIKeys+=",";	
		strAssociatedCIKeys += app.g.get_field(oRS,"FK_CHILD_ID");
	}	
	
	var strParams = "pk_cis="+(strCIKeys)+"&fk_company_id="+app.pfu(_swdoc.oCompany().pk_company_id);
    var oRS = app.g.get_sqrecordset("call/incident/cisownedbycompany",strParams);
	while(oRS.Fetch())
	{
		strAssociatedCIKeys+=",";	
		strAssociatedCIKeys += app.g.get_field(oRS,"PK_AUTO_ID");
	}	

	var strParams = "fk_company_id="+app.pfu(_swdoc.oCompany().pk_company_id);
    var oSites = app.g.get_sqrecordset("general/get_company_sites",strParams);
	while(oSites.Fetch())
	{
		var strSiteName = app.g.get_field(oSites,"site_name");

		var strParams = "pk_cis="+(strCIKeys)+"&fk_site="+app.pfu(strSiteName);
	    var oRS = app.g.get_sqrecordset("call/incident/cisownedbysites",strParams);
		while(oRS.Fetch())
		{
			strAssociatedCIKeys+=",";	
			strAssociatedCIKeys += app.g.get_field(oRS,"PK_AUTO_ID");
		}	
	}	

	app._slf(_swdoc.EF().sl_configitems, "_kv="+strAssociatedCIKeys);
	var intEndCount = _swdoc.EF().sl_configitems.rowCount();
	if(intEndCount<intStartCount)
		MessageBox("CIs were removed from this call, as they were not associated with the currently selected Organisation");
	return;
}

var boolMI = false;
function hideshow_majorincstab()
{
	var tTab = _swdoc.EF().TabRel.tab;
	var currMI = (_swdoc.OC().itsm_majorinc=="1");
	_swdoc.EF().TabRel.ShowTabItem(8,currMI);
	app._etab(_swdoc.EF().TabRel, tTab);
	_swdoc.boolMI = currMI;
}

//ITSM MEROPE

//-- load sld info and settings
function handle_set_sladef(strNewSLD, boolValidate, boolOnResolve)
{
	if(_swdoc.boolSLAEnforced)
	{
		_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
		_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
	//	_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
	//	_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
		_swdoc.UpdateFormFromData();
		MessageBox("SLA is being enforced from the process.");
		return;
	}

	if(boolValidate==undefined)
		boolValidate==false;
	var strDisplaySLD = strNewSLD;

	var strUseSLD = strNewSLD;
	var boolRelative = false;
	var strRelative = "";

	//-- get actual SLD ID if getting from related record
	switch(strNewSLD)
	{
		case "[Use Profile SLA]":
			if(_swdoc.OC().probcode!="")
			{
				var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
				var oRecSLD = app.g.get_record("itsmsp_slad", oRec.itsm_sla);
				if(oRecSLD)
				{
					strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
					boolRelative = true;
					strRelative = "[Use Profile Priority]";
					//return false;
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Profile SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No profile has been set for this request. The Profile SLA cannot be used.",16);
				return false;
			}
			break;
		case "[Use Charge Centre SLA]":
			if(_swdoc.OC().costcenter!="")
			{
				var strCCUse = _swdoc.OC().costcenter;
				if(_swdoc.OC().costcenter=="[Use Customer Charge Centre]")
				{
					strCCUse = _swdoc.oCust().costcenter;
				}
				if(strCCUse=="")
				{
					_swdoc.OC().itsm_sladef = 0;
					_swdoc.OC().itsm_slaname = "[Use Charge Centre SLA]";
					_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
					_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
					_swdoc.UpdateFormFromData();
					if(boolValidate)
						MessageBox("No charge centre has been set for this request. The charge centre SLA cannot be used.");
					return false;

				}
				else
				{
                    //-- 992268 Add the below line
					_swdoc.OC().costcenter = strCCUse;
					var oRec = app.g.get_record("costcent", strCCUse);
					var oRecSLD = app.g.get_record("itsmsp_slad", oRec.itsm_sla);
					if(oRecSLD)
					{
						strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
						boolRelative = true;
						strRelative = "[Use Charge Centre Priority]";
						//return false;
					}
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Charge Centre SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No charge centre has been set for this request. The charge centre SLA cannot be used.");
				return false;
			}
			break;
		case "[Use Customer SLA]":
			if(_swdoc.oCust().keysearch=="")
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Customer SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No customer selected, cannot resolve customer SLA. Please select a valid SLA.",16);
				return false;
			}
			if(_swdoc.oCust().sld=="")
			{
				_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
				_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No Default SLA is associated to this Customer. Please select a valid SLA.",16);
				else
					MessageBox("Customer does not have a default SLA.");
				return false;
			}
			var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.oCust().sld);
			if(oRecSLD)
			{
				strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
				boolRelative = true;
				strRelative = "[Use Customer Priority]";
				//return false;
			}
		break;		
		case "[Use Process SLA]":
			if(bpm_workflow.pk_workflow_id!="")
			{
				if(bpm_workflow.fk_default_sla_new=="")
				{
					_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
					_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
					_swdoc.UpdateFormFromData();
					MessageBox("No SLA has been set for the requests Business Process. The Process SLA cannot be used.");
					return false;
				}
				else
				{
					var strSLA = bpm_workflow.fk_default_sla_name;
					if(strSLA.indexOf("[Use")>-1 && strSLA.indexOf("SLA]")>-1)
					{
						_swdoc.handle_set_sladef(bpm_workflow.fk_default_sla_name);
						return true;
					}
					else
					{
						//var oRec = app.g.get_record("costcent", strCCUse);
						var oRecSLD = app.g.get_record("itsmsp_slad", bpm_workflow.fk_default_sla_new);
						if(oRecSLD)
						{
							strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
							boolRelative = true;
							strRelative = "[Use Process Priority]";
							//return false;
						}
					}
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Process SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No process has been set for this request. The Process SLA cannot be used.");
				return false;
			}
			break;	
		}

  	var oRecSLD = app.g.get_sqrecordset("general/get_itsm_sla","kv="+strUseSLD);
	if(oRecSLD.Fetch())
	{
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD,"chargeto_cc");
		switch(strNewCC)
		{
			case "[Use Department Charge Centre]":
				strNewCC = _swdoc.oCust().department;
				break;
			case "[Use Customer Charge Centre]":
				strNewCC = _swdoc.oCust().costcenter;
				break;		
			default:
				break;
		}
		//-- eof cc option
			
		//-- set oc values
		if(strNewCC!="" && strNewCC!="False") _swdoc.OC().costcenter = strNewCC;
		_swdoc.OC().itsm_sladef = 	app.g.get_field(oRecSLD,"pk_slad_id");
		_swdoc.OC().itsm_slaname = app.g.get_field(oRecSLD,"slad_id");
		_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
		_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
		
		var strFKssla = app.g.get_field(oRecSLD,"fk_ssla");
		if(boolRelative)
			_swdoc.handle_set_priority(strRelative);
		else
			_swdoc.handle_set_priority(strFKssla);
		
		//-- check if customer dept has nay alternative settings
		_swdoc.check_customer_department_slad(strNewSLD,_swdoc.oCust().department);
		
		app._een(_swdoc.MF().lbl_oc_priority, true);
		app._een(_swdoc.MF().lb_oc_sla, true);
		_swdoc.UpdateFormFromData();
	}
	else
	{
		MessageBox("Failed to retrieve service level definition record for '" + strDisplaySLD + "'. Please contact your Supportworks Administrator");
		return false;
	}
	return true;
}

//-- check if there is alternative slad settings based on the customers department
function check_customer_department_slad(strSLADID, strDepartment)
{
	if(strDepartment=="")
		return;
 	var strParams = "fk_slad_id=" +app.pfu(strSLADID)+ "&fk_dept=" +app.pfu(strDepartment);
 	var oRecSLD = app.g.get_sqrecordset("call/incident/get_dept_sla",strParams);
	if(oRecSLD.Fetch())
	{
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD,"chargeto_cc");
		switch(strNewCC)
		{
			case "[Use Department Charge Centre]":
				strNewCC = _swdoc.oCust().department;
				break;
			case "[Use Customer Charge Centre]":
				strNewCC = _swdoc.oCust().costcenter;
				break;		
			default:
				break;
		}
		//-- eof cc option
			
		//-- set oc values
		if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
		_swdoc.OC().priority = app.g.get_field(oRecSLD,"fk_sla");
	}
}


function load_site_cis(strSiteID)
{
	//###20120717 rewrite to cope with multiple sites
	if(""==strSiteID)
	{
		app._slf(_swdoc.MF().sl_cust_cis,"pk_auto_id=-1");
		return
	} 

	if(typeof _swdoc.multi_siteCIIDS[strSiteID] === "undefined")
		_swdoc.get_site_cis(strSiteID);
		
	// DTH: webclient - add the following from load_site_services
	var userCIS = _swdoc.multi_siteCIIDS[strSiteID];

	var serviceKeys = app.service.get_site_services(strSiteID,true);
	//###20120607 - filtering out unwanted services
	if(_swdoc.boolServiceCIBehaviourOnIncident) serviceKeys = _swdoc.filter_out_non_display_services(serviceKeys);

	if(serviceKeys=="")
		serviceKeys = "-1";
	userCIS = serviceKeys + "," + userCIS;
	
	_swdoc.update_ci_list(userCIS);
}

function get_customer_cis(strCustID)
{
	//###20120719 - rewrite to cope with change of customer
	var strKeyValues = app.cmdb.get_me_cis(strCustID, "CUSTOMER","");
	
	if(strKeyValues=="")
		strKeyValues = "-1";
	_swdoc.multi_customerCIIDS[strCustID] = strKeyValues;
}

function get_site_cis(strSiteID)
{
	//###20120717 - rewrite to cope with multiple sites (and organisations)
	var strKeyValues = app.cmdb.get_site_cis(strSiteID);	
	
	if(strKeyValues=="")
		strKeyValues = "-1";
	_swdoc.multi_siteCIIDS[strSiteID] = strKeyValues;

}

function handle_set_priority(strValue)
{
	if(strValue=="[Use SLA Default Priority]")
	{
		var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.OC().itsm_sladef);
		if(oRecSLD)
		{	
			_swdoc.OC().priority = oRecSLD.fk_ssla;
			_swdoc.strCurrentPriority = _swdoc.OC().priority;
			_swdoc.UpdateFormFromData();
		}
		return false;
	}	
	else if(strValue=="[Use Customer Priority]")
	{
		if(_swdoc.oCust().keysearch!="")
		{
			if(_swdoc.oCust().sld==_swdoc.OC().itsm_sladef)
			{
				if(_swdoc.oCust().priority=="[Use SLA Default Priority]")
					_swdoc.handle_set_priority("[Use SLA Default Priority]");
				else
				{
					_swdoc.OC().priority = _swdoc.oCust().priority;
					_swdoc.UpdateFormFromData();
				}
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The customer SLA is not the same as the currently selected SLA. The Customer priority cannot be used.");
			}
		}
		else
		{
				if(_swdoc.OC().itsm_slaname=="[Use Customer SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("No customer selected. The Customer priority cannot be used.");
				}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Profile Priority]")
	{
		if(_swdoc.OC().probcode!="")
		{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Profile SLA is not the same as the currently selected SLA. The Profile priority cannot be used.");
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Profile SLA]")
			{
				_swdoc.OC().priority="[Use Profile Priority]";
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No profile has been set for this request. The Profile priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Charge Centre Priority]")
	{
		if(_swdoc.OC().costcenter!="")
		{
			var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.priority);
			}
			else
			{
				if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("The cost center SLA is not the same as the currently selected SLA. The cost center priority cannot be used.");
				}
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
			{
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
				_swdoc.OC().priority = strValue;
				_swdoc.strCurrentPriority=_swdoc.OC().priority;
				_swdoc.UpdateFormFromData();
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No cost center has been set for this request. The cost center priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Process Priority]")
	{
		if(bpm_workflow.pk_workflow_id!="")
		{
			//var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(bpm_workflow.fk_default_sla_new==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(bpm_workflow.fk_default_sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Process SLA is not the same as the currently selected SLA. The Process priority cannot be used.");
			}
		}
		else
		{
			_swdoc.OC().priority = _swdoc.strCurrentPriority;
			_swdoc.UpdateFormFromData();
			MessageBox("No process has been set for this request. The process priority cannot be used.");
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else
	{
		if (_swdoc.boolIncSLASetManually)
		{
			_swdoc.OC().priority = strValue;
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		_swdoc.UpdateFormFromData();
		return false;
	}
}

function handle_set_contract(strNewSLD)
{
//###20120726	var strDefaultContract = app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract, strNewSLD);

	var strDefaultContract = app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract, strNewSLD);
	if((typeof strDefaultContract !== "undefined") && strDefaultContract!="")
	{
		_swdoc.OC().fk_contract_id = strDefaultContract;
		app.g.strLogAndResolveContract = strDefaultContract;
	} else {
		//###20120727
		app._eva(_swdoc.MF().lb_contract , "");
		app._ete(_swdoc.MF().lb_contract , "");
		_swdoc.OC().fk_contract_id = "";
		//_slf(_swdoc.MF().lb_contract,"_swc_fk_company_id=" + _swdoc.OC().fk_company_id);
	}
	_swdoc.UpdateFormFromData();
		
}

function get_contract_cis()
{
	//###20120717 - rewrite to allow for multiple organisations
	var strKeyValues = app.g.get_contract_cikeys(_swdoc.OC().fk_contract_id,true,true);	

	if(strKeyValues=="")
		strKeyValues = "-1";
	_swdoc.multi_contractCIIDS[_swdoc.OC().fk_contract_id] = strKeyValues;

}

function load_contract_cis()
{
	//###20120717 rewrite to deal with multiple organisations
	if(_swdoc.OC().fk_contract_id=="")
	{
		app._slf(_swdoc.MF().sl_cust_cis,"pk_auto_id=-1");
		return
	} 
	
	if(typeof _swdoc.multi_contractCIIDS[_swdoc.OC().fk_contract_id] === "undefined")
		_swdoc.get_contract_cis();
			
	// DTH: webclient - added the following from load_contract_services
	var userCIS = _swdoc.multi_contractCIIDS[_swdoc.OC().fk_contract_id];
	var serviceKeys = app.cfg.get_contract_cikeys(_swdoc.OC().fk_contract_id,true);
	
	//###20120607 - filtering out unwanted services
	if(_swdoc.boolServiceCIBehaviourOnIncident) serviceKeys = _swdoc.filter_out_non_display_services(serviceKeys);

	if(serviceKeys=="")
		serviceKeys = "-1";
	userCIS = serviceKeys + "," + userCIS;
	
	_swdoc.update_ci_list(userCIS);
}


function update_ci_list(strCIIDs)
{

	if(typeof strCIIDs === "undefined"){
			app._slf(_swdoc.MF().sl_cust_cis,"pk_auto_id=-1");
			return;
	}

	if (strCIIDs != "") // don't apply any filters if there are no CIs, to prevent a SQL error message
	{
		switch (_swdoc.MF().fld_ci_filter.value)
		{
			case "Services and Items":
				app._slf(_swdoc.MF().sl_cust_cis, "pk_auto_id=" + strCIIDs + "&type=1");
				break;
			
			case "Services Only":
				app._slf(_swdoc.MF().sl_cust_cis, "pk_auto_id=" + strCIIDs + "&type=2");
				break;
			
			case "Items Only":
				app._slf(_swdoc.MF().sl_cust_cis, "pk_auto_id=" + strCIIDs + "&type=3");
				break;
		}
	}
	_swdoc.MF().sl_cust_cis.SetRowSelected(0);				
	_swdoc.MF().sl_cust_cis.Refresh();
	_swdoc.uncheckset_customer_items();
}

						OnFormLoading(strFormName, strCallclass)
{
	//-- can they raise an this type of call
	if(app.itsm.can_create(strCallclass,true)==false) return false;

	return true;
}

						OnFormClosing()
{
	//-- if logged see if we need to raise anything
	if(_swdoc.boolLogged)
	{
		_swdoc.onlog_raise_probrfc();
	}

	app.g.strLogAndResolveContract = "";
	app.g.strLogAndResolveCompany = "";
	
	app.strRAKbDocFilter="";
	
	return true;
}

						OnLogAndResolveCall()
{
	//-- set preferred assignment - if in helpdesk context means gets assigned to prefered group.
	if(app.session.currentGroupId=="")
	{
		_swdoc.set_preferred_assignment(true);
	}
	
	//-- nwj - hide confirmation
	app.g.bDoNotShowLogCallConfirmation = true;
	app.g.bPromptToOpenCallDetails = false;
	
	return true;
}

						OnValidate()
{
	//-- app.itsm 2.4.0 - check if need mandatory ci
	if(bpm_workflow.flg_requireci==1)
	{
		if(_swdoc.EF().sl_configitems.rowCount() < 1)
		{
			if(_swdoc.EF().sl_services.rowCount() < 1)
			{
				MessageBox("Before logging this Incident request you have to associate at least one affected configuration item or service");
				return VALIDATE_FAIL;
			}
		}
	}
	//-- 108840 : Check if we have required number of CIs 
	var strCallType = "Incident";
	var intCurCINo = _swdoc.EF().sl_configitems.rowCount() + _swdoc.EF().sl_services.rowCount();
	if(!app.itsm.mandatory_ci_check(strCallType, intCurCINo))
	{
		var intReqCINo = app.oSettings.get_setting("int" + strCallType + "OnLoggingRequiredCINo");
		var strPlural = "";
		if (intReqCINo > 1)
		{
			strPlural= "s";
		}
		MessageBox("Before logging this " + strCallType + " request you have to associate at least " + intReqCINo + " affected configuration item" + strPlural + " or service" + strPlural + ".");
		return VALIDATE_FAIL;
	}
	return VALIDATE_DEFAULT;
}

						OnResolveRecord(strTable, strColumn, strValue)
{
 	//-- resolving the customer record
 	if(IsObjectDefined("mainform") && strTable == 'userdb' && _swdoc.boolResolvingCust == false && strValue!="")
 	{
 		var strWhere = "";
		if ("companyname" == strColumn && "" != strValue){
			_swdoc.ResolveRecord("company","companyname",strValue);
 			_swdoc.strPreSelectedCompany = strValue;
		}

  		if(strColumn =="firstname!surname")
  		{
  			//-- if using customer name search on firstname and lastname
  			//-- 2.4.1 bug fix 79046
   			arrNames = strValue.split(" ");
   			if(arrNames[1]==undefined)
   			{
      			var strParams = "_swc_firstname="+arrNames[0];
      			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   				var strSearch = "firstname="+arrNames[0];
    		}
   			else
   			{
    			var strLastName="";
    			for(var y=1;y<arrNames.length;y++)
    			{
     				if(y>1)strLastName = strLastName+" ";
     				strLastName = strLastName+arrNames[y];
    			}
      			var strParams =  "_swc_firstname="+arrNames[0]+"&_swc_surname="+strLastName;
      			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   				var strSearch = "firstname="+arrNames[0]+"&surname="+strLastName;
   			}
  		}
  		else
  		{
       		var strParams =  "_swc_"+strColumn+"="+strValue;
   			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   			var strSearch = strColumn+"="+strValue;
   			//-- START 107620
   			strSearch = strSearch.replace("%","% ");
			//-- END 107620
   		}
		if(intCount!=1)
		{
			_swdoc.strSearchFor = strSearch;
  			mainform.SetTimer(1, 100);
  			_swdoc.boolResolvingCust = true;
  			return "";
 		}
 	}
 	
	//-- resolving org record 	
 	if(IsObjectDefined("mainform") && strTable == 'company' && strValue =="")
 	{
  		//2.4 clear down list if user is being unselected
  		app.g.strLogAndResolveCompany = "";
  		_swdoc.ResetRecord('userdb');
 	} 
 	
 	if(IsObjectDefined("mainform") && strValue=="" && (strTable=="userdb"))
 	{
 		//MessageBox('Setting Timer');
  		mainform.SetTimer(3, 100);
 	}
	//-- resolving org record 	
 	if(IsObjectDefined("mainform") && strTable == 'bpm_workflow' && strValue =="")
 	{
 		app._een(mainform.lb_oc_slad1,true);
		app._een(mainform.lbl_oc_sladef,true);
		app.g.reset_bpm_form_attribute_check(_swdoc);
	} 

 	return strValue;
}

						OnPreValidate()
{
	var funcFinalValidation = function()
	{
		if (!app.g.resolve_default_priority(_swdoc.OC().priority,_swdoc,"Incident"))
		{
			return VALIDATE_FAIL;
		}
		return VALIDATE_DEFAULT;
	}
	
	//--F0110887 Start
	var intExpiryEpoch = app.g.fix_epoch(_swdoc.oCompany().supportexpiryx);
	var boolOrgSupported = new Boolean(app.g.checkOrgSupport(_swdoc.oCust().companyname, intExpiryEpoch, false));
	if (boolOrgSupported==false)
	{
		 MessageBox("You cannot log a call for this customer as the '" + _swdoc.oCust().companyname + "' organisation is no longer supported.");
		 return VALIDATE_FAIL;
	}
	//--F0110887 End
	
	var boolResolve = false;
	if(	_swdoc.OC().itsm_slaname=="[Use Customer SLA]"||_swdoc.OC().itsm_slaname=="[Use Profile SLA]" || _swdoc.OC().itsm_slaname=="[Use Workflow SLA]" || _swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
		boolResolve = true;
	if(boolResolve)
	{
		var boolOK = _swdoc.handle_set_sladef(_swdoc.OC().itsm_slaname,true);
		if(!boolOK)
			return VALIDATE_FAIL;
	}
	
	//###20120802 check cc, dept, site in org?
	var iCaptureMistakes = 0;
	var intMistakeCounter = 0;
	var tDept; // needs to remain outside as department name is NOT the fk_dept_code but dept_name
	if ("" !== _swdoc.OC().site){
		tSite = app.g.get_record("site", _swdoc.OC().site);
		if (tSite.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 1;
			intMistakeCounter++;
		}
	}
	if ("" !== _swdoc.OC().costcenter){
		var tCC = app.g.get_record("costcent", _swdoc.OC().costcenter);
		if (tCC.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 2;
			intMistakeCounter++;
		}
	}
	if ("" !== _swdoc.OC().fk_dept_code){
		tDept = app.g.get_record("department", _swdoc.OC().fk_dept_code);
		if (tDept.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 4;
			intMistakeCounter++;
		}
	}
	if (iCaptureMistakes > 0)
	{
		var strDetectedMistakes = "";
		var intDealtWithMistakes = 0;
		if ( 1 == (iCaptureMistakes % 2) ) {
			strDetectedMistakes += "site (" + _swdoc.OC().site + ")";
			intDealtWithMistakes++;
		}
		if ( 1 == (Math.floor(iCaptureMistakes / 2) % 2) ) {
			strDetectedMistakes += ((0==intDealtWithMistakes)?"":((2==intMistakeCounter)?" and ":", ")) + "charge centre (" + _swdoc.OC().costcenter + ")";
			intDealtWithMistakes++;
		}
		if ( 1 == (Math.floor(iCaptureMistakes / 4) % 2) ) {
			if(!tDept.dept_name)
			{
				//-- F0108424
				var oCust2 = app.g.get_record("userdb", _swdoc.OC().cust_id);
				strDetectedMistakes += ((0==intDealtWithMistakes)?"":" and ") + "department ("+oCust2.department+") does not exist and";
			}else
			{
				strDetectedMistakes += ((0==intDealtWithMistakes)?"":" and ") + "department (" + tDept.dept_name + ")";
			}
			//-- F0108424

		}
		MessageBox("The " + strDetectedMistakes + " " + ((1==intMistakeCounter)?"is":"are") + " NOT part of " + _swdoc.OC().companyname + ".\n\nSelect 'OK' to proceed with the current data.", MB_OKCANCEL | MB_ICONEXCLAMATION, function(intResponse)
		{
			if (2==intResponse)
			{
				return VALIDATE_FAIL;
			}
			else
			{
				return funcFinalValidation();
			}
		});
		
	}
	else
	{
		return funcFinalValidation();
	}		
}

