						OnFormLoaded()
{
	//-- F0105032
	if(_swdoc._form.PreloadType==2)
		_swdoc.oOpencall = app.g.get_sys_record("opencall",_swdoc.OC().callref)
	//-- EOF F0105032

	//--
	//-- if logging ofr a customer
	if(app.g.LOG_CALL_FOR_USER!="")
	{
		_swdoc.ResolveRecord("userdb", "keysearch", app.g.LOG_CALL_FOR_USER);
		app.g.LOG_CALL_FOR_USER="";
	}
	else if(app.g.LOG_FOR_ORGID!="")
	{
		_swdoc.ResolveRecord("company", "pk_company_id", app.g.LOG_FOR_ORGID);	
		app.g.LOG_FOR_ORGID="";
	}
	else if(app.g.LOG_CALL_FOR_CI!="")
	{
		app.g.sl_appendfilter(_swdoc.EF().sl_configitems, app.g.LOG_CALL_FOR_CI,'pk_auto_id');
		_swdoc.EF().sl_configitems.Refresh();
 		app.g.LOG_CALL_FOR_CI="";
		_swdoc.check_ci_assessment();
	}
	if(_swdoc.oCust().keysearch !="")
	{
		_swdoc.setup_customer_details();
	}
	
	app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract);
	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	
	var boolExternalCompanies =dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1;
	app._een(_swdoc.MF().lb_company, boolExternalCompanies);
	app._evi(_swdoc.MF().lb_contract, boolExternalCompanies);
	app._evi(_swdoc.MF().lbl_contract, boolExternalCompanies);
	if(!_swdoc.MF().lb_company.enable)
	{
		//-- check if we should hide
		var boolHideCompanyField = (dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1);
		app._evi(_swdoc.MF().lb_company, boolHideCompanyField);		
		//alter filter if we do not support external
		
		app._slf(_swdoc.MF().fld_userdb_site,"");
		app._slf(_swdoc.MF().fld_userdb_costcenter,"");
		app._slf(_swdoc.MF().fld_userdb_dept,"");	
		app._slf(_swdoc.MF().lb_oc_cc,"");
		app._slf(_swdoc.MF().lb_oc_site,"");
		app._slf(_swdoc.MF().fld_oc_dept,"");
				
	}
	
	//###20120717 ensure correct field for organisation is showing - workaround because distinct picklist with Display is broken
	//###20120720 addition of boolHideCompanyField - feeding on the effect of the DD setting.
	var boolCompanyFieldVisible = (_swdoc.MF().lb_company.visible);
	app._evi(_swdoc.MF().lbl_oc_company, boolCompanyFieldVisible);

	var boolCompanySet = ("" !== _swdoc.OC().fk_company_id);
	app._ema(_swdoc.MF().x_fld_org, !boolCompanySet);
	app._evi(_swdoc.MF().x_fld_org, !boolCompanySet && boolCompanyFieldVisible);
	app._een(_swdoc.MF().x_fld_org, false);
	
	app._ema(_swdoc.MF().pl_oc_org, boolCompanySet);
	app._evi(_swdoc.MF().pl_oc_org, boolCompanySet && boolCompanyFieldVisible);
	app._een(_swdoc.MF().pl_oc_org, boolCompanySet && boolExternalCompanies);
	
	app._evi(_swdoc.MF().lbl_company, boolCompanyFieldVisible);	
	//-- eof hide / enable	    	
	
	var strFilterPicklist = "by Customer";
	if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AllowSiteFilterCIsAndServices")==1)
		strFilterPicklist += (!boolExternalCompanies)?"|by Site":"|by Customer: Site|by Settings: Site";
	if(dd.GetGlobalParamAsNumber("Application Settings/Incident/AllowOrganisationFilterCIsAndServices")==1)
		strFilterPicklist += (!boolExternalCompanies)?"|by Organisation":"|by Customer: Organisation|by Settings: Organisation";
	if(boolExternalCompanies && dd.GetGlobalParamAsNumber("Application Settings/Incident/AllowContractFilterCIsAndServices")==1)
		strFilterPicklist +="|by Contract";
		
	_swdoc.MF().fld_cis.pickList = strFilterPicklist;

	//-- RJC - F0105434
	app.g.get_default_process(_swdoc,'Incident',_swdoc.MF().fld_bpm_workflow,_swdoc.MF().lbl_bpm_workflow);
	//-- EOF - F0105434
	
	//-- F0094989
	if(_swdoc.OC().itsm_slaname.indexOf("[Use ")<0 && _swdoc.OC().itsm_slaname.indexOf(" SLA]")<0 )
	{
		if(_swdoc.OC().itsm_slaname!="")
		{
			var slarec = app.g.get_sqrecordset("general/get_itsm_sla","kv="+_swdoc.OC().itsm_slaname);
			if(slarec.Fetch())
			{
				var intSLAId = app.g.get_field(slarec,"slad_id");
				if(intSLAId!="") {
					_swdoc.handle_set_contract(intSLAId);
					_swdoc.handle_set_sladef(intSLAId);					
				}	
			}
		}
	}
	_swdoc.strDefaultImpact = (_swdoc.OC().itsm_impact_level);
	_swdoc.strDefaultUrgency = (_swdoc.OC().itsm_urgency_level);
	_swdoc.strCurrentPriority = _swdoc.OC().priority;
	_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
	
	//###20120619
	_swdoc.strOrigSLAFilter = _swdoc.MF().lb_oc_slad1.filter;
	
	//ES -- START F0107205 : When logging call from email...
	if(_swdoc.OC().itsm_slaname == "[Use Customer SLA]" && _swdoc.oCust().sld_name != "")
	{
		_swdoc.MF().lb_oc_slad1_OnValueChanged("[Use Customer SLA]");
		
		//-- If priority not set...
		if (_swdoc.OC().priority == "")
		{
			//-- ...try to find priority from matrix
			_swdoc.OC().priority = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
			_swdoc.UpdateFormFromData();
		}
	}
	//-- END F0107205
	
	// -- Update Main Organisation on the form if only the Organisation is set
	if(_swdoc.oCompany().pk_company_id && _swdoc.oCust().keysearch =="")
	{
		_swdoc.ResolveRecord("userdb", "fk_company_id", _swdoc.oCompany().pk_company_id);
	}
	
	//-- F0108825 : A newly logged call should not have resolve/close text
	//	         even when it has been copied from a resloved/closed call
	_swdoc.OC().resolve_text="";
	_swdoc.OC().close_text="";	
	
	//	PM00143977 : values copied from parent call
	_swdoc.OC().lastactdatex="";	
	_swdoc.OC().lastactdate="";	
	_swdoc.OC().lastactby="";	
	_swdoc.OC().assigndatx="";	
	_swdoc.OC().withinresp="";	
	_swdoc.OC().slaresp="";	
	_swdoc.OC().assigndate="";	
	
	// -- F00109898
	_swdoc.OC().resp_time = "";
	_swdoc.OC().fix_time = "";
	//-- TK Set Summary Based on Email Subject
	if((_swdoc._form['emailsubject']) && (app.oSettings.get_setting("boolUseSubjectasSummary")=='True'))
	{
		_swdoc.OC().itsm_title = _swdoc._form['emailsubject'];
		_swdoc.boolIncTitleSetManually = true;
	} 

	//PM00132039
	//Quick Log calls - Category is not displayed on template when a CI is added to the quick log call
	if (_swdoc.OC().probcode!="")
	{
	// -- indicate that profile was not set by CI
	_swdoc.intProfileCodeCI = -1;
	}
	
	_swdoc.strDefaultPriority = _swdoc.MF().lb_oc_sla.value;
	
}

						mb_cust_actions_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;
	var iSearch=2;	
	var iMgr = 3;
	
	if(nItemID==iSearch)
	{
		var strURL = "searchtype=1&canreset=1&fk_company_id=" + _swdoc.oCompany().pk_company_id;
		app.g.search_for("customers",false,strURL,function(sResults)
		{
			if(sResults.selectedkeys=="") return;
    
    		var arrKey = sResults.selectedkeys.split(",");
    		_swdoc.ResolveRecord("userdb", "keysearch", arrKey[0]);
		});
    }
    else if(nItemID==iMgr)
    {
    	//-- popup a form 
		if (_swdoc.userdb.fk_manager != "") app.OpenFormForEdit("tfCustomer", _swdoc.userdb.fk_manager, "", true);
	}
}

						mbtn_cci_views_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_cust_cis,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_cust_cis,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}	
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						sl_cust_cis_OnItemChecked(nRow, bChecked)
{
	 _swdoc.boolIncSLASetManually = true; //-- 105734
	
	var pk_ci_id = app.g.sl_getrawcolvalue(sl_cust_cis, nRow, "pk_auto_id");	
	var fk_ci_type = app.g.sl_getrawcolvalue(sl_cust_cis, nRow, "ck_config_type");

	var oList = _swdoc.EF().sl_configitems;
	
	var itemType = "Configuration Item";

	if(fk_ci_type.indexOf("Service")>-1)
	{
		oList = _swdoc.EF().sl_services;
		var itemType = "Service";
	}

	//MessageBox(pk_ci_id + " A:A " + nRow)
	if(bChecked)
	{
		if(app.g.sl_findrow_byvalue(oList, pk_ci_id, 'pk_auto_id') > -1) return;

		//MessageBox(pk_ci_id)
		//-- TK Replace append Filter with Stored Query	
		//app.g.sl_appendfilter(oList, pk_ci_id,'pk_auto_id');
		if (itemType == "Configuration Item")
		{
			app.g.sl_kv_appendfilter(_swdoc.EF().sl_configitems, pk_ci_id,'pk_auto_id');
			_swdoc.EF().sl_configitems.Refresh();
			//ES - F0111753 - set Process based on value set in Configuration Item and Configuration Type
			var intCIID = app.g.sl_getrawcolvalue(sl_cust_cis, nRow, "pk_auto_id");
			var dbValue1 = new SqlQuery;
			dbValue1.InvokeStoredQuery("cmdb/get.itemi_fkworkflowid","pk_auto_id="+intCIID);
			if(dbValue1.Fetch())
			{
				var strWorkflowId = app.g.get_field(dbValue1,"fk_workflow_id");
				//Get fk_workflow_id from config_itemi if not blank
				if (strWorkflowId!="")
				{
					if (_swdoc.bpm_workflow.pk_workflow_id=="")
					{
						_swdoc.bpm_workflow.pk_workflow_id = strWorkflowId;
						_swdoc.UpdateFormFromData();
					}
					else if (_swdoc.bpm_workflow.pk_workflow_id!="")
					{
						if (_swdoc.bpm_workflow.pk_workflow_id!=strWorkflowId)
						{
							var msgProcess = MessageBox("The default Incident Process for the selected item is [" + arrWorkflowId[y] + "]. Would you like to apply this change?",MB_YESNO|MB_ICONQUESTION);
 							if (msgProcess==6)
							{
								_swdoc.bpm_workflow.pk_workflow_id = strWorkflowId;
								_swdoc.UpdateFormFromData();
							}
						}
					}
				}	
				//Get fk_workflow_id from config_typei if blank in config_itemi		
				else if (strWorkflowId=="")
				{
					var dbValue2 = new SqlQuery;
					dbValue2.InvokeStoredQuery("cmdb/get.typei_fkworkflowid","pk_auto_id="+intCIID);
					if (dbValue2.Fetch())
					{
						var strTypeWorkflowId = app.g.get_field(dbValue2,"fk_workflow_id");
						if (strTypeWorkflowId!="")
						{
							if (_swdoc.bpm_workflow.pk_workflow_id=="")
							{
								_swdoc.bpm_workflow.pk_workflow_id = strTypeWorkflowId;
								_swdoc.UpdateFormFromData();
							}
							else if (_swdoc.bpm_workflow.pk_workflow_id!="")
							{
								var msgProcessType = MessageBox("The default Incident Process for the selected item is [" + arrWorkflowId[y] + "]. Would you like to apply this change?",MB_YESNO|MB_ICONQUESTION);
 								if (msgProcessType==6)
								{
									_swdoc.bpm_workflow.pk_workflow_id = strTypeWorkflowId;
									_swdoc.UpdateFormFromData();
								}
							}
						}
					}
				} //END - F0111753
			}	
		}else if (itemType == "Service")
		{
  			app.g.sl_kv_appendfilter(_swdoc.EF().sl_services, pk_ci_id,'pk_auto_id');
  			_swdoc.EF().sl_services.Refresh();
  			
  			//ES - F0111753 - set Process based on value set in Service
			var intCIID = app.g.sl_getrawcolvalue(sl_cust_cis, nRow, "pk_auto_id");
			var dbValue3 = new SqlQuery;
			dbValue3.InvokeStoredQuery("cmdb/get.itemi_fkworkflowid","pk_auto_id="+intCIID);
			if(dbValue3.Fetch())
			{
				var strServiceWorkflowId = app.g.get_field(dbValue3,"fk_workflow_id");
				//Get fk_workflow_id from config_itemi for app.service item
				if (strServiceWorkflowId!="")
				{
					if (_swdoc.bpm_workflow.pk_workflow_id=="")
					{
						_swdoc.bpm_workflow.pk_workflow_id = strServiceWorkflowId;
						_swdoc.UpdateFormFromData();
					}
					else if (_swdoc.bpm_workflow.pk_workflow_id!="")
					{
						if (_swdoc.bpm_workflow.pk_workflow_id!=strServiceWorkflowId)
						{
							var msgProcessService = MessageBox("The selected item recommends the Process [" + strServiceWorkflowId + "] for this Incident. Would you like to apply this change?",MB_YESNO|MB_ICONQUESTION);
 							if (msgProcessService==6)
							{
								_swdoc.bpm_workflow.pk_workflow_id = strServiceWorkflowId;
								_swdoc.UpdateFormFromData();
							}
						}
					}
				}			
			} //END - F0111753
  		}
		//-- NWJ - determine who the preferred support is
 		_swdoc.determine_prefered_support(true);	

		//-- nwj - 15.07.2008 - check ci contract
		app.cfg.process_call_contract_check(pk_ci_id, true,_swdoc);
		
		// -- SG, 02/07/2012, F0103324
		// Create new array to hold CI and Bespoke Terms info
		app.bespokeTermsProcessing(pk_ci_id);
		// -- END OF BESPOKE PROCESSING
	}
	else
	{
			var intRowPos = app.g.sl_findrow_byvalue(oList, pk_ci_id, 'pk_auto_id');
			if(intRowPos>-1)
			{
				oList.RemoveRow(intRowPos);		
		
				//-- 29.04.2008 - 2.2.0 if this ci that has been removed was used to set the pref supp group then change it
				if(_swdoc.intAssessmentCI == pk_ci_id)
				{
					_swdoc.OC().itsm_sladef = 0;
					_swdoc.OC().itsm_slaname = "[Use Customer SLA]";
					_swdoc.OC().priority = "[Use Customer Priority]";
					_swdoc.OC().itsm_prefered_suppgroup = "";
					_swdoc.OC().itsm_prefered_text = "";

					app._eva(_swdoc.MF().lb_oc_slad1, 0);
					app._ete(_swdoc.MF().lb_oc_slad1, 0);
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);

					_swdoc.UpdateFormFromData();
				}
			
				_swdoc.determine_prefered_support(true);
				//_swdoc.check_ci_assessment();
			}
	}

	app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	
}

						mbtn_profile_OnMenuItem(strName, nItemID)
{
	var iDesc = 2;
	var iProfile = 1;
	var iAPR=3;
	var iSumm=4;
	
	if(nItemID==iDesc)
	{
  		if(_swdoc.updatedb.updatetxt == "")
  		{
        	MessageBox("Please enter some descriptive text before trying to perform a Knowledge base search.");
        }
    	else
    	{   		
        	if(!app.g.use_sw_knowledgebase())
			{
				app.allocatekbdoc(_swdoc._kbidentifier,_swdoc);
        		app.g.open_third_party_kb_search(true, _swdoc.updatedb.updatetxt,"",app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,","), _swdoc._kbidentifier);
			}
			else
			{ 		
        		url = "hsl:KbSearch?search=" + _swdoc.updatedb.updatetxt;
        		app.global.OpenEmbeddedURL(url);
        	}
    	}
    }
	if(nItemID==iSumm)
	{
  		if(_swdoc.opencall.itsm_title == "")
  		{
        	MessageBox("Please enter a summary before trying to perform a Knowledge base search.");
        }
    	else
    	{   	
    		if(!app.g.use_sw_knowledgebase())
			{
				app.allocatekbdoc(_swdoc._kbidentifier,_swdoc);
        		app.g.open_third_party_kb_search(true, _swdoc.opencall.itsm_title,"",app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,","), _swdoc._kbidentifier);
        		
			}
			else
			{ 			
        		url = "hsl:KbSearch?search=" + _swdoc.opencall.itsm_title;
        		app.global.OpenEmbeddedURL(url);
        	}
    	}
    }
	else if(nItemID==iProfile)
	{
		if(_swdoc.OC().probcode == "")
  		{
        	MessageBox("Please select an incident category before trying to perform a Knowledge base search.");
        }
    	else
    	{
    		if(!app.g.use_sw_knowledgebase())
			{
				MessageBox("This option is not available with your Knowledgebase.");
			}
			else
			{ 	
        		url = "hsl:KbSearch?search=" + _swdoc.OC().probcode;
        		app.global.OpenEmbeddedURL(url);
        	}
    	}
    	return;
    }
	else if(nItemID==iAPR)
	{
		//-- Auto problem search
		var strCIKeys = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems, 'pk_auto_id', ",", "");
		var strServiceKeys = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_services, 'pk_auto_id', ",", "");
		if(strServiceKeys!="")
		{
			if(strCIKeys!="")strCIKeys+=",";
			strCIKeys+=strServiceKeys;
		}
  		var strURL = "canreset=1&matchingprobcode=" + _swdoc.OC().probcode;
  		if(strCIKeys!="")
  		{
  			strURL += "&ci_keys="+strCIKeys;
  		}
		app.g.search_for("apr",true,strURL,function(oResult)
		{
			if(oResult.selectedkeys!="")
			{		
				app.g.sl_appendfilter(_swdoc.EF().sl_problems,oResult.selectedkeys,0);
				MessageBox('The associated Problems/Known Errors can be viewed from the Associated Information tab.');
			}
		});
	}
}

						OnTimer(nTimer)
{
	// TODO: Add your event handler code here
	if(nTimer==1)
	{
		mainform.KillTimer(1);
		//###20120716
		if ("" != _swdoc.oCompany().pk_company_id){
			_swdoc.strPreSelectedCompany = _swdoc.oCompany().pk_company_id;
		}
		var strURL = "searchmode=1&multiselect=0&autorun=1&searchtype=1&canreset=1&fk_company_id=" + _swdoc.strPreSelectedCompany + "&"+_swdoc.strSearchFor;
		_swdoc.strSearchFor="";
		_swdoc.strPreSelectedCompany = "";
		app.g.popup("search_customers",strURL,function(aForm)
		{
			if (aForm)
			{
				var sResults = new Object();
				sResults.selectedkeys = aForm.document.strSelectedKeys;
				sResults.selectedpfskeys = app.g.pfs_keys(aForm.document.strSelectedKeys);
				sResults.selectedtext = aForm.document.strSelectedText;
				sResults.selectedother = aForm.document.strSelectedOther;
				sResults.selectedcmdbids = aForm.document.strSelectedCMDBIDs;
				sResults.selectedcompanyids = aForm.document.strSelectedCompIDs;
				sResults.strSearchedCompanyRedux = aForm.document.strSearchedCompanyRedux;
		
    			if(sResults.selectedkeys=="") 
    			{
 					_swdoc.boolResolvingCust = false;
 					_swdoc.load_cis();
   					return;
    			}
    			var arrKey = sResults.selectedkeys.split(",");
    			_swdoc.ResolveRecord("userdb", "keysearch", arrKey[0]);
		
				//###20120601	
    			if(""!=sResults.strSearchedCompanyRedux){
					_swdoc.multi_c_set_company(sResults.strSearchedCompanyRedux);
				}
			
				_swdoc.boolResolvingCust = false;
			}
			else
			{
				_swdoc.boolResolvingCust = false;
 				_swdoc.load_cis();
   				return;
			}
			
		});	
	}
	
	else if(nTimer==2)
	{
		mainform.KillTimer(2);
		if(_swdoc.OC().probcode=="" && _swdoc.MF().tb_probcode.text =="") 
		{
			app._ete(_swdoc.MF().tb_probcode,"");
		}
	}
	
	else if(nTimer==3)
	{
		mainform.KillTimer(3);
		_swdoc.load_cis();
		return;

	}
	else if(nTimer==4)
	{
		mainform.KillTimer(4);
		_swdoc.boolChangeProfile=false;

	}

}

