						btn_search_ci_OnPressed()
{
    //-- NWJ 08.03.2004
    //-- open CI search form in select mode


	var strURL = "canreset=1&fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id);
	app.cmdb.search_ci(true,strURL,function(sResults)
	{
		if (sResults.selectedkeys=="") return;
  		var strType = sResults.selectedtypes;
  		if(strType.indexOf("Service")>-1)
  		{
  			var strServiceKeys = "";
  			var strKeys = "";
  			var arrTypes = strType.split(",");
  			var arrKeys = sResults.selectedkeys.split(",");
  			
  			for(var x in arrKeys)
  			{
  				var strThisType = arrTypes[x];
  				if(strThisType.indexOf("Service")>-1)
  				{
  					if(strServiceKeys!="")
  						strServiceKeys+=",";
  					strServiceKeys += arrKeys[x];
  				}else
  				{
  					if(strKeys!="")
  						strKeys+=",";
  					strKeys += arrKeys[x];
  				}
  			}
  			if(strKeys!="")
  				app.g.sl_kv_appendfilter(sl_configitems, strKeys,'pk_auto_id');
  			if(strServiceKeys!="")
  				app.g.sl_kv_appendfilter(sl_services, strServiceKeys,'pk_auto_id');
  		}
  		else
  		{
  		 	// -- SG, 21/08/2012, F0103324
    		// Output Bespoke Terms for selected CI's, where appropriate
			app.bespokeTermsProcessing(sResults.selectedkeys);	
    		//END OF BESPOKE TERMS PROCESSING
    		
  			app.g.sl_kv_appendfilter(sl_configitems, sResults.selectedkeys,'pk_auto_id');
  			
  			//ES - F0111753 - set Process based on value set in Configuration Item and Configuration Type
			var dbValue1 = new SqlQuery;
			var dbValue2 = new SqlQuery;
			var arrKeys = sResults.selectedkeys.split(",");
			var arrWorkflowId = new Array();
			// Grab value of fk_workflow_id for each selected Configuration Item from config_itemi or from config_typi where itemi is blank
			for(var x in arrKeys)
  			{
				dbValue1.InvokeStoredQuery("cmdb/get.itemi_fkworkflowid","pk_auto_id="+arrKeys[x]);
				if(dbValue1.Fetch())
				{
					if (app.g.get_field(dbValue1,"fk_workflow_id")!="")
					{
						arrWorkflowId[x] = app.g.get_field(dbValue1,"fk_workflow_id");
					}
					else if (app.g.get_field(dbValue1,"fk_workflow_id")=="")
					{
						dbValue2.InvokeStoredQuery("cmdb/get.typei_fkworkflowid","pk_auto_id="+arrKeys[x]);
						if (dbValue2.Fetch())
						{
							if (app.g.get_field(dbValue2,"fk_workflow_id")!="")
							{
								arrWorkflowId[x] = app.g.get_field(dbValue2,"fk_workflow_id");
							}
						}
					}
				}
			}
			// Remove blank or undefined elements from the array
			for (var check in arrWorkflowId)
			{
				if (arrWorkflowId[check]=="undefined" || arrWorkflowId[check]=="")
				{
					var index = arrWorkflowId.indexOf(check);
					arrWorkflowId.splice(index,1);
				}
			}
			var arrayLength = arrWorkflowId.length;
			var position = 0;
			// Set Process field based on fk_workflow_id value
			for (var y in arrWorkflowId)
			{
				var intIncrement = 0;
				while (intIncrement < arrayLength)
				{
					if ((arrWorkflowId[y] == arrWorkflowId[intIncrement] && (intIncrement!=position)) || arrayLength == 1)
					{
						if (_swdoc.bpm_workflow.pk_workflow_id=="")
						{
							_swdoc.bpm_workflow.pk_workflow_id = arrWorkflowId[y];
							_swdoc.UpdateFormFromData();
						}
						else if (_swdoc.bpm_workflow.pk_workflow_id!="")
						{
							if (_swdoc.bpm_workflow.pk_workflow_id!=arrWorkflowId[y])
							{
								var msgProcess = MessageBox("The default Incident Process for the selected item is [" + arrWorkflowId[y] + "]. Would you like to apply this change?",MB_YESNO|MB_ICONQUESTION);
 								if (msgProcess==6)
								{
									_swdoc.bpm_workflow.pk_workflow_id = arrWorkflowId[y];
									_swdoc.UpdateFormFromData();
								}
							}
						}
					}
				intIncrement++;
				}
				position++;
			}//END - F0111753
  		}  
		//-- if only 1 CI use it default profile unless blank
		if(sl_configitems.rowCount()==1)
		{
			var strCode = app.g.sl_getrawcolvalue(sl_configitems,0,"fk_profilecode");
			if(strCode!="") 
			{
				_swdoc.OC().probcode = strCode;				
				_swdoc.UpdateFormFromData();		
			}
		}
	    	
		//-- NWJ - determine who the preferred support is
 		_swdoc.determine_prefered_support(true);
 		
 		//PM00132039
 		sl_configitems.Refresh();
		_swdoc.MF().sl_cust_cis.Refresh();
 		//END PM00132039
 		
 		//-- nwj - 15.07.2008 - check ci contract
		app.cfg.process_call_contract_check( sResults.selectedkeys, true,_swdoc);
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	});
}

						mbtn_ici_actions_OnMenuItem(strName, nItemID)
{
	var iRemoveCI = 1;

	//-- Remove Selected config items
	if(nItemID == iRemoveCI)
	{
   	
		//-- do this by finding unselected rows and making sure they are part of fitler
		var strFilter = "";
		for (var x=_swdoc.EF().sl_configitems.rowCount()-1;x > -1;x--)
		{
			var value = _swdoc.EF().sl_configitems.GetItemTextRaw(x,"pk_auto_id");		
			if (!_swdoc.EF().sl_configitems.IsRowSelected(x))
			{
				//-- add to filter
				if (value != "")
				{
    				if (strFilter != "") strFilter +=",";
    				strFilter +=value;
				}
			}
			else
			{
				//-- 29.04.2008 - 2.2.0 if this ci that has been removed was used to set the pref supp group then change it
				if(_swdoc.intAssessmentCI == value)
				{
					_swdoc.OC().itsm_prefered_suppgroup = "";
					_swdoc.OC().itsm_prefered_text = "";
					_swdoc.UpdateFormFromData();
				}
			}
		}
		if(strFilter!="")
			strFilter="_kv="+strFilter;

       app._slf(_swdoc.EF().sl_configitems, strFilter);  
       _swdoc.EF().sl_configitems.Refresh();
       _swdoc.EF().sl_configitems.SetRowSelected(0);
	   _swdoc.check_ci_assessment();
	   _swdoc.uncheckset_customer_items();
       return;
	}
       
}

						mbtn_ici_views_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMchildren = 2;	
	var iVCMparents = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_configitems,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_configitems,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMchildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iVCMparents)
		{
			app.cmdb.view_vcm(intCI,true);
		}

		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						mbtn_srch_prb_OnMenuItem(strName, nItemID)
{
	var iNormal = 1;
	var iAPR = 2;
	var iCIs = 3;
	
	switch(nItemID)
	{
    	case iNormal:
   			var strURL = "canreset=1";    	
    		app.itsm.search_calls("PRB", strURL,true,function(sResults)
    		{
    			if(sResults.selectedkeys=="") return;	
				app.g.sl_kv_appendfilter(_swdoc.EF().sl_problems, sResults.selectedkeys,"callref");
				_swdoc.EF().sl_problems.Refresh();
    		});
			break;
    	case iAPR:
   			app.g.search_for("apr",true,"",function(sResults)
   			{
   				if(sResults.selectedkeys=="") return;	
				app.g.sl_kv_appendfilter(_swdoc.EF().sl_problems, sResults.selectedkeys,"callref");
				_swdoc.EF().sl_problems.Refresh();
   			});
			break;			
	   	case iCIs:	
		{	
			var strCIKeys = app.g.sqllist_get_valuesdel(sl_configitems, 'pk_auto_id', ",", "");
			var strServiceKeys = app.g.sqllist_get_valuesdel(sl_services, 'pk_auto_id', ",", "");
			if(strServiceKeys!="")
			{
				if(strCIKeys!="")strCIKeys+=",";
				strCIKeys+=strServiceKeys;
			}
  			var strURL = "canreset=1";
  			if(strCIKeys!="")
  			{
  				strURL += "&ci_keys="+strCIKeys;
  			}
    		app.itsm.search_calls("PRB",strURL,true,function(oResults)
    		{
    			if(oResults.selectedkeys=="") return;	
				app.g.sl_kv_appendfilter(_swdoc.EF().sl_problems, oResults.selectedkeys,"callref");
				_swdoc.EF().sl_problems.Refresh();	
    		});
    		break;	
		}		
	}

}

