						OnFormLoaded()
{
	//-- NWJ - 28.02.2006
	//--      get any incoming cis
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems, _swdoc.OC().callref ,"INCOMING","cis_excludeservice");	
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_services, _swdoc.OC().callref ,"INCOMING","cis_servicesonly");	
	
	//-- load CIs if call has been logged from another incident	
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems, _swdoc.OC().callref ,"INCIDENT","cis_excludeservice");	
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_services, _swdoc.OC().callref ,"INCIDENT","cis_servicesonly");	
	
	
	
	_swdoc.hideshow_majorincstab();
	
	//-- Show appropriate KB Tab
	var tTab = _swdoc.EF().TabRel.tab;
	if(!app.g.use_sw_knowledgebase())
	{
		if(app.strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			_swdoc.EF().TabRel.ShowTabItem(9,true);
			_swdoc.EF().TabRel.ShowTabItem(5,false);
			app._etab(_swdoc.EF().TabRel, tTab);
		}
	}
	else
	{
		_swdoc.EF().TabRel.ShowTabItem(9,false);
		_swdoc.EF().TabRel.ShowTabItem(5,true);
		app._etab(_swdoc.EF().TabRel, tTab);
	}
		
}

