						mbtn_ici_views1_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMchildren = 2;	
	var iVCMparents = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_services,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_services,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMchildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iVCMparents)
		{
			app.cmdb.view_vcm(intCI,true);
		}

		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						mbtn_ici_actions1_OnMenuItem(strName, nItemID)
{
	var iRemoveCI = 1;

	//-- Remove Selected config items
	if(nItemID == iRemoveCI)
	{
   	
		//-- do this by finding unselected rows and making sure they are part of fitler
		var strFilter = "";
		for (var x=_swdoc.EF().sl_services.rowCount()-1;x > -1;x--)
		{
			var value = _swdoc.EF().sl_services.GetItemTextRaw(x,"pk_auto_id");		
			if (!_swdoc.EF().sl_services.IsRowSelected(x))
			{
				//-- add to filter
				if (value != "")
				{
    				if (strFilter != "") strFilter +=",";
    				strFilter += value;
				}
			}
			else
			{
				//-- 29.04.2008 - 2.2.0 if this ci that has been removed was used to set the pref supp group then change it
				if(_swdoc.intAssessmentCI == value)
				{
					_swdoc.OC().itsm_prefered_suppgroup = "";
					_swdoc.OC().itsm_prefered_text = "";
					_swdoc.UpdateFormFromData();
				}
			}
		}
		if(strFilter!="")
			strFilter="_kv="+strFilter;

       app._slf(_swdoc.EF().sl_services, strFilter);  
       _swdoc.EF().sl_services.Refresh();
       _swdoc.EF().sl_services.SetRowSelected(0);
	   _swdoc.check_ci_assessment();
	   _swdoc.uncheckset_customer_items();
       return;
	}
       
}

						btn_search_ci1_OnPressed()
{
    // -- JPG, 22/01/2014
    // It is redefined the mode for the new window
    var strURL = "searchmode=1&multiselect=1&canreset=1&fk_company_id=" + app.pfu(_swdoc.oCust().fk_company_id);
    // It is defined the next window to execute, searching services with better filters
    app.g.popup("manage_portfolio", strURL, function(sResults)
    {
    	if (sResults)
    	{
    		// Case there are not any selection go back
    		if (sResults.document.strSelectedKeys=="") return;
  			
  			
  			// -- JPG, 22/01/2015
    		// Present Bespoke Terms for selected Services
    		app.bespokeTermsProcessing(sResults.document.strSelectedKeys, "Configuration Item");
    		// END OF BESPOKE TERMS PROCESSING  	
   	           			
   			app.g.sl_kv_appendfilter(sl_services, sResults.document.strSelectedKeys,'pk_auto_id');
   		
   			//ES - F0111753 - set Process based on value set in Configuration Item and Configuration Type
			var dbValue = new SqlQuery;
			var arrKeys = sResults.document.strSelectedKeys.split(",");
			var arrWorkflowId = new Array();
			// Grab value of fk_workflow_id for each selected Configuration Item
			for(var x in arrKeys)
  			{
				dbValue.InvokeStoredQuery("cmdb/get.itemi_fkworkflowid","pk_auto_id="+arrKeys[x]);
				if(dbValue.Fetch())
				{
					if (app.g.get_field(dbValue,"fk_workflow_id")!="")
					{
						arrWorkflowId[x] = app.g.get_field(dbValue,"fk_workflow_id");
					}
				}
			}
			// Remove blank or undefined elements from the array
			for (var check in arrWorkflowId)
			{
				if (arrWorkflowId[check]=="undefined" || arrWorkflowId[check]=="")
				{
					var index = arrWorkflowId.indexOf(check);
					arrWorkflowId.splice(index,1);
				}
			}
			var arrayLength = arrWorkflowId.length;
			var position = 0;
			// Set Process field based on fk_workflow_id value
			for (var y in arrWorkflowId)
			{
				var intIncrement = 0;
				while (intIncrement < arrayLength)
				{
					if ((arrWorkflowId[y] == arrWorkflowId[intIncrement] && (intIncrement!=position)) || arrayLength == 1)
					{
						if (_swdoc.bpm_workflow.pk_workflow_id=="")
						{
							_swdoc.bpm_workflow.pk_workflow_id = arrWorkflowId[y];
							_swdoc.UpdateFormFromData();
						}
						else if (_swdoc.bpm_workflow.pk_workflow_id!="")
						{
							if (_swdoc.bpm_workflow.pk_workflow_id!=arrWorkflowId[y])
							{
								var msgProcess = MessageBox("The default Incident Process for the selected item is [" + arrWorkflowId[y] + "]. Would you like to apply this change?",MB_YESNO|MB_ICONQUESTION);
 								if (msgProcess==6)
								{
									_swdoc.bpm_workflow.pk_workflow_id = arrWorkflowId[y];
									_swdoc.UpdateFormFromData();
								}
							}
						}
					}
				intIncrement++;
				}
				position++;
			}
			//END - F0111753
  		
			//-- if only 1 CI use it default profile unless blank
			if(sl_services.rowCount()==1)
			{
				var strCode = app.g.sl_getrawcolvalue(sl_services,0,"fk_profilecode");
				if(strCode!="") 
				{
					_swdoc.OC().probcode = strCode;				
					_swdoc.UpdateFormFromData();		
				}
			}
	    		
			//-- NWJ - determine who the preferred support is
 			_swdoc.determine_prefered_support(true);
 			
 			
 			//-- JPG, 22.01.2015 - check ci contract
			app.cfg.process_call_contract_check( sResults.document.strSelectedKeys, true,_swdoc);
		
			sl_services.Refresh();
			_swdoc.MF().sl_cust_cis.Refresh();
			app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
    	}
    	else
    	{
    		return;
    	}
    });
}

