						Tab1_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here

	if(nTab == 0)
	{
		app._slf(sl_cust_incidents1 , "callref="+_swdoc.OC().callref+"&cust_id="+_swdoc.OC().cust_id);
		sl_cust_incidents1.Refresh();
		
	}
	else if(nTab == 1)
	{
		app._slf(sl_site_incidents1 , "callref="+_swdoc.OC().callref+"&site="+_swdoc.oCust().site+"&cust_id="+_swdoc.oCust().keysearch);
		sl_site_incidents1.Refresh();
		
	}
	else if(nTab == 2)
	{
		app._slf(sl_org_incidents1 , "callref="+_swdoc.OC().callref+"&fk_company_id="+_swdoc.oCust().fk_company_id+"&cust_id="+_swdoc.oCust().keysearch);
		sl_org_incidents1.Refresh();
		
	}
	else if(nTab == 3)
	{
		app._slf(sl_incprofile , "probcode="+_swdoc.OC().probcode);
		sl_incprofile.Refresh();
	}
	else if(nTab == 4)
	{
		sl_relincs.Refresh();
	}

}

						TabRel_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	tCMDB = 0;
	tServices = 1;
	tProblems = 2;
	tRFCs = 3;
	tSRs = 4;
	tKB = 5;
	tIncidents = 6;
	tMajIncidents = 7;
	
	if(nTab==tCMDB)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_configitems.rowCount() < 1) sl_configitems.Refresh();
	}
	if(nTab==tProblems)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_problems.rowCount() < 1) sl_problems.Refresh();
	}
	else if(nTab==tRFCs)
	{
			//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
			if (sl_rfcs.rowCount() < 1) sl_rfcs.Refresh();
	}
	else if(nTab==tSRs)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_srs.rowCount() < 1) sl_srs.Refresh();
	}
	else if(nTab==tKB)
	{
		if (sl_kbs1.rowCount() < 1)
		{
			app._slf(sl_kbs1,"cr="+_swdoc.OC().callref);	
			sl_kbs1.Refresh();
		}
	}	
	else if(nTab==tIncidents)
	{
		Tab1_OnTabSelected(Tab1.tab);
	}
	else if(nTab==tMajIncidents)
	{
		if (sl_majinc.rowCount() < 1)sl_majinc.Refresh();
	}	
	else if(nTab==tServices)
	{
		if (sl_services.rowCount() < 1)
			sl_services.Refresh();
	}	


}
