method.code:
						var strUpdateCIkeys = "";

//-- pointers
function oCI()
{
	return _swdoc.config_itemi;
}

function MF()
{
	return _swdoc.mainform;
}

//-- functions
function setup_form_fields()
{
	//-- get default arguements
	_swdoc.strUpdateCIkeys = _swdoc.GetArg("itemkeys");
	app.g.popuplate_form_fromargs("config_itemi", _swdoc, false);

	//-- get profile text
	var strInfo = "<Multiple Values>";
	if(_swdoc.config_itemi.fk_profilecode!="<Multiple Values>")
	{
		strInfo = app.g.get_key_info("pcdesc",_swdoc.config_itemi.fk_profilecode,"info", "code");
	}
	app._ete(_swdoc.MF().lb_profile , strInfo);
	
	//-- if mult company and contacts then show multiple text (as distinct picklist with display column cant be set)
	app._evi(_swdoc.MF().tb_org_mult, (_swdoc.GetArg("config_itemi.fk_company_id")=="<Multiple Values>"));
	if(_swdoc.MF().tb_org_mult.visible)
	{	
		app._ete(_swdoc.MF().tb_org_mult, "<Multiple Values>");
	}
	else
	{
		//-- have company so set filter
		var strFilter = "_swc_fk_company_id=" + app.pfu(_swdoc.oCI().fk_company_id);
		app._slf(_swdoc.MF().lb_site , strFilter);
		app._slf(_swdoc.MF().lb_contact , strFilter);
	}
	
	app._evi(_swdoc.MF().tb_contact_mult, (_swdoc.GetArg("config_itemi.fk_userdb")=="<Multiple Values>"));
	if(_swdoc.MF().tb_contact_mult.visible)app._ete(_swdoc.MF().tb_contact_mult, "<Multiple Values>");
	
	//-- set auth flag
	app._ete(_swdoc.MF().flg_authorised, (_swdoc.oCI().isauthorised =="No")?0:1);
	
	_swdoc.UpdateFormFromData();
}

//-- when the user changes the type of a CI we need to open the appropriate CI form
function change_config_type(funcCallback)
{
	var strOrigType = _swdoc.oCI().ck_config_type;
	app.cmdb.select_configtype("",true,function(strNewType)
	{
		if(strNewType=="")
		{
			if ((funcCallback != undefined) && (funcCallback != null))
			{
				funcCallback();
			}
		}

		//-- get config type form and if different prompt and if ok save and load correct form
		var strConfirm = "The configuration type may use a different form than that of the current configuration item type(s) and you may lose data. Are you sure you want to change the type?"
		if(!confirm(strConfirm))
		{
			if ((funcCallback != undefined) && (funcCallback != null))
			{
				funcCallback();
			}
		}
	
		if(strOrigType != strNewType)
		{
			_swdoc.oCI().ck_config_type = strNewType;	
			_swdoc.UpdateFormFromData();
		}
	});
	
}

