							OnFormLoaded()
{
	_swdoc.SetTitle("Common Configuration Item Update");
		
	//-- setup field mode
	_swdoc.setup_form_fields();
	
	//-- reset modified flag	
	_swdoc.ResetModiedFlag("config_itemi");
}

							lb_status_OnValueChanged(strValue)
{

	_swdoc.oCI().isfaulty = "No";
	_swdoc.oCI().isdeactivated = "No";
	_swdoc.oCI().isactive = "No";		
	_swdoc.oCI().isunavailable = "No";	
				
	//-- check if selected status is an unavailable status or inactive
	var strStatus = app.cmdb.get_cmdbstatus(_swdoc.oCI().ck_config_type, strValue);
	_swdoc.oCI().cmdb_status = strStatus;
	
	if (strStatus.toLowerCase()=="unavailable")
	{
		_swdoc.oCI().isunavailable = "Yes";
	}
	else if (strStatus.toLowerCase()=="deactivated")
	{
		_swdoc.oCI().isdeactivated = "Yes"
	}
	else if (strStatus.toLowerCase()=="faulty")
	{
		_swdoc.oCI().isfaulty = "Yes";		
	}
	else 
	{
		//-- assume active
		_swdoc.oCI().isactive = "Yes";				
	}
	
	_swdoc.UpdateFormFromData();	
}

							lb_profile_OnLeftClick()
{
	
	//--
	app.g.pick_problemprofile("","",true, function(strInfo){
		if(strInfo!=""){
		  _swdoc.oCI().fk_profilecode = strInfo.split("|")[0];
		  app._ete(lb_profile, strInfo.split("|")[1]);
		  _swdoc.UpdateFormFromData();
		}
	
	});

	return false;
}

							lb_company_OnValueChanged(strValue)
{

	var strFilter = "_swc_fk_company_id=" + app.pfu(strValue);
	app._slf(lb_site , strFilter);
	app._slf(lb_contact , strFilter);

	_swdoc.oCI().companyname = lb_company.display;
	app._evi(tb_org_mult, false);
	
	_swdoc.oCI().fk_site = "";
	_swdoc.oCI().fk_userdb = "";	
	_swdoc.oCI().owner_name = "";
	app._evi(tb_contact_mult, false);
	
	_swdoc.UpdateFormFromData();

}
