//-- functions
function setup_form_fields(boolInserting)
{
	//-- if inserting setup as
	_swdoc.strConfigItem = _swdoc.oCI().ck_config_item;
	_swdoc.boolInsertingCI = boolInserting;
	_swdoc.boolMakeUnavailable=false;
	if(boolInserting)
	{
		_swdoc.boolInserted = false;
		_swdoc.oCI().fk_profilecode = "";
		_swdoc.oCI().isactivebaseline="Yes";
		_swdoc.oCI().isunavailable = "No";
		_swdoc.oCI().isauthorised = "Yes";			
		_swdoc.oService().users_difference=0;	
		_swdoc.oService().cost_subscription="0.00";	
		_swdoc.oService().cost_request="0.00";	
		_swdoc.oService().cost_maintenance="0.00";	
		_swdoc.oService().total_subscription_revenue="0.00";	
		app._een(_swdoc.MF().fld_f_status_level,false);
		app._een(_swdoc.MF().fld_op_status,false);
		
	}
	else
	{
		//-- F0086202 - add form field mgt to app.service catalog
		app.cmdb.load_control_fields(_swdoc.oCI().catalog_type,1);
		//-- disable controlled fields if user is not manager or specialist
		app.cmdb.process_form_ciattribute_disablecheck(_swdoc,1);

		//-- else if updating setup as
		_swdoc.strOriginalUnavailability = _swdoc.oCI().isunavailable;

		app._een(_swdoc.MF().fld_service_name,false);
				
		//-- set up form for baseline needs
		_swdoc.check_baseline();	
			
 		if(_swdoc.oCI().status_portfolio == 'Catalog')
			app._slf(_swdoc.MF().fld_status_lifecycle , "lid=SERVICE-LIFECYCLE-STATUS-CATALOG");
 		else if(_swdoc.oCI().status_portfolio == 'Pipeline')
			app._slf(_swdoc.MF().fld_status_lifecycle , "lid=SERVICE-LIFECYCLE-STATUS-PIPELINE");
 		else if(_swdoc.oCI().status_portfolio == 'Retired')
			app._slf(_swdoc.MF().fld_status_lifecycle , "lid=SERVICE-LIFECYCLE-STATUS-RETIRED");
	}		
	//-- disable or hide field if internal support only
	if(dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")!=1)
	{
		app._evi(_swdoc.MF().fld_onr_cc,true);
		app._evi(_swdoc.MF().fld_ons_cc,true);
		app._evi(_swdoc.MF().lbl_ons_cc,true);
		app._evi(_swdoc.MF().lbl_onr_cc,true);
	}
	//-- eof hide / enable
		
	//-- store current site
	_swdoc.strCurrentSite = _swdoc.oCI().fk_site;
	_swdoc.strCurrentSupplier = _swdoc.oCI().fk_supplier;
	//-- store current Archived flag status
	_swdoc.intArchivedFlag = _swdoc.oCI().service_archived;
	//--
	//-- show hide tab items depending on insert or update
	app._een(_swdoc.MF().mbtn_view_ci,(!boolInserting));
	
	//-- show event management only when not inserting
	_swdoc.MF().tabAvail.ShowTabItem(2,!boolInserting);	
	_swdoc.MF().tabAvail.ShowTabItem(3,!boolInserting);
	app._etab(_swdoc.MF().tabAvail,0);

	//-- main app.service tab
	_swdoc.MF().tabCI.ShowTabItem(1,!boolInserting && app.service.can_manage_cost_and_subs(false)); // - show costs and subscriptions if allowed
	_swdoc.MF().tabCI.ShowTabItem(2,!boolInserting && _swdoc.oCI().flg_availmntr=="1"); // - -show availability settings if app.service allows it
	_swdoc.MF().tabCI.ShowTabItem(3,!boolInserting && app.service.can_manage_baselines(false));
	_swdoc.MF().tabCI.ShowTabItem(4,!boolInserting);		
	_swdoc.MF().tabCI.ShowTabItem(5,!boolInserting);		
	_swdoc.MF().tabCI.ShowTabItem(6,false);		
	_swdoc.MF().tabCI.ShowTabItem(7,!boolInserting);		
	_swdoc.MF().tabCI.ShowTabItem(8,false);		
	_swdoc.MF().tabCI.ShowTabItem(9,!boolInserting);	
	_swdoc.MF().tabCI.ShowTabItem(10,!boolInserting);	
	_swdoc.MF().tabCI.ShowTabItem(11,!boolInserting);
	app._een(_swdoc.MF().create,(!boolInserting));	
	app._etab(_swdoc.MF().tabCI,0);
	
	_swdoc.MF().TabRel.ShowTabItem(0,!boolInserting);
	_swdoc.MF().TabRel.ShowTabItem(1,!boolInserting && _swdoc.oCI().flg_bundle=="1");
	_swdoc.MF().TabRel.ShowTabItem(2,!boolInserting);
	_swdoc.MF().TabRel.ShowTabItem(3,!boolInserting);
	_swdoc.MF().TabRel.ShowTabItem(5,!boolInserting && _swdoc.oCI().fk_supplier!="");
	app._etab(_swdoc.MF().TabRel,0);
	//-- set the form title depending on mode and status of ci
	_swdoc.set_form_title(boolInserting);
	
	_swdoc.UpdateFormFromData();
		
}


function init_service_availability()
{
	app._slf(_swdoc.MF().sl_availhist,"_swc_fk_ci_id=" + _swdoc.oCI().pk_auto_id);
			
	//-- cal tot perc and times
	var intOpDownTime = app.g.sqllist_gettotalvalue(_swdoc.MF().sl_availhist,'opdowntime',true);
	var intDownTime = app.g.sqllist_gettotalvalue(_swdoc.MF().sl_availhist,'downtime',true);
	
	var iPerc = (intOpDownTime / intDownTime) * 100;
	iPerc = Math.round(100 - iPerc);
	if(isNaN(iPerc))iPerc=100;

	intOpDownTime = app.g.mins_from_perc(intOpDownTime);
	intDownTime = app.g.mins_from_perc(intDownTime);
	
	app._ete(_swdoc.MF().lbl_totavail,(iPerc + "% Availability Achieved"));
	app._ete(_swdoc.MF().lbl_totdown,intDownTime);
	app._ete(_swdoc.MF().lbl_opdowntime,intOpDownTime);
		
	app.cmdb.update_avail_hist_stats(config_itemi.pk_auto_id);

}

function set_form_title(boolInserting)
{
	if(boolInserting)
		var strTitle = "Add New Service";
	else
		var strTitle = "Service ("+_swdoc.oCI().ck_config_item+")";
	if(strTitle!="")_swdoc.SetTitle(strTitle);
}


function check_baseline()
{
	if(_swdoc.oCI().isactivebaseline!="Yes")
	{
		//-- disable buttons and 
		_swdoc.boolDisableMode = true;
	}	
	//--
	//-- disable buttons
	//_een(_swdoc.MF().btn_select_profile,(!_swdoc.boolDisableMode));
	//_een(_swdoc.MF().btn_select_type,(!_swdoc.boolDisableMode));	
	//_een(_swdoc.MF().btn_mng_outage,(!_swdoc.boolDisableMode));
	//_een(_swdoc.MF().btn_update,(!_swdoc.boolDisableMode));		
}


//- -when the user changes the type of a CI we need to open the appropriate CI form
function change_config_type()
{
	var strOrigType = _swdoc.oCI().ck_config_type;
	var strNewType = app.cmdb.select_configtype("");
	if(strNewType=="")return;
	
	if(strOrigType != strNewType)
	{
		_swdoc.oCI().ck_config_type = strNewType;	
		var strNewFormName = app.cmdb.get_configtype_formname(strNewType);
		_swdoc.UpdateFormFromData();
	}
}

