//-- 2.5
// calls the global function calculate revenue - which calculates the revenue from subscriptions
function call_calc_revenue()
{
	app.service.calculate_revenue(_swdoc.oService().fk_cmdb_id, _swdoc.oService());
	//_swdoc.LoadRecordData("swdata", "service_portfolio", _swdoc.oService().fk_cmdb_id);
	_swdoc.UpdateFormFromData();
	_swdoc.MF().sl_costs1.Refresh();
}

//-- 2.5
// calls the global function update server costs - which calculates the costs from the related records
function update_costs()
{
	app.service.update_service_costs(_swdoc.oService().fk_cmdb_id,_swdoc.oService());
	//_swdoc.LoadRecordData("swdata", "service_portfolio", _swdoc.oService().fk_cmdb_id);
	_swdoc.UpdateFormFromData();
	_swdoc.MF().sl_costs1.Refresh();
}

//-- 2.5
// gets the list of of costs, and refreshes the cost table
function load_costs()
{
	app._slf(_swdoc.MF().sl_costs1,"pids="+app.service.get_ids_of_costs(_swdoc.oService().fk_cmdb_id));
	_swdoc.MF().sl_costs1.Refresh();
}

