						OnDataSaved()
{
	//-- inserting so setup form for update 
	if(_swdoc.boolInsertingCI)
	{
    	//-- create app.cmdb relationship
		//-- if customer has a site then relate it as a ci (so can see in vcm company -> site -> customer)
		if(_swdoc.oCI().fk_site != "")    		
		{
			var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultSiteCIRelationship");
			var recSite = app.g.get_record("site", _swdoc.oCI().fk_site)
			app.cmdb.generate_meconfigrelation(recSite.fk_cmdb_id,_swdoc.oCI().pk_auto_id, strRel);  
		}
		else if(_swdoc.oCI().fk_company_id!="")
		{
			//-- else form relationship to company (so will see link in vcm company -> customer)
			var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgCIRelationship");
			_swdoc.LoadRecordData("swdata", "company", _swdoc.oCI().fk_company_id);    		
   			app.cmdb.generate_meconfigrelation(oCompany().fk_cmdb_id,_swdoc.oCI().pk_auto_id,strRel);  
   		}

		_swdoc.oCI().baselineid = _swdoc.oCI().pk_auto_id;
		_swdoc.boolSiteChanged = false;
		_swdoc.setup_form_fields(false);
		_swdoc.Save(0);				
		
	}
	else
	{
		var funcDiaryUpdates = function()
		{
			if(_swdoc.boolSilentDiaryUpdate && _swdoc.boolInserted)
			{
				//-- add diary entry
				app.cmdb.insert_diaryentry(_swdoc.oCI().pk_auto_id, "Manual Update", "General Update", app.session.analystId , app.global.GetCurrentEpocTime(), _swdoc.strSilentDiaryText, 0);
			}
			_swdoc.boolInserted = true;
			
			if(_swdoc.boolRecalculateCosts)
			{
				//TODO app.service.update_service_costs(_swdoc.oService().fk_cmdb_id);
			}
			if(_swdoc.intArchivedFlag != _swdoc.oCI().service_archived)
			{
				var strArchiveDiaryEntry = "The Archived flag has been changed from " + _swdoc.intArchivedFlag + " to " + _swdoc.oCI().service_archived + ".";
				app.cmdb.insert_diaryentry(_swdoc.oCI().pk_auto_id, "Manual Update", "General Update", app.session.analystId , app.global.GetCurrentEpocTime(), strArchiveDiaryEntry, 0);
			}
		}
		
		if((_swdoc.boolSiteChanged)&&(_swdoc.oCI().fk_site!=""))
		{
			//-- 14.07.2008 - create new site association
			var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultSiteCIRelationship");
			var recSite = app.g.get_record("site", _swdoc.oCI().fk_site)
			app.cmdb.generate_meconfigrelation(recSite.fk_cmdb_id,_swdoc.oCI().pk_auto_id,strRel);  
			_swdoc.boolSiteChanged=false;
		}
		_swdoc.strCurrentSite = _swdoc.oCI().fk_site;
		if(_swdoc.boolForceDiaryUpdate && _swdoc.boolInserted)
		{
			//-- add app.cmdb log entry by popping up an update form
			var strURL = "service=1&config_diary.updatetxt=" + _swdoc.strAutoDiaryText + "&config_diary.analystid=" + app.session.analystId + "&config_diary.fk_ci_id=" + _swdoc.oCI().pk_auto_id;
			app.cmdb.create_diaryentry(_swdoc.oCI().pk_auto_id, strURL,function()
			{
				funcDiaryUpdates();
			});
		}
		else
		{
			funcDiaryUpdates();
		}
	}
	//app.g.update_service_costs(_swdoc.oService());
}

						OnSaveData()
{
	//-- check for any regex matching that has been setup for ci values
	if(app.cmdb.process_form_ciattribute_regexcheck(_swdoc,1)==false) return false;

	//-- when inserting check that ck_config_item and ck_config_type is unique
	if(_swdoc.boolInsertingCI)
	{
		if(_swdoc.oService().vsb_description=="")
		{
			_swdoc.oService().vsb_description = _swdoc.oService().service_desc;
		}
		
		if(_swdoc.oService().vsb_title=="")
		{
			_swdoc.oService().vsb_title = _swdoc.oService().service_name;
		}
	}
	else
	{
		//-- are they allowed to update
		//TODO if(!app.service.can_update(true)) return false;
		
		//-- if not baselining and not current then warn user
		if(!_swdoc.boolBaselining)
		{
			if(_swdoc.oCI().isactivebaseline!="Yes")
			{
				if(!confirm("This item is currently an inactive baseline, are you sure you want to save your changes?"))
				{
					return false;
				}
			}
			
		}
	}	
	return true;
}

							btn_update_OnPressed()
{
	//-- add app.cmdb log entry by popping up an update form
	var strURL = "config_diary.analystid=" + app.session.analystId + "&config_diary.fk_ci_id=" + _swdoc.oCI().pk_auto_id + "&service=true";
	app.cmdb.create_diaryentry(_swdoc.oCI().pk_auto_id, strURL, function()
	{
		app.g.sl_refresh(sl_diary,null,0);
	});
}

							OnFormLoaded()
{
	if(!app.service.can_view(true))_swdoc.CloseForm();
	//-- setup field mode
	_swdoc.setup_form_fields(_swdoc.oCI().pk_auto_id==0);
	
	//-- Enable SLA association
	app._een(_swdoc.MF().sl_defslas, _swdoc.oCI().pk_auto_id!=0);
	app._een(_swdoc.MF().btn_add_class, _swdoc.oCI().pk_auto_id!=0);
	app._een(_swdoc.MF().btn_rem_class, _swdoc.oCI().pk_auto_id!=0);
	
	app._evi(_swdoc.MF().fld_disp_inc, app.boolServiceCIBehaviourOnIncident);
	app._evi(_swdoc.MF().fld_disp_sr, app.boolServiceCIBehaviourOnServiceRequest);

	// RJC F0104320
	//-- app.g.apply_appcode_sla_filter(_swdoc.MF().fld_fk_sld1,'SLA-SERVICE');
//	app.g.apply_appcode_sla_filter(_swdoc.MF().sl_slad1,'SLA-CMDB');
	app.g.apply_appcode_sla_filter(_swdoc.MF().sl_olas1,'OLA');
	//-- reset modified flag	
	_swdoc.ResetModiedFlag("config_itemi");
	_swdoc.ResetModiedFlag("sc_folio");
	_swdoc.MF().TabRel_OnTabSelected(0);
	
}

							tabCI_OnTabSelected(nTab)
{
	//if ((nTab==2)||(nTab==1)) MessageBox("Need 7.3 in order to work. Extended tables fix required");

	var tCost = 1;
	var tAvail = 2;	
	var tBaselines = 3;	
	var tRequests = 4;
	var tDiary = 5;	
	var tKB = 9;	
	var tABA = 10;	
	
	if(nTab==tRequests)
	{
		//-- Nothing Needed Now
	}
	else if(nTab==tBaselines)
	{
		
		app._een(_swdoc.MF().btn_delete_bln,false);
		app._een(_swdoc.MF().btn_reactivate,false);
		app._een(_swdoc.MF().btn_baseline,(_swdoc.oCI().isactivebaseline=="Yes"));
		app._slf(_swdoc.MF().sl_baselines , "_swc_baselineid="+_swdoc.oCI().baselineid);
		_swdoc.MF().sl_baselines.Refresh();
	}
	else if(nTab==tAvail)
	{
		app._een(btn_create_history,((tb_startedonx.text>0) && (tb_resolvedonx.text>0) &&(tb_startedonx.text<tb_resolvedonx.text)));
				
		
		_swdoc.MF().tabAvail_OnTabSelected(0);
		_swdoc.init_service_availability();
		_swdoc.MF().sl_availhist.Refresh();
	}
	else if(nTab == tCost)
	{
		_swdoc.load_costs();
		_swdoc.MF().TabFinance_OnTabSelected(0);
	}
	else if(nTab==tKB)
	{
		if(mainform.sl_kb1.rowCount()<1)
		{
			var strDocs = app.oKB.get_cis_kbs(_swdoc.oCI().pk_auto_id);
			if(strDocs=="")
				strDocs="0";
			app._slf(_swdoc.MF().sl_kb1,"swc=docref&kvs="+strDocs);
		}
		_swdoc.MF().sl_kb1.Refresh();
		//-- Second SQL List
		var strSRSDocs = app.oKB.get_srs_kbs_portfolio(_swdoc.oCI().pk_auto_id, _swdoc.oCI().status_lifecycle);
		if(strSRSDocs=="")
			strSRSDocs="0";
		app._slf(_swdoc.MF().sl_kb2,"swc=docref&kvs="+strSRSDocs);
		_swdoc.MF().sl_kb2.Refresh();
	}else if(nTab==tABA)
	{
		app._slf(_swdoc.MF().sl_bus_area1, "swc=fk_config_item&kvs="+_swdoc.oCI().ck_config_item);
		_swdoc.MF().sl_bus_area1.Refresh();
	}else if(nTab==tDiary)
	{
		_swdoc.MF().sl_diary.Refresh();
	}else if (nTab==7)
	{
		_swdoc.MF().sl_uri1.Refresh();
	}else if (nTab==11)
	{	
		_swdoc.MF().sqlListContracts.Refresh();
	}





}

							btn_baseline_OnPressed()
{
	//-- Baseline active baseline
	//-- create exact copy of it and its extended details and availability settings
	if(!app.cmdb.can_baseline(true))return;
	
	app.cmdb.create_baseline(_swdoc.oCI().pk_auto_id, _swdoc.oCI().baselineid, _swdoc.oCI().ck_config_type,true,0,false,undefined,function(intNewCI)
	{
		if(intNewCI > 0)
		{
			_swdoc.boolBaselining = true;
			_swdoc.oCI().isactivebaseline = "No";
			_swdoc.Save(0);
			_swdoc.boolBaselining = false;
			_swdoc.MF().sl_baselines.Refresh();	
			if (confirm("The item has been baselined successfully. Do you want to view the active baseline?"))
			{
				//-- 
				_swdoc.CloseForm();
				app.cmdb.popup_configitem(intNewCI,true,"",true);			
			}
		}
	});
	
	
}

							sl_baselines_OnItemSelected(nRow)
{
	var strABLN = sl_baselines.GetItemText(nRow, 2);
	app._een(btn_reactivate, (strABLN!="Yes"));

	var intPK = sl_baselines.GetItemText(nRow, 0);
	app._een(btn_delete_bln, ((strABLN!="Yes")&&(intPK!=_swdoc.oCI().pk_auto_id)&& (dd.GetGlobalParamAsString("Application Settings/CMDB/AllowDatabaseDelete")==1) ));
	
	var strBLNIdx = sl_baselines.GetItemText(nRow, 1);
	app._een(btn_compare_bl, (strBLNIdx!=_swdoc.oCI().ck_baselineindex));

}

							btn_reactivate_OnPressed()
{
	if(!app.cmdb.can_baseline(true))return;
	var intCI = sl_baselines.GetItemText(sl_baselines.curSel, 'pk_auto_id');
	
	//-- get active bln
	var activeCIid = 0;
	var intRow = app.g.sl_findrow_byvalue(sl_baselines,"Yes","isactivebaseline");
	if (intRow>-1)activeCIid = sl_baselines.GetItemTextRaw(intRow, "pk_auto_id");
	
	if(app.cmdb.reactivate_baseline(intCI, activeCIid))
	{
		_swdoc.CloseForm();
		app.cmdb.popup_configitem(intCI,true,"",true);			
	}


}

							btn_delete_bln_OnPressed()
{
	if(!app.cmdb.can_baseline(true))return;
	var strDeleteItems = app.g.sl_getselected_valuesdel(sl_baselines,'pk_auto_id',",");
	var strConfirm = "Are you sure that you want to delete the selected baseline from the database?"
	if(confirm(strConfirm))
	{
		//-- F0101341
		var strParams = "cids=" + strDeleteItems;
		if( app.g.submitsqs("service/process/delete_baseline",strParams))
		{
			//-- remove deleted items
			app.g.sl_clearrows(sl_baselines,strDeleteItems,0,null,0);
		}
	}


}

							btn_del_child_OnPressed()
{
	if(!app.service.can_update(true))
		return false;

	var strConfirm = "Are you sure you want to delete the selected child relation?";
	if (confirm(strConfirm))
	{
		var varKey = sl_children.GetItemTextRaw(sl_children.curSel,'fk_child_id');
		var strParams = "bp=0&key="+app.pfu(_swdoc.oService().fk_cmdb_id)+"&service="+app.pfu(varKey);
		app.g.submitsqs("service/delete_catalog_rels", strParams);	
		_swdoc.update_costs();
		sl_children.RemoveRow(sl_children.curSel);
		sl_children.SetRowSelected(0);
	}


}

							btn_del_parent_OnPressed()
{
	if(!app.service.can_update(true))
		return false;
	
	var strConfirm = "Are you sure you want to delete the selected parent relation?";
	if (confirm(strConfirm))
	{
		var varKey = sl_parents.GetItemTextRaw(sl_parents.curSel,'fk_parent_id');
		var strParams = "bp=1&key="+app.pfu(_swdoc.oService().fk_cmdb_id)+"&service="+app.pfu(varKey);
		app.g.submitsqs("service/delete_catalog_rels", strParams);	
		app.service.update_service_costs(varKey);
		sl_parents.RemoveRow(sl_parents.curSel);
		sl_parents.SetRowSelected(0);
	}

}

							tb_startedonx_OnValueChanged(strValue)
{
//-- if this and resolvedonx > 0 then enable history button
	app._een(btn_create_history,((tb_startedonx.text>0) && (tb_startedonx.text<tb_resolvedonx.text)));
	
}

							tb_resolvedonx_OnValueChanged(strValue)
{
//-- if this and resolvedonx > 0 then enable history button
	app._een(btn_create_history,((tb_startedonx.text>0) && (tb_startedonx.text<tb_resolvedonx.text)));
	
}

							btn_create_history_OnPressed()
{
	//-- create an availability history record
	var strArgs = "fk_ci_item="+_swdoc.oCI().ck_config_item+"&fk_ci_id=" + _swdoc.oCI().pk_auto_id + "&startedonx=" + tb_startedonx.text + "&endedonx=" + tb_resolvedonx.text+ "&isservice=1";
	app.OpenFormForAdd("cmdb_avail_history", "", strArgs, true, function(oForm){
		if(oForm.document.boolSaved){
			_swdoc.oAvail().startedonx = "";
			_swdoc.oAvail().expectresolvebyx = "";		
			_swdoc.oAvail().resolvedonx = "";		
			_swdoc.UpdateFormFromData();
			
			_swdoc.init_service_availability();
			_swdoc.MF().sl_availhist.Refresh();
			_swdoc.Save(0);
			
		}
	});	
}

							tabAvail_OnTabSelected(nTab)
{
	app._een(btn_create_history,((tb_startedonx.text>0) && (tb_resolvedonx.text>0) &&(tb_startedonx.text<tb_resolvedonx.text)));
	
	if(nTab==0)
	{
		_swdoc.MF().sl_availhist.Refresh();
	}else if (nTab==2)
	{
		_swdoc.MF().sl_avail_act1.Refresh();
	}else if (nTab==3)
	{
		_swdoc.MF().sl_blackout1.Refresh();
		
	}else if (nTab==4)
	{
		_swdoc.MF().sl_thresholds.Refresh();			
		_swdoc.MF().sl_thresholds.SetRowSelected(0);		
	}

}

							mbtn_view_ci_OnMenuItem(strName, nItemID)
{
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = _swdoc.oCI().pk_auto_id;
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = _swdoc.oCI().ck_config_item;
		if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			//app.cmdb.view_schedule(intCI,strItem);                      
			var result = app.OpenHtmlWindow("CHANGE_SCHEDULE_1", "frame", app.g.get_webserver()+"/sw/clisupp/_fsc/"+app.session.dataDictionary+"/index.php?analystid="+app.session.analystId+"&sessid="+app.session.sessionId, 
                                  "Change Schedule", true, 800);
		}
	}
}

							btn_compare_bl_OnPressed()
{
	//-- app.itsm 2.4.0 - call to compare this ci (master) against the selected baseline
	var intBaselineCIID = sl_baselines.GetItemText(sl_baselines.curSel, 'pk_auto_id');
	if(intBaselineCIID>0)
	{
		app.cmdb.compare_baseline_data(_swdoc.oCI(), intBaselineCIID);
	}
}

							OnFieldValueChanged(strName, strValue)
{
	// TODO: Add your event handler code here
	var oEle = _swdoc.MF().elements[strName];
	if((oEle) && (oEle.dataRef!=""))
	{
		var passbackvalue = app.cmdb.check_ci_element_attribute(oEle,_swdoc,undefined,undefined,1);
		
		if(passbackvalue=="__notcmdbfield__") return true;
		if(passbackvalue!=strValue)
		{
			_swdoc.strChangeBackBinding = oEle.dataRef;
			_swdoc.strChangeBackToValue = passbackvalue;
			//-- need to reset value back
			_swdoc.MF().SetTimer(1, 10);
		}
	}

}

							OnTimer(nTimer)
{
	// TODO: Add your event handler code here
	mainform.KillTimer(nTimer);
	
	//-- reset field value from onfieldvaluechanged
	if(nTimer==1)
	{
		app.g.set_doc_record_fieldvalue(_swdoc, _swdoc.strChangeBackBinding, _swdoc.strChangeBackToValue);
		
	}

}

