							mbtn_add_child_OnMenuItem(strName, nItemID)
{
	if(!app.service.can_update(true))
		return false;
	var iService =1;	
	var iCI =2;		
	
	//-- get intCI
  	var intCI = _swdoc.oCI().pk_auto_id;
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = _swdoc.oCI().ck_config_item;
		if(nItemID == iService)
		{
			//-- add parent non operational rel
			app.service.generate_servicerelation(_swdoc.oCI().pk_auto_id,_swdoc.oCI().ck_config_type,_swdoc.oCI().ck_config_item,_swdoc.oCI().ck_baselineindex,true,_swdoc.oCI(), function(boolRes)
			{
				if (boolRes)
				{
					sl_children.Refresh();
					sl_children.SetRowSelected(0);
				}
				_swdoc.Save(0);
			});
		}
		else if(nItemID == iCI)
		{
			//-- add parent non operational rel
			app.service.generate_configrelation(_swdoc.oCI().pk_auto_id,_swdoc.oCI().ck_config_type,_swdoc.oCI().ck_config_item,_swdoc.oCI().ck_baselineindex,true,_swdoc.oCI(), function(boolRes)
			{
				if (boolRes)
				{
					sl_children.Refresh();
					sl_children.SetRowSelected(0);
				}
				_swdoc.Save(0);
			});
		}
		else
		{
			_swdoc.Save(0);
		}
	}
	else
	{
		_swdoc.Save(0);
	}
	
}

							btn_new_parent_service_OnPressed()
