	// TODO: Add your event handler code here
	
	app.service.generate_servicerelation(_swdoc.oCI().pk_auto_id,_swdoc.oCI().ck_config_type,_swdoc.oCI().ck_config_item,_swdoc.oCI().ck_baselineindex,false,_swdoc.oCI(), function(boolRes)
