			sl_parents.SetRowSelected(0);
		}
	});


}

							sl_costs1_OnItemDoubleClicked(nRow, nCol)
{
	// TODO: Add your event handler code here
		
	var varKey = sl_costs1.GetItemTextRaw(sl_costs1.curSel,'pk_auto_id');
	var varType = sl_costs1.GetItemTextRaw(sl_costs1.curSel,'cost_type');
	
	var oForm = null;
	if (varType=="Underpinning CI" || varType=="Underpinning Service")
	{
		app.OpenFormForEdit('service_cost_ci',varKey,"",true, function(oForm){
		if(oForm.boolFigureChanged)
			_swdoc.update_costs();	
		_swdoc.Save(0);
		});
	}
	else if (varType=="Component")
	{
		app.OpenFormForEdit('service_component',varKey,"",true, function(oForm){
		if(oForm.boolFigureChanged)
			_swdoc.update_costs();	
		_swdoc.Save(0);
		});
	}
	else
	{
		app.OpenFormForEdit('service_costs',varKey,"",true, function(oForm){
		if(oForm.boolFigureChanged)
			_swdoc.update_costs();	
		_swdoc.Save(0);
		});
	}

}

							mbtn_add_subscription_OnMenuItem(strName, nItemID)
{
	if(!app.service.can_update(true))
		return false;
	var iCustomer = 1;	
	var iOrganisation =2;		
	var iDepartment = 3;

	
	var funcRevAndVisibility = function()
	{
		_swdoc.call_calc_revenue();
		_swdoc.Save(0);
		app._slf(_swdoc.MF().sl_revenue,"swc=fk_service&kvs="+_swdoc.oCI().pk_auto_id+"&sf=catalog_subscription");
		_swdoc.MF().sl_revenue.Refresh();
		_swdoc.MF().sl_visibility.Refresh();
	}
	
	
	//-- get intCI
  	var intService = _swdoc.oService().fk_cmdb_id;
  	if(intService>0)
  	{
  		if(sl_service_sla.rowCount()==0)
  		{
  			MessageBox("Please associate service levels this service is offered at before creating subscriptions.");
  			return false;
  		}

		//-- pass in text identfier as well
  		if(nItemID == iCustomer)
		{
			app.service.add_subscription('search_customers',_swdoc.oService(),function(boolRes)
			{
				if (!boolRes)
				{
					return;
				}
				else
				{
					funcRevAndVisibility();
				}
			});
		}
		else if(nItemID == iOrganisation)
		{
			app.service.add_subscription('search_company',_swdoc.oService(),function(boolRes)
			{
				if (!boolRes)
				{
					return;
				}
				else
				{
					funcRevAndVisibility();
				}
			});
		
		}
		else if(nItemID == iDepartment)
		{
			app.service.add_subscription('search_dept',_swdoc.oService(),function(boolRes)
			{
				if (!boolRes)
				{
					return;
				}
				else
				{
					funcRevAndVisibility();
				}
			});
		}
	}
	else
	{
		funcRevAndVisibility();
	}
}

							btn_del_subscription_OnPressed()
{
	if(!app.service.can_update(true))
		return false;
	var strConfirm = "Are you sure you want to remove the selected subscription to this service?";
	if (confirm(strConfirm))
	{
		var strPriKey = app.g.sl_getprikeyvalue(sl_revenue, sl_revenue.curSel);
		var strParams = "key="+app.pfu(strPriKey);
		app.g.submitsqs("service/process/delete_subscription", strParams);	
		_swdoc.call_calc_revenue();
		_swdoc.MF().sl_revenue.Refresh();
		_swdoc.Save(0);
	}	
}

