							btn_service_sla_OnPressed()
{
	if(!app.service.can_update(true))
		return false;
	var strSLA = app.g.sl_getcolvalue(sl_service_sla, sl_service_sla.curSel, "fk_sla");
	var strSLAName = app.g.sl_getcolvalue(sl_service_sla, sl_service_sla.curSel, "fk_sla_name");
	if(strSLA==sc_folio.fk_default_sla)
	{
		MessageBox("Unable to remove Service Level '"+strSLAName+"', this Service Level is the default level for this service.");
		return false;
	}

	var strParams = "sid="+app.pfu(_swdoc.oService().fk_cmdb_id)+"&sla="+app.pfu(strSLA);
	var intCount = app.g.sqs_rowcount("service/count_slas_subs", strParams);
	if(intCount>0)
	{
		MessageBox("Unable to remove Service Level '"+strSLAName+"', this Service Level is associated to subscriptions of this service.");
	}
	else
	{
		var strConfirm = "Are you sure you want to remove the selected Service Level?";
		if (confirm(strConfirm))
		{
			var strPriKey = app.g.sl_getprikeyvalue(sl_service_sla, sl_service_sla.curSel);
			var strParams = "key="+app.pfu(strPriKey);
			app.g.submitsqs("service/delete_service_sla", strParams);	
			sl_service_sla.RemoveRow(sl_service_sla.curSel);
			sl_service_sla.SetRowSelected(0);
		}
	}
	
}

							btn_new_service_sla_OnPressed()
{
	if(!app.service.can_update(true))
		return false;

	var strSelectedSLAs="";
	strSelectedSLAs = app.g.sqllist_get_valuesdel(sl_service_sla,4,",","'", true);
	var strURL = "fk_service="+_swdoc.oService().fk_cmdb_id;
	if(strSelectedSLAs!="")
		strURL += "&fk_used_slas="+strSelectedSLAs;
	app.OpenFormForAdd("service_sla", "", strURL, true, function(){
		sl_service_sla.Refresh();
	});


}

