							fld_f_status_level_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	_swdoc.oCI().cmdb_status = strValue;

	//-- reset status's
	_swdoc.oCI().isimpacted = "No";		
	_swdoc.oCI().isfaulty = "No";
	_swdoc.oCI().isdeactivated = "No";
	_swdoc.oCI().isactive = "No";		
	_swdoc.oCI().isunavailable = "No";	

	//-- check if selected status is an unavailable status or inactive
	var strStatusInfo = app.service.get_statusinfo(_swdoc.oCI().catalog_type, strValue);
	var strStatusColour = strStatusInfo.split("|")[0];
	var strStatus = strStatusInfo.split("|")[1];	
	app._ecol(_swdoc.MF().fld_f_status_level,strStatusColour);
	_swdoc.oCI().cmdb_status = strStatus;
	if ((strStatus.toLowerCase()=="unavailable") && (_swdoc.strOriginalUnavailability=="No"))
	{
		_swdoc.oCI().isunavailable = "Yes";
		if(!_swdoc.boolMakeUnavailable) 
		{
			_swdoc.boolMakeUnavailable = true;
			
			//-- if we can monitor this type of ci
			if(_swdoc.oService().avail_mntr==1)
			{
				if(confirm("Setting this status will mark the configuration item as unavailable. Would you like to modify the availability notification settings?"))
				{
					app._etab(tabCI,3);
				}
			}
		}
	}
	else if (strStatus.toLowerCase()=="deactivated")
	{
		_swdoc.oCI().isdeactivated = "Yes"
	}
	else if (strStatus.toLowerCase()=="faulty")
	{
		_swdoc.oCI().isfaulty = "Yes";		
	}
	else if (strStatus.toLowerCase()=="impacted")
	{
		_swdoc.oCI().isimpacted = "Yes";		
	}	
	else 
	{
		//-- assume active
		_swdoc.oCI().isactive = "Yes";				
	}
	
	//-- if was originally unavail and now making avail
	if((_swdoc.strOriginalUnavailability=="Yes") &&(!_swdoc.boolMakeUnavailable))
	{
		//-- if we can monitor this type of ci
		if(_swdoc.oService().avail_mntr==1)
		{
			if(confirm("Setting this status will mark the configuration item as available. Would you like to modify the availability notification settings?"))
			{
				app._etab(tabCI,3);
			}		
		}
	}

	_swdoc.UpdateFormFromData();	

}

							mbtn_add_subscription1_OnMenuItem(strName, nItemID)
{
	if(!app.service.can_update(true))return false;
	
	var iCustomer = 1;	
	var iOrganisation =2;		
	var iDepartment = 3;

	//-- get intCI
  	var intService = _swdoc.oService().fk_cmdb_id;
  	if(intService>0)
  	{
  		//-- pass in text identfier as well
  		if(nItemID == iCustomer)
		{
			app.service.add_view_permission('search_customers',_swdoc.oService(),function()
			{
				_swdoc.MF().sl_visibility.Refresh();
			});
		}
		else if(nItemID == iOrganisation)
		{
			app.service.add_view_permission('search_company',_swdoc.oService(),function()
			{
				_swdoc.MF().sl_visibility.Refresh();
			});
		}
		else if(nItemID == iDepartment)
		{
			app.service.add_view_permission('search_dept',_swdoc.oService(),function()
			{
				_swdoc.MF().sl_visibility.Refresh();
			});
		}
	}
	else
	{
		_swdoc.MF().sl_visibility.Refresh();
	}
	
	
	
}

							btn_del_subscription1_OnPressed()
{
	if(!app.service.can_update(true))
		return false;
	var strConfirm = "Are you sure you want to remove the selected visiblity permission to this service?";
	if (confirm(strConfirm))
	{
		var strPriKey = app.g.sl_getprikeyvalue(sl_visibility, sl_visibility.curSel);
		var strParams = "key="+app.pfu(strPriKey);
		app.g.submitsqs("service/process/delete_view_permission", strParams);	
		sl_visibility.RemoveRow(sl_visibility.curSel);
		sl_visibility.SetRowSelected(0);
	}		
}

